import React, { createContext, useContext } from 'react';
import { DataTableContextValue } from './types';

const DataTableContext = createContext<DataTableContextValue | undefined>(undefined);

export const DataTableProvider: React.FC<{
  children: React.ReactNode;
  value: DataTableContextValue;
}> = ({ children, value }) => {
  return (
    <DataTableContext.Provider value={value}>
      {children}
    </DataTableContext.Provider>
  );
};

export const useDataTableContext = () => {
  const context = useContext(DataTableContext);
  if (!context) {
    throw new Error('useDataTableContext must be used within DataTableProvider');
  }
  return context;
};

import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { Settings2, Download, FileSpreadsheet } from "lucide-react";
import { ProfileManager } from "./profile-manager";
import { GridApi, ColDef as AgColDef } from "ag-grid-community";
import { GridProfile } from "@/stores/profile.store";
import { useToast } from "@/hooks/use-toast";

const monospaceFonts = [
  { value: 'JetBrains Mono', label: 'JetBrains Mono' },
  { value: 'Fira Code', label: 'Fira Code' },
  { value: 'Source Code Pro', label: 'Source Code Pro' },
  { value: 'IBM Plex Mono', label: 'IBM Plex Mono' },
  { value: 'Roboto Mono', label: 'Roboto Mono' },
  { value: 'Monaco', label: 'Monaco' },
  { value: 'Consolas', label: 'Consolas' },
  { value: 'Courier New', label: 'Courier New' },
  { value: 'monospace', label: 'System Monospace' },
];

interface DataTableToolbarProps {
  onFontChange: (font: string) => void;
  onSpacingChange: (spacing: string) => void;
  onOpenColumnSettings?: () => void;
  gridApi?: GridApi | null;
  onProfileChange?: (profile: GridProfile) => void;
  getColumnDefsWithStyles?: () => AgColDef[];
}

export function DataTableToolbar({ 
  onFontChange, 
  onOpenColumnSettings,
  gridApi,
  onProfileChange,
  getColumnDefsWithStyles 
}: DataTableToolbarProps) {
  const { toast } = useToast();

  const handleExportExcel = () => {
    if (!gridApi) {
      toast({
        title: "Export failed",
        description: "Grid is not ready for export",
        variant: "destructive"
      });
      return;
    }

    try {
      gridApi.exportDataAsExcel({
        fileName: `data-export-${new Date().toISOString().split('T')[0]}.xlsx`,
        author: 'AG-Grid Export',
        sheetName: 'Data',
        processCellCallback: (params) => {
          const colDef = params.column.getColDef();
          // Use valueFormatter for export if available
          if (colDef.valueFormatter) {
            return typeof colDef.valueFormatter === 'function'
              ? colDef.valueFormatter(params as any)
              : params.value;
          }
          return params.value;
        }
      });

      toast({
        title: "Export successful",
        description: "Data exported to Excel file",
      });
    } catch {
      toast({
        title: "Export failed",
        description: "An error occurred while exporting data",
        variant: "destructive"
      });
    }
  };

  const handleExportCsv = () => {
    if (!gridApi) {
      toast({
        title: "Export failed",
        description: "Grid is not ready for export",
        variant: "destructive"
      });
      return;
    }

    try {
      gridApi.exportDataAsCsv({
        fileName: `data-export-${new Date().toISOString().split('T')[0]}.csv`,
        processCellCallback: (params) => {
          const colDef = params.column.getColDef();
          // Use valueFormatter for export if available
          if (colDef.valueFormatter) {
            return typeof colDef.valueFormatter === 'function'
              ? colDef.valueFormatter(params as any)
              : params.value;
          }
          return params.value;
        }
      });

      toast({
        title: "Export successful",
        description: "Data exported to CSV file",
      });
    } catch {
      toast({
        title: "Export failed",
        description: "An error occurred while exporting data",
        variant: "destructive"
      });
    }
  };
  return (
    <div className="flex items-center justify-between p-4 border-b bg-muted/40">
      <div className="flex items-center gap-4">
        <ProfileManager 
          gridApi={gridApi || null} 
          onProfileChange={onProfileChange}
          getColumnDefsWithStyles={getColumnDefsWithStyles}
        />
        <div className="border-l pl-4 flex items-center gap-2">
          <label htmlFor="font-select" className="text-sm font-medium">
            Font:
          </label>
          <Select onValueChange={onFontChange} defaultValue="monospace">
            <SelectTrigger id="font-select" className="w-[180px] h-8">
              <SelectValue placeholder="Select font" />
            </SelectTrigger>
            <SelectContent>
              {monospaceFonts.map((font) => (
                <SelectItem key={font.value} value={font.value}>
                  <span style={{ fontFamily: font.value }}>{font.label}</span>
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="flex items-center gap-2">
        <Button 
          variant="outline" 
          size="sm" 
          onClick={handleExportExcel}
          className="h-8"
          disabled={!gridApi}
        >
          <FileSpreadsheet className="mr-2 h-4 w-4" />
          Excel
        </Button>
        <Button 
          variant="outline" 
          size="sm" 
          onClick={handleExportCsv}
          className="h-8"
          disabled={!gridApi}
        >
          <Download className="mr-2 h-4 w-4" />
          CSV
        </Button>
        {onOpenColumnSettings && (
          <Button 
            variant="outline" 
            size="sm" 
            onClick={onOpenColumnSettings}
            className="h-8"
          >
            <Settings2 className="mr-2 h-4 w-4" />
            Customize Columns
          </Button>
        )}
      </div>
    </div>
  );
}

import React, { useCallback, useEffect, memo, useState, useMemo } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogFooter } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Sheet, SheetContent, SheetHeader, SheetTitle } from '@/components/ui/sheet';
import { ColumnSelectorPanel } from './panels/ColumnSelectorPanel';
import { PropertyEditorPanel } from './panels/PropertyEditorPanel';
import { BulkActionsPanel } from './panels/BulkActionsPanel';
import { ColDef, ColumnState } from 'ag-grid-community';
import { useColumnCustomizationStore } from './store/column-customization.store';
import { Undo2, Redo2, Settings2, Columns, ChevronLeft, ChevronRight, Zap, Trash2, Volume2, VolumeX } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useButtonFeedback, useProgressIndicator } from './utils/feedback';
import { cn } from '@/lib/utils';
import { useSoundPreference } from './hooks/useSoundPreference';

interface ColumnCustomizationDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  columnDefs: ColDef[];
  columnState?: ColumnState[]; // AG-Grid column state
  onApply: (updatedColumns: ColDef[]) => void;
}

export const ColumnCustomizationDialog: React.FC<ColumnCustomizationDialogProps> = memo(({
  open,
  onOpenChange,
  columnDefs,
  columnState,
  onApply
}) => {
  const {
    selectedColumns,
    columnDefinitions,
    pendingChanges,
    setOpen,
    setColumnDefinitions,
    setColumnState,
    applyChanges,
    resetChanges,
    clearAllCustomizations,
    showColumnDrawer,
    setShowColumnDrawer,
    bulkActionsPanelCollapsed,
    setBulkActionsPanelCollapsed
  } = useColumnCustomizationStore();
  const { toast } = useToast();
  const { buttonRef: applyCloseButtonRef, triggerFeedback } = useButtonFeedback();
  const { progressRef, showProgress, hideProgress } = useProgressIndicator();
  const [isProcessing, setIsProcessing] = useState(false);
  const [showClearAllConfirmation, setShowClearAllConfirmation] = useState(false);
  const { soundEnabled, toggleSound } = useSoundPreference();

  // Initialize column definitions when dialog opens
  useEffect(() => {
    if (open) {
      // Only update if columnDefs have actually changed
      const needsUpdate = columnDefs.length !== columnDefinitions.size ||
        columnDefs.some(col => {
          const colId = col.field || col.colId || '';
          const existing = columnDefinitions.get(colId);
          return !existing || existing !== col;
        });

      if (needsUpdate && columnDefs.length > 0) {
        const columnMap = new Map<string, ColDef>();
        columnDefs.forEach(col => {
          const colId = col.field || col.colId || '';
          if (colId) {
            columnMap.set(colId, col);
          }
        });
        setColumnDefinitions(columnMap);
      }

      // Also set column state if provided
      if (columnState) {
        console.log('[ColumnCustomizationDialog] Setting column state:', {
          columnStateLength: columnState.length,
          sampleState: columnState.slice(0, 3).map(cs => ({
            colId: cs.colId,
            hide: cs.hide,
            width: cs.width
          }))
        });
        setColumnState(columnState);
      } else {
        console.log('[ColumnCustomizationDialog] No column state provided');
      }
    }
    setOpen(open);
  }, [open, columnDefs, columnState, columnDefinitions.size, setColumnDefinitions, setColumnState, setOpen]);

  const selectedCount = selectedColumns.size;
  const totalColumns = columnDefinitions.size;

  // Check if there are any customizations to clear
  const hasAnyCustomizations = useMemo(() => {
    const customizationProperties = [
      'cellStyle', 'headerStyle', 'cellClass', 'headerClass',
      'valueFormatter', 'filter', 'filterParams',
      'cellEditor', 'cellEditorParams',
      'width', 'minWidth', 'maxWidth', 'pinned', 'lockPosition', 'lockVisible'
    ];

    for (const column of columnDefinitions.values()) {
      for (const prop of customizationProperties) {
        const value = (column as any)[prop];
        if (value !== undefined && value !== null && value !== false && value !== '') {
          return true;
        }
      }
    }

    // Also check for applied templates
    return false;
  }, [columnDefinitions]);

  // Count customized columns for confirmation dialog
  const customizedColumnsCount = useMemo(() => {
    const customizationProperties = [
      'cellStyle', 'headerStyle', 'cellClass', 'headerClass',
      'valueFormatter', 'filter', 'filterParams',
      'cellEditor', 'cellEditorParams',
      'width', 'minWidth', 'maxWidth', 'pinned', 'lockPosition', 'lockVisible'
    ];

    let count = 0;
    for (const column of columnDefinitions.values()) {
      let hasCustomizations = false;
      for (const prop of customizationProperties) {
        const value = (column as any)[prop];
        if (value !== undefined && value !== null && value !== false && value !== '') {
          hasCustomizations = true;
          break;
        }
      }
      if (hasCustomizations) count++;
    }

    return count;
  }, [columnDefinitions]);

  // Apply changes
  const _handleApplyChanges = useCallback(() => {
    console.log('[ColumnCustomizationDialog] handleApplyChanges called');
    // Use requestAnimationFrame for smooth UI updates
    requestAnimationFrame(() => {
      const updatedColumns = applyChanges();
      console.log('[ColumnCustomizationDialog] Columns after applyChanges:', {
        count: updatedColumns.length,
        hasCustomizations: updatedColumns.some(col => 
          col.cellStyle || col.valueFormatter || col.cellClass
        )
      });
      onApply(updatedColumns);
    });
  }, [applyChanges, onApply]);

  // Apply and close in a single operation
  const handleApplyAndClose = useCallback(() => {
    console.log('[ColumnCustomizationDialog] handleApplyAndClose called');

    // Use requestAnimationFrame to defer UI updates
    requestAnimationFrame(() => {
      // Show processing state
      setIsProcessing(true);
      showProgress();

      // Apply changes immediately
      const updatedColumns = applyChanges();
      console.log('[ColumnCustomizationDialog] Columns after applyChanges:', {
        count: updatedColumns.length,
        hasCustomizations: updatedColumns.some(col => 
          col.cellStyle || col.valueFormatter || col.cellClass
        )
      });

      // Count customizations once
      const customizationCount = updatedColumns.filter(col => 
        col.cellStyle || col.headerStyle || col.valueFormatter || col.cellClass || col.headerClass
      ).length;

      // Apply changes to grid immediately
      onApply(updatedColumns);

      // Close dialog immediately for perceived performance
      onOpenChange(false);

      // Defer non-critical operations
      requestAnimationFrame(() => {
        // Hide processing state
        hideProgress();
        setIsProcessing(false);

        // Defer feedback to next tick
        Promise.resolve().then(() => {
          // Trigger feedback asynchronously
          triggerFeedback({
            haptic: true,
            visual: true,
            sound: soundEnabled
          });

          // Show toast if customizations were applied
          if (customizationCount > 0) {
            toast({
              title: 'Column customizations applied',
              description: `Updated ${customizationCount} column${customizationCount !== 1 ? 's' : ''} with custom styles and formatting`,
            });
          }
        });

        // Use aria-live region instead of creating/removing elements
        const announcement = customizationCount > 0 
          ? `Success! Applied customizations to ${customizationCount} column${customizationCount !== 1 ? 's' : ''}`
          : 'Settings applied successfully';

        // Announce to screen readers using existing aria-live region
        const liveRegion = document.getElementById('aria-live-region');
        if (liveRegion) {
          liveRegion.textContent = announcement;
          // Clear after announcement
          setTimeout(() => { liveRegion.textContent = ''; }, 3000);
        }
      });
    });
  }, [applyChanges, onApply, onOpenChange, toast, triggerFeedback, showProgress, hideProgress, soundEnabled]);

  // Discard changes
  const handleDiscardChanges = useCallback(() => {
    const changeCount = Array.from(pendingChanges.values()).reduce((acc, changes) => acc + Object.keys(changes).length, 0);
    resetChanges();
    toast({
      title: 'Changes discarded',
      description: `${changeCount} pending change${changeCount !== 1 ? 's' : ''} discarded`,
    });
  }, [resetChanges, pendingChanges, toast]);

  // Clear all customizations
  const handleClearAllCustomizations = useCallback(() => {
    setShowClearAllConfirmation(true);
  }, []);

  // Confirm clear all customizations
  const handleConfirmClearAll = useCallback(() => {
    const clearedCount = clearAllCustomizations();
    setShowClearAllConfirmation(false);
    toast({
      title: 'All columns cleared',
      description: `Removed customizations from ${clearedCount} column${clearedCount !== 1 ? 's' : ''}`,
    });
  }, [clearAllCustomizations, toast]);

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-[90vw] w-[1100px] h-[80vh] p-0 flex flex-col bg-background overflow-hidden">
        {/* Modern, Sophisticated Header */}
        <DialogHeader className="px-6 py-4 border-b bg-gradient-to-r from-background to-muted/30">
          <div className="flex items-center justify-between gap-4">
            <div className="flex items-center gap-3">
              <div className="flex h-9 w-9 items-center justify-center rounded-md bg-primary/10 border border-primary/20">
                <Settings2 className="h-4 w-4 text-primary" />
              </div>
              <div className="space-y-1">
                <DialogTitle className="text-lg font-semibold leading-none">
                  Column Settings
                </DialogTitle>
                <DialogDescription className="sr-only">
                  Configure column properties, styling, and behavior for the data grid
                </DialogDescription>
                <div className="flex items-center gap-2">
                  <Badge variant="secondary" className="text-xs px-2 py-0.5">
                    {selectedCount} selected
                  </Badge>
                  <Badge variant="outline" className="text-xs px-2 py-0.5">
                    {totalColumns} total
                  </Badge>
                  {pendingChanges.size > 0 && (
                    <Badge variant="default" className="text-xs px-2 py-0.5">
                      {Array.from(pendingChanges.values()).reduce((acc, changes) => acc + Object.keys(changes).length, 0)} changes
                    </Badge>
                  )}
                </div>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <Button
                variant="ghost"
                size="sm"
                onClick={toggleSound}
                className="h-8 w-8 p-0 rounded-full"
                title={soundEnabled ? "Disable sound effects" : "Enable sound effects"}
              >
                {soundEnabled ? (
                  <Volume2 className="h-4 w-4" />
                ) : (
                  <VolumeX className="h-4 w-4" />
                )}
                <span className="sr-only">{soundEnabled ? "Disable sound effects" : "Enable sound effects"}</span>
              </Button>
            </div>
          </div>
        </DialogHeader>

        {/* Clean Body Layout */}
        <div className="flex-1 flex overflow-hidden min-h-0 relative bg-background">
          {/* Column Selector - Mobile Drawer or Desktop Panel */}
          {showColumnDrawer ? (
            <>
              {/* Mobile-style column selector button */}
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowColumnDrawer(true)}
                className="fixed left-4 bottom-20 z-50 h-10 px-3 shadow-lg bg-background border"
              >
                <Columns className="h-4 w-4 mr-2" />
                Columns
              </Button>
              <Sheet open={showColumnDrawer} onOpenChange={setShowColumnDrawer}>
                <SheetContent side="left" className="w-[300px] p-0">
                  <SheetHeader className="px-4 py-3 border-b">
                    <SheetTitle>Select Columns</SheetTitle>
                  </SheetHeader>
                  <div className="flex-1 overflow-hidden">
                    <ColumnSelectorPanel />
                  </div>
                </SheetContent>
              </Sheet>
            </>
          ) : (
            <div className="w-[260px] border-r bg-muted/30 overflow-hidden flex flex-col">
              <ColumnSelectorPanel />
            </div>
          )}

          {/* Property Editor Panel */}
          <div className="flex-1 overflow-hidden flex flex-col min-w-0">
            <PropertyEditorPanel uiMode="advanced" />
          </div>

          {/* Quick Actions Panel - Collapsible */}
          {selectedColumns.size > 0 && (
            <>
              {/* Toggle Button */}
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setBulkActionsPanelCollapsed(!bulkActionsPanelCollapsed)}
                className={cn(
                  "absolute top-1/2 -translate-y-1/2 z-20 h-16 w-6 px-0 py-2 rounded-l-md rounded-r-none border border-r-0 bg-background hover:bg-muted/50 shadow-sm transition-all duration-300",
                  bulkActionsPanelCollapsed ? "right-4" : "right-[336px]"
                )}
                title={bulkActionsPanelCollapsed ? "Show Quick Actions" : "Hide Quick Actions"}
              >
                {bulkActionsPanelCollapsed ? (
                  <ChevronLeft className="h-4 w-4" />
                ) : (
                  <ChevronRight className="h-4 w-4" />
                )}
              </Button>

              {/* Collapsible Panel */}
              <div className={cn(
                "w-[320px] border-l bg-muted/30 overflow-hidden flex flex-col transition-all duration-300 mr-4",
                bulkActionsPanelCollapsed ? "w-0 mr-0" : "w-[320px] mr-4"
              )}>
                <div className="px-4 py-3 border-b bg-gradient-to-r from-muted/30 to-background">
                  <div className="flex items-center gap-2">
                    <Zap className="h-4 w-4 text-primary" />
                    <span className="text-sm font-semibold">Quick Actions</span>
                  </div>
                </div>
                <BulkActionsPanel />
              </div>
            </>
          )}
        </div>

        {/* Modern Footer */}
        <DialogFooter className="px-6 py-3 border-t flex items-center justify-between bg-gradient-to-r from-muted/30 to-background">
          <div className="flex items-center gap-2">
            <Button
              variant="ghost"
              size="sm"
              disabled={true}
              className="h-8 px-3 gap-1.5 text-muted-foreground"
            >
              <Undo2 className="h-3.5 w-3.5" />
              <span className="text-sm">Undo</span>
            </Button>
            <Button
              variant="ghost"
              size="sm"
              disabled={true}
              className="h-8 px-3 gap-1.5 text-muted-foreground"
            >
              <Redo2 className="h-3.5 w-3.5" />
              <span className="text-sm">Redo</span>
            </Button>

            {/* Clear All Customizations Button */}
            <div className="h-4 w-px bg-border mx-1" />
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClearAllCustomizations}
              disabled={!hasAnyCustomizations}
              className="h-8 px-3 gap-1.5 font-bold text-red-500 hover:text-red-500 hover:bg-red-50 dark:hover:bg-red-950/20 disabled:text-muted-foreground disabled:hover:text-muted-foreground disabled:hover:bg-transparent disabled:font-normal"
              title={hasAnyCustomizations ? "Remove all customizations from all columns" : "No customizations to clear"}
              style={{ color: hasAnyCustomizations ? 'tomato' : undefined }}
            >
              <Trash2 className="h-3.5 w-3.5" />
              <span className="text-sm">Clear All Columns</span>
            </Button>
          </div>

          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleDiscardChanges}
              className="h-8 px-4 text-sm"
              disabled={isProcessing}
            >
              Reset
            </Button>
            <div className="relative">
              <Button
                ref={applyCloseButtonRef}
                variant="default"
                size="sm"
                onClick={handleApplyAndClose}
                disabled={isProcessing}
                className="h-8 px-6 text-sm relative overflow-visible min-w-[120px]"
              >
                {isProcessing ? (
                  <div className="flex items-center gap-2">
                    <div className="w-3 h-3 border border-primary-foreground/30 border-t-primary-foreground rounded-full animate-spin" />
                    <span>Applying...</span>
                  </div>
                ) : (
                  'Apply & Close'
                )}
              </Button>
              <div 
                ref={progressRef} 
                className="absolute bottom-0 left-0 right-0 h-0.5 bg-transparent overflow-hidden transition-opacity duration-300"
                style={{ opacity: 0 }}
              >
                <div className="h-full w-full bg-primary animate-[slide_1.5s_ease-in-out_infinite]" 
                     style={{ 
                       background: 'linear-gradient(90deg, transparent, hsl(var(--primary) / 0.8), transparent)',
                       transform: 'translateX(-100%)'
                     }} 
                />
              </div>
            </div>
          </div>
        </DialogFooter>

        {/* Aria live region for screen reader announcements */}
        <div id="aria-live-region" className="sr-only" role="status" aria-live="polite" aria-atomic="true" />
      </DialogContent>

      {/* Clear All Customizations Confirmation Dialog */}
      <Dialog open={showClearAllConfirmation} onOpenChange={setShowClearAllConfirmation}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Trash2 className="h-5 w-5 text-destructive" />
              Clear All Columns
            </DialogTitle>
            <DialogDescription>
              This will remove all customizations from {customizedColumnsCount} column{customizedColumnsCount !== 1 ? 's' : ''}.
            </DialogDescription>
          </DialogHeader>
          <div className="py-4 space-y-3">
            <p className="text-sm text-muted-foreground">The following will be removed:</p>
            <ul className="list-disc list-inside space-y-1 text-sm text-muted-foreground">
              <li>Cell and header styling</li>
              <li>Value formatters</li>
              <li>Filters and editors</li>
              <li>Column sizing and positioning</li>
              <li>Applied templates</li>
            </ul>
            <p className="text-sm font-medium text-foreground">This action cannot be undone.</p>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setShowClearAllConfirmation(false)}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={handleConfirmClearAll}
            >
              Clear All Columns
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </Dialog>
  );
});

ColumnCustomizationDialog.displayName = 'ColumnCustomizationDialog';

import React, { useState, useEffect, useMemo } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Separator } from '@/components/ui/separator';
import { Switch } from '@/components/ui/switch';
import { ScrollArea } from '@/components/ui/scroll-area';
import { 
  Plus, 
  Trash2, 
  Eye, 
  Palette,
  ArrowUp,
  ArrowDown,
  Settings,
  Copy,
  Check
} from 'lucide-react';

interface ValueFormatterEditorProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  initialFormatter?: (params: { value: unknown }) => string;
  onSave: (formatter: (params: { value: unknown }) => string, cellStyle?: (params: { value: unknown }) => React.CSSProperties) => void;
  title: string;
  columnType?: 'text' | 'number' | 'date' | 'boolean';
}

interface FormattingRule {
  id: string;
  condition: {
    type: 'equals' | 'contains' | 'startsWith' | 'endsWith' | 'greaterThan' | 'lessThan' | 'greaterEqual' | 'lessEqual' | 'between' | 'isEmpty' | 'isNotEmpty';
    value: string;
    value2?: string; // for 'between' condition
  };
  display: {
    type: 'text' | 'original' | 'custom';
    text: string;
  };
  styling: {
    backgroundColor?: string;
    textColor?: string;
    fontSize?: number;
    fontWeight?: 'normal' | 'bold' | '100' | '200' | '300' | '400' | '500' | '600' | '700' | '800' | '900';
    fontStyle?: 'normal' | 'italic';
    textDecoration?: 'none' | 'underline' | 'line-through';
    textAlign?: 'left' | 'center' | 'right';
    border?: {
      width: number;
      style: 'solid' | 'dashed' | 'dotted' | 'double';
      color: string;
      sides: {
        top: boolean;
        right: boolean;
        bottom: boolean;
        left: boolean;
      };
    };
    padding?: {
      top: number;
      right: number;
      bottom: number;
      left: number;
    };
  };
  enabled: boolean;
}

const DEFAULT_RULE: Omit<FormattingRule, 'id'> = {
  condition: {
    type: 'equals',
    value: ''
  },
  display: {
    type: 'custom',
    text: ''
  },
  styling: {
    // All styling properties start as undefined - only apply what user explicitly sets
    backgroundColor: undefined,
    textColor: undefined,
    fontSize: undefined,
    fontWeight: undefined,
    fontStyle: undefined,
    textDecoration: undefined,
    textAlign: undefined,
    border: {
      width: 0,
      style: 'solid',
      color: '#000000',
      sides: {
        top: false,
        right: false,
        bottom: false,
        left: false
      }
    },
    padding: {
      top: 0,
      right: 0,
      bottom: 0,
      left: 0
    }
  },
  enabled: true
};

const CONDITION_OPTIONS = [
  { value: 'equals', label: 'Equals', needsValue: true, needsValue2: false },
  { value: 'contains', label: 'Contains', needsValue: true, needsValue2: false },
  { value: 'startsWith', label: 'Starts with', needsValue: true, needsValue2: false },
  { value: 'endsWith', label: 'Ends with', needsValue: true, needsValue2: false },
  { value: 'greaterThan', label: 'Greater than', needsValue: true, needsValue2: false },
  { value: 'lessThan', label: 'Less than', needsValue: true, needsValue2: false },
  { value: 'greaterEqual', label: 'Greater than or equal', needsValue: true, needsValue2: false },
  { value: 'lessEqual', label: 'Less than or equal', needsValue: true, needsValue2: false },
  { value: 'between', label: 'Between', needsValue: true, needsValue2: true },
  { value: 'isEmpty', label: 'Is empty', needsValue: false, needsValue2: false },
  { value: 'isNotEmpty', label: 'Is not empty', needsValue: false, needsValue2: false }
];

const SAMPLE_DATA = {
  text: ['Sample Text', 'Hello World', 'Test', '', 'Option1'],
  number: [1, 25, 100, 0, -5, 150],
  date: ['2024-01-15', '2024-12-25', '2024-06-30'],
  boolean: [true, false, true]
};

export const ValueFormatterEditor: React.FC<ValueFormatterEditorProps> = ({
  open,
  onOpenChange,
  initialFormatter,
  onSave,
  title,
  columnType = 'text'
}) => {
  const [rules, setRules] = useState<FormattingRule[]>([]);
  const [defaultFallback, setDefaultFallback] = useState({
    display: { type: 'original' as const, text: '' },
    styling: {
      // Default fallback styling also starts undefined - no styles unless explicitly set
      backgroundColor: undefined,
      textColor: undefined,
      fontSize: undefined,
      fontWeight: undefined,
      fontStyle: undefined,
      textDecoration: undefined,
      textAlign: undefined,
      border: {
        width: 0,
        style: 'solid' as const,
        color: '#000000',
        sides: {
          top: false,
          right: false,
          bottom: false,
          left: false
        }
      },
      padding: {
        top: 0,
        right: 0,
        bottom: 0,
        left: 0
      }
    }
  });
  const [copiedFormatter, setCopiedFormatter] = useState(false);
  const [copiedCellStyle, setCopiedCellStyle] = useState(false);
  const [copiedExcel, setCopiedExcel] = useState(false);

  // Initialize rules from existing formatter
  useEffect(() => {
    if (initialFormatter && typeof initialFormatter === 'function') {
      // Check if the formatter has visual editor metadata
      const visualRules = (initialFormatter as any).__visualRules;
      const visualDefaultFallback = (initialFormatter as any).__visualDefaultFallback;

      console.log('[ValueFormatterEditor] Initializing from existing formatter:', {
        hasVisualRules: !!visualRules,
        hasDefaultFallback: !!visualDefaultFallback,
        rulesCount: visualRules?.length || 0
      });

      if (visualRules && Array.isArray(visualRules)) {
        // Restore the original rules
        setRules(visualRules);
        console.log('[ValueFormatterEditor] Restored', visualRules.length, 'rules from metadata');
      } else {
        // No visual rules available - start with empty rules
        setRules([]);
        console.log('[ValueFormatterEditor] No visual rules found - starting with empty rules');
      }

      if (visualDefaultFallback) {
        // Restore the default fallback
        setDefaultFallback(visualDefaultFallback);
        console.log('[ValueFormatterEditor] Restored default fallback from metadata');
      }
    } else {
      setRules([]);
    }
  }, [initialFormatter]);

  const addRule = () => {
    const newRule: FormattingRule = {
      id: Date.now().toString(),
      ...DEFAULT_RULE
    };
    setRules(prev => [...prev, newRule]);
  };

  const updateRule = (id: string, updates: Partial<FormattingRule>) => {
    setRules(prev => prev.map(rule => 
      rule.id === id ? { ...rule, ...updates } : rule
    ));
  };

  const deleteRule = (id: string) => {
    setRules(prev => prev.filter(rule => rule.id !== id));
  };

  const moveRule = (id: string, direction: 'up' | 'down') => {
    setRules(prev => {
      const index = prev.findIndex(rule => rule.id === id);
      if (index === -1) return prev;

      const newIndex = direction === 'up' ? index - 1 : index + 1;
      if (newIndex < 0 || newIndex >= prev.length) return prev;

      const newRules = [...prev];
      [newRules[index], newRules[newIndex]] = [newRules[newIndex], newRules[index]];
      return newRules;
    });
  };

  // Check if a value matches a condition
  const matchesCondition = (value: unknown, condition: FormattingRule['condition']): boolean => {
    const strValue = String(value || '');
    const numValue = Number(value);

    switch (condition.type) {
      case 'equals':
        return strValue === condition.value;
      case 'contains':
        return strValue.toLowerCase().includes(condition.value.toLowerCase());
      case 'startsWith':
        return strValue.toLowerCase().startsWith(condition.value.toLowerCase());
      case 'endsWith':
        return strValue.toLowerCase().endsWith(condition.value.toLowerCase());
      case 'greaterThan':
        return !isNaN(numValue) && numValue > Number(condition.value);
      case 'lessThan':
        return !isNaN(numValue) && numValue < Number(condition.value);
      case 'greaterEqual':
        return !isNaN(numValue) && numValue >= Number(condition.value);
      case 'lessEqual':
        return !isNaN(numValue) && numValue <= Number(condition.value);
      case 'between':
        return !isNaN(numValue) && numValue >= Number(condition.value) && numValue <= Number(condition.value2 || condition.value);
      case 'isEmpty':
        return strValue === '';
      case 'isNotEmpty':
        return strValue !== '';
      default:
        return false;
    }
  };

  // Generate display text based on rule
  const getDisplayText = (value: unknown, display: FormattingRule['display']): string => {
    switch (display.type) {
      case 'original':
        return String(value || '');
      case 'text':
      case 'custom':
        return display.text;
      default:
        return String(value || '');
    }
  };

  // Convert styling object to CSS properties - only include explicitly set properties
  const styleToCss = (styling: FormattingRule['styling']): React.CSSProperties => {
    const css: React.CSSProperties = {};

    // Only include properties that are explicitly defined (not undefined)
    if (styling.backgroundColor !== undefined) {
      css.backgroundColor = styling.backgroundColor;
    }
    if (styling.textColor !== undefined) {
      css.color = styling.textColor;
    }
    if (styling.fontSize !== undefined) {
      css.fontSize = `${styling.fontSize}px`;
    }
    if (styling.fontWeight !== undefined) {
      css.fontWeight = styling.fontWeight;
    }
    if (styling.fontStyle !== undefined) {
      css.fontStyle = styling.fontStyle;
    }
    if (styling.textDecoration !== undefined) {
      css.textDecoration = styling.textDecoration;
    }
    if (styling.textAlign !== undefined) {
      css.textAlign = styling.textAlign;
    }

    // Handle padding - only if any side has a value > 0
    if (styling.padding && (styling.padding.top > 0 || styling.padding.right > 0 || styling.padding.bottom > 0 || styling.padding.left > 0)) {
      css.padding = `${styling.padding.top}px ${styling.padding.right}px ${styling.padding.bottom}px ${styling.padding.left}px`;
    }

    // Handle borders - only if width > 0 and at least one side is enabled
    if (styling.border && styling.border.width > 0 && (styling.border.sides.top || styling.border.sides.right || styling.border.sides.bottom || styling.border.sides.left)) {
      const borderStyle = `${styling.border.width}px ${styling.border.style} ${styling.border.color}`;
      if (styling.border.sides.top) css.borderTop = borderStyle;
      if (styling.border.sides.right) css.borderRight = borderStyle;
      if (styling.border.sides.bottom) css.borderBottom = borderStyle;
      if (styling.border.sides.left) css.borderLeft = borderStyle;
    }

    console.log('[ValueFormatterEditor] styleToCss generated:', css);
    return css;
  };

  // Generate the formatter function
  const generateFormatter = (): (params: { value: unknown }) => string => {
    return (params: { value: unknown }) => {
      const value = params.value;

      // Check each rule in order
      for (const rule of rules) {
        if (rule.enabled && matchesCondition(value, rule.condition)) {
          return getDisplayText(value, rule.display);
        }
      }

      // Fallback to default
      return getDisplayText(value, defaultFallback.display);
    };
  };

  // Generate the cell style function
  const generateCellStyle = (): (params: { value: unknown }) => React.CSSProperties | undefined => {
    return (params: { value: unknown }) => {
      const value = params.value;

      // Check each rule in order
      for (const rule of rules) {
        if (rule.enabled && matchesCondition(value, rule.condition)) {
          const ruleStyles = styleToCss(rule.styling);
          // Only return styles if there are actually some set
          return Object.keys(ruleStyles).length > 0 ? ruleStyles : undefined;
        }
      }

      // Fallback to default styling - only if there are actually some styles set
      const fallbackStyles = styleToCss(defaultFallback.styling);
      return Object.keys(fallbackStyles).length > 0 ? fallbackStyles : undefined;
    };
  };

     // Generate Excel-style format string with extended styling syntax
   const generateExcelFormatString = (): string => {
     if (rules.length === 0) {
       return '@'; // Default: show original value
     }

     const sections: string[] = [];

     // Process each rule to create format sections
     rules.forEach((rule, _index) => {
       if (!rule.enabled) return;

       let section = '';

       // Add condition
       switch (rule.condition.type) {
         case 'equals':
           // For exact matches, we'll use positive/negative/zero format
           if (!isNaN(Number(rule.condition.value))) {
             const num = Number(rule.condition.value);
             if (num > 0) section += '[>0]';
             else if (num < 0) section += '[<0]';
             else section += '[=0]';
           } else {
             section += `[="${rule.condition.value}"]`;
           }
           break;
         case 'greaterThan':
           section += `[>${rule.condition.value}]`;
           break;
         case 'lessThan':
           section += `[<${rule.condition.value}]`;
           break;
         case 'greaterEqual':
           section += `[>=${rule.condition.value}]`;
           break;
         case 'lessEqual':
           section += `[<=${rule.condition.value}]`;
           break;
         case 'between':
           section += `[>=${rule.condition.value}][<=${rule.condition.value2 || rule.condition.value}]`;
           break;
         case 'isEmpty':
           section += '[@=""]';
           break;
         case 'isNotEmpty':
           section += '[<>""]';
           break;
         case 'contains':
           section += `[="*${rule.condition.value}*"]`;
           break;
         case 'startsWith':
           section += `[="${rule.condition.value}*"]`;
           break;
         case 'endsWith':
           section += `[="*${rule.condition.value}"]`;
           break;
         default:
           // For text conditions, we'll create a text-based format
           section += `[="${rule.condition.value}"]`;
       }

       // Add extended styling directives - only for explicitly set properties

       // Text color - only if explicitly set
       if (rule.styling.textColor !== undefined) {
         const colorName = getColorName(rule.styling.textColor);
         if (colorName) {
           section += `[${colorName}]`;
         } else {
           section += `[${rule.styling.textColor}]`;
         }
       }

       // Background color - only if explicitly set
       if (rule.styling.backgroundColor !== undefined) {
         section += `[BG:${rule.styling.backgroundColor}]`;
       }

       // Font size - only if explicitly set
       if (rule.styling.fontSize !== undefined) {
         section += `[Size:${rule.styling.fontSize}]`;
       }

       // Font weight - only if explicitly set
       if (rule.styling.fontWeight !== undefined) {
         if (rule.styling.fontWeight === 'bold' || rule.styling.fontWeight === '700') {
           section += '[Bold]';
         } else {
           section += `[Weight:${rule.styling.fontWeight}]`;
         }
       }

       // Font style - only if explicitly set
       if (rule.styling.fontStyle !== undefined && rule.styling.fontStyle === 'italic') {
         section += '[Italic]';
       }

       // Text decoration - only if explicitly set
       if (rule.styling.textDecoration !== undefined) {
         if (rule.styling.textDecoration === 'underline') {
           section += '[Underline]';
         } else if (rule.styling.textDecoration === 'line-through') {
           section += '[Strikethrough]';
         }
       }

       // Text alignment - only if explicitly set
       if (rule.styling.textAlign !== undefined) {
         if (rule.styling.textAlign === 'center') {
           section += '[Center]';
         } else if (rule.styling.textAlign === 'right') {
           section += '[Right]';
         } else if (rule.styling.textAlign === 'left') {
           section += '[Left]';
         }
       }

       // Border - only if explicitly set (width > 0 and at least one side enabled)
       if (rule.styling.border && rule.styling.border.width > 0 && (rule.styling.border.sides.top || rule.styling.border.sides.right || rule.styling.border.sides.bottom || rule.styling.border.sides.left)) {
         const { width, style, color, sides } = rule.styling.border;

         // Check if all sides are enabled (full border)
         const allSides = sides.top && sides.right && sides.bottom && sides.left;

         if (allSides) {
           section += `[Border:${width}px-${style}-${color}]`;
         } else {
           // For individual sides, we'll use a simplified notation
           const activeSides = [];
           if (sides.top) activeSides.push('top');
           if (sides.right) activeSides.push('right');  
           if (sides.bottom) activeSides.push('bottom');
           if (sides.left) activeSides.push('left');
           section += `[Border:${width}px-${style}-${color}-${activeSides.join('+')}]`;
         }
       }

       // Padding - only if explicitly set (any side > 0)
       if (rule.styling.padding && (rule.styling.padding.top > 0 || rule.styling.padding.right > 0 || rule.styling.padding.bottom > 0 || rule.styling.padding.left > 0)) {
         const { top, right, bottom, left } = rule.styling.padding;

         if (top === right && right === bottom && bottom === left) {
           // All sides same
           section += `[P:${top}px]`;
         } else if (top === bottom && left === right) {
           // Vertical/horizontal same
           section += `[P:${top}px-${left}px]`;
         } else {
           // All different
           section += `[P:${top}px-${right}px-${bottom}px-${left}px]`;
         }
       }

       // Add display format
       if (rule.display.type === 'custom') {
         section += `"${rule.display.text}"`;
       } else {
         // Determine number format based on column type and value
         if (columnType === 'number') {
           section += '#,##0.00';
         } else {
           section += '@'; // Text format
         }
       }

       sections.push(section);
     });

     // Add default fallback
     if (defaultFallback.display.type === 'text') {
       sections.push(`"${defaultFallback.display.text}"`);
     } else {
       sections.push('@'); // Show original value
     }

     return sections.join(';') || '@';
   };

   // Helper function to get color names for Excel format
   const getColorName = (hexColor: string): string | null => {
     const colorMap: Record<string, string> = {
       '#ff0000': 'Red',
       '#00ff00': 'Green', 
       '#0000ff': 'Blue',
       '#ffff00': 'Yellow',
       '#ff00ff': 'Magenta',
       '#00ffff': 'Cyan',
       '#000000': 'Black',
       '#ffffff': 'White',
       '#16a34a': 'Green',
       '#dc2626': 'Red',
       '#2563eb': 'Blue',
       '#ca8a04': 'Yellow'
     };
     return colorMap[hexColor.toLowerCase()] || null;
   };

   // Generate readable formatter code for display
   const generateFormatterCode = (): string => {
     if (rules.length === 0) {
       return `(params) => {
   // No rules defined - return original value
   return String(params.value || '');
 }`;
     }

     const conditions = rules.map((rule, index) => {
       if (!rule.enabled) return `  // Rule ${index + 1} (disabled)`;

       let conditionCheck = '';
       const value = rule.condition.value;
       const value2 = rule.condition.value2;

       switch (rule.condition.type) {
         case 'equals':
           conditionCheck = `String(value) === '${value}'`;
           break;
         case 'contains':
           conditionCheck = `String(value).toLowerCase().includes('${value.toLowerCase()}')`;
           break;
         case 'startsWith':
           conditionCheck = `String(value).toLowerCase().startsWith('${value.toLowerCase()}')`;
           break;
         case 'endsWith':
           conditionCheck = `String(value).toLowerCase().endsWith('${value.toLowerCase()}')`;
           break;
         case 'greaterThan':
           conditionCheck = `Number(value) > ${value}`;
           break;
         case 'lessThan':
           conditionCheck = `Number(value) < ${value}`;
           break;
         case 'greaterEqual':
           conditionCheck = `Number(value) >= ${value}`;
           break;
         case 'lessEqual':
           conditionCheck = `Number(value) <= ${value}`;
           break;
         case 'between':
           conditionCheck = `Number(value) >= ${value} && Number(value) <= ${value2 || value}`;
           break;
         case 'isEmpty':
           conditionCheck = `String(value) === ''`;
           break;
         case 'isNotEmpty':
           conditionCheck = `String(value) !== ''`;
           break;
       }

       const returnValue = rule.display.type === 'custom' 
         ? `'${rule.display.text}'` 
         : 'String(value)';

       return `  if (${conditionCheck}) {
     return ${returnValue}; // Rule ${index + 1}
   }`;
     }).filter(Boolean).join('\n');

     const fallbackReturn = defaultFallback.display.type === 'text' 
       ? `'${defaultFallback.display.text}'`
       : 'String(value || \'\')';

     return `(params) => {
   const value = params.value;

 ${conditions}

   // Default fallback
   return ${fallbackReturn};
 }`;
   };

   // Generate readable cell style code for display
   const generateCellStyleCode = (): string => {
     if (rules.length === 0) {
       return `(params) => {
   // No styling rules defined
   return ${JSON.stringify(styleToCss(defaultFallback.styling), null, 2)};
 }`;
     }

     const conditions = rules.map((rule, index) => {
       if (!rule.enabled) return `  // Rule ${index + 1} (disabled)`;

       let conditionCheck = '';
       const value = rule.condition.value;
       const value2 = rule.condition.value2;

       switch (rule.condition.type) {
         case 'equals':
           conditionCheck = `String(value) === '${value}'`;
           break;
         case 'contains':
           conditionCheck = `String(value).toLowerCase().includes('${value.toLowerCase()}')`;
           break;
         case 'greaterThan':
           conditionCheck = `Number(value) > ${value}`;
           break;
         case 'lessThan':
           conditionCheck = `Number(value) < ${value}`;
           break;
         case 'between':
           conditionCheck = `Number(value) >= ${value} && Number(value) <= ${value2 || value}`;
           break;
         case 'isEmpty':
           conditionCheck = `String(value) === ''`;
           break;
         case 'isNotEmpty':
           conditionCheck = `String(value) !== ''`;
           break;
         default:
           conditionCheck = `String(value) === '${value}'`;
       }

       const styles = styleToCss(rule.styling);
       const styleString = JSON.stringify(styles, null, 4).replace(/\n/g, '\n     ');

       return `  if (${conditionCheck}) {
     return ${styleString}; // Rule ${index + 1}
   }`;
     }).filter(Boolean).join('\n');

     const fallbackStyles = styleToCss(defaultFallback.styling);
     const fallbackStyleString = JSON.stringify(fallbackStyles, null, 2);

     return `(params) => {
   const value = params.value;

 ${conditions}

   // Default fallback
   return ${fallbackStyleString};
 }`;
   };

   // Preview data with applied formatting
   const previewData = useMemo(() => {
     const sampleValues = SAMPLE_DATA[columnType] || SAMPLE_DATA.text;
     const formatter = generateFormatter();
     const styleFunc = generateCellStyle();

     return sampleValues.map(value => ({
       original: value,
       formatted: formatter({ value }),
       style: styleFunc({ value })
     }));
   }, [rules, defaultFallback, columnType]);

     const handleSave = () => {
     const formatter = generateFormatter();
     const cellStyle = generateCellStyle();
     const excelFormatString = generateExcelFormatString();

     // Attach Excel format string metadata to the formatter
     Object.defineProperty(formatter, '__formatString', { 
       value: excelFormatString, 
       writable: false,
       enumerable: false,
       configurable: true
     });

     // Attach formatter type metadata
     Object.defineProperty(formatter, '__formatterType', { 
       value: 'visual', 
       writable: false,
       enumerable: false,
       configurable: true
     });

     // Attach visual editor metadata for full round-trip editing
     Object.defineProperty(formatter, '__visualRules', { 
       value: rules, 
       writable: false,
       enumerable: false,
       configurable: true
     });

     Object.defineProperty(formatter, '__visualDefaultFallback', { 
       value: defaultFallback, 
       writable: false,
       enumerable: false,
       configurable: true
     });

     console.log('[ValueFormatterEditor] Generated formatter with metadata:', {
       formatString: excelFormatString,
       rulesCount: rules.length,
       hasDefaultFallback: !!defaultFallback
     });

     onSave(formatter, cellStyle);
     onOpenChange(false);
   };

   // Copy to clipboard functions
   const copyFormatterCode = async () => {
     try {
       await navigator.clipboard.writeText(generateFormatterCode());
       setCopiedFormatter(true);
       setTimeout(() => setCopiedFormatter(false), 2000);
     } catch (err) {
       console.error('Failed to copy formatter code:', err);
     }
   };

   const copyCellStyleCode = async () => {
     try {
       await navigator.clipboard.writeText(generateCellStyleCode());
       setCopiedCellStyle(true);
       setTimeout(() => setCopiedCellStyle(false), 2000);
     } catch (err) {
       console.error('Failed to copy cell style code:', err);
     }
   };

   const copyExcelFormat = async () => {
     try {
       await navigator.clipboard.writeText(generateExcelFormatString());
       setCopiedExcel(true);
       setTimeout(() => setCopiedExcel(false), 2000);
     } catch (err) {
       console.error('Failed to copy Excel format:', err);
     }
   };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-7xl h-[800px] p-0 flex flex-col">
        <DialogHeader className="px-6 py-4 border-b">
          <DialogTitle className="flex items-center gap-2">
            <Palette className="h-5 w-5" />
            {title} - Conditional Formatter Builder
          </DialogTitle>
        </DialogHeader>

        <div className="flex gap-4 h-full overflow-hidden">
          {/* Rules Panel */}
          <div className="flex-1 flex flex-col">
            <div className="p-4 border-b bg-muted/30">
