// Template storage key
const TEMPLATES_STORAGE_KEY = 'column-customization-templates';

// Template interface
interface ColumnTemplate {
  id: string;
  name: string;
  createdAt: number;
  properties: Partial<ColDef>;
  isSystem?: boolean; // Flag to identify system templates
}

// Predefined system templates
const SYSTEM_TEMPLATES: ColumnTemplate[] = [
  {
    id: 'system-currency',
    name: 'Currency Format',
    createdAt: Date.now(),
    isSystem: true,
    properties: {
      valueFormatter: (params: any) => {
        if (params.value == null) return '';
        return new Intl.NumberFormat('en-US', {
          style: 'currency',
          currency: 'USD'
        }).format(params.value);
      },
      cellStyle: { textAlign: 'right', fontWeight: '500' },
      headerStyle: () => ({ textAlign: 'right', fontWeight: '600' }),
      type: 'numericColumn'
    }
  },
  {
    id: 'system-percentage',
    name: 'Percentage Format',
    createdAt: Date.now(),
    isSystem: true,
    properties: {
      valueFormatter: (params: any) => {
        if (params.value == null) return '';
        return `${(params.value * 100).toFixed(2)}%`;
      },
      cellStyle: { textAlign: 'right', color: '#059669' },
      headerStyle: () => ({ textAlign: 'right' }),
      type: 'numericColumn'
    }
  },
  {
    id: 'system-date',
    name: 'Date Format',
    createdAt: Date.now(),
    isSystem: true,
    properties: {
      valueFormatter: (params: any) => {
        if (!params.value) return '';
        return new Date(params.value).toLocaleDateString('en-US', {
          year: 'numeric',
          month: 'short',
          day: 'numeric'
        });
      },
      cellStyle: { textAlign: 'center' },
      headerStyle: () => ({ textAlign: 'center' }),
      filter: 'agDateColumnFilter'
    }
  },
  {
    id: 'system-bold-header',
    name: 'Bold Headers',
    createdAt: Date.now(),
    isSystem: true,
    properties: {
      headerStyle: () => ({ 
        fontWeight: '700', 
        backgroundColor: '#f8fafc',
        borderBottom: '2px solid #e2e8f0'
      }),
      wrapHeaderText: true,
      autoHeaderHeight: true
    }
  },
  {
    id: 'system-center-align',
