        };
        const color = colors[status as keyof typeof colors] || '#6b7280';
        return `<span style="background: ${color}; color: white; padding: 2px 8px; border-radius: 12px; font-size: 11px; font-weight: 500;">${params.value}</span>`;
      },
      cellStyle: { textAlign: 'center' },
      headerStyle: () => ({ textAlign: 'center' })
    }
  }
];

// Helper function to get system template descriptions
const getSystemTemplateDescription = (templateId: string): string => {
  const descriptions: Record<string, string> = {
    'system-currency': 'Formats numbers as currency with right alignment',
    'system-percentage': 'Displays values as percentages with green color',
    'system-date': 'Formats dates in readable format with center alignment',
    'system-bold-header': 'Makes headers bold with enhanced styling',
    'system-center-align': 'Centers text in both cells and headers',
    'system-highlight': 'Highlights column with yellow background',
    'system-compact': 'Reduces font size and padding for compact view',
    'system-status': 'Renders status values as colored badges'
  };
  return descriptions[templateId] || 'System template';
};

export const BulkActionsPanel: React.FC = () => {
  const {
    selectedColumns,
    columnDefinitions,
    pendingChanges,
    updateBulkProperties,
    setAppliedTemplate
  } = useColumnCustomizationStore();

  const [selectedTemplateIds, setSelectedTemplateIds] = useState<string[]>([]);
  const [userTemplates, setUserTemplates] = useState<ColumnTemplate[]>([]);
  const [showSaveDialog, setShowSaveDialog] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const [templateName, setTemplateName] = useState('');
  const [templateToDelete, setTemplateToDelete] = useState<string>('');
  const [editingTemplateId, setEditingTemplateId] = useState<string>('');

  // Combine system and user templates
  const templates = useMemo(() => {
    return [...SYSTEM_TEMPLATES, ...userTemplates];
  }, [userTemplates]);

  // Properties to save in templates - comprehensive list
  const TEMPLATE_PROPERTIES = [
    // NOTE: 'field' and 'headerName' are intentionally excluded from templates
    // They should only be applied to single columns, not in bulk

    // Data type and basic properties
    'cellDataType', 'type', 'valueGetter', 'valueSetter',

    // Filter configurations
    'filter', 'filterParams', 'floatingFilter', 'floatingFilterComponent', 'floatingFilterComponentParams',
    'suppressHeaderMenuButton', 'suppressFiltersToolPanel', 'filterValueGetter',

    // Editor configurations
    'editable', 'cellEditor', 'cellEditorParams', 'cellEditorPopup', 'cellEditorPopupPosition',
    'singleClickEdit', 'stopEditingWhenCellsLoseFocus', 'cellEditorSelector',

    // Format configurations
    'valueFormatter', 'useValueFormatterForExport',
    'cellClass', 'cellClassRules', 'cellStyle',

    // Header configurations
    'headerClass', 'headerStyle', 'headerTooltip', 'headerComponent', 'headerComponentParams',
    'headerTextAlign', 'headerCheckboxSelection', 'headerCheckboxSelectionFilteredOnly',
    'wrapHeaderText', 'autoHeaderHeight',  // Added from StylingTab

    // Cell renderer
    'cellRenderer', 'cellRendererParams', 'cellRendererSelector',

    // Layout and display
    'wrapText', 'autoHeight', 'rowSpan', 'colSpan',
    'textAlign', 'verticalAlign',

    // Sorting and aggregation
    'sortable', 'sort', 'sortingOrder', 'comparator',
    'unSortIcon', 'aggFunc', 'allowedAggFuncs',

    // Pinning and sizing
    'pinned', 'lockPosition', 'lockPinned', 'lockVisible',
    'width', 'minWidth', 'maxWidth', 'flex',
    'resizable', 'suppressSizeToFit',
    'initialWidth', 'initialHide', 'initialPinned',  // Added from GeneralTab

    // Tooltips
    'tooltip', 'tooltipField', 'tooltipValueGetter', 'tooltipComponent', 'tooltipComponentParams',

    // Other properties
    'suppressKeyboardEvent', 'suppressNavigable', 'suppressPaste',
    'checkboxSelection', 'showDisabledCheckboxes'
  ];

  // Load templates from localStorage
  useEffect(() => {
    const storedTemplates = localStorage.getItem(TEMPLATES_STORAGE_KEY);
    if (storedTemplates) {
      try {
        setUserTemplates(JSON.parse(storedTemplates));
      } catch (e) {
        console.error('Failed to load templates:', e);
      }
    }
  }, []);

  // Save templates to localStorage
  const saveTemplates = useCallback((newTemplates: ColumnTemplate[]) => {
    setUserTemplates(newTemplates);
    localStorage.setItem(TEMPLATES_STORAGE_KEY, JSON.stringify(newTemplates));
  }, []);

  // Get current column configuration
  const getCurrentColumnConfig = useCallback(() => {
    if (selectedColumns.size === 0) return null;

    // Get the first selected column's configuration
    const firstColumnId = Array.from(selectedColumns)[0];
    const columnDef = columnDefinitions.get(firstColumnId);
    const changes = pendingChanges.get(firstColumnId);

    if (!columnDef) return null;

    const config: Partial<ColDef> = {};

    TEMPLATE_PROPERTIES.forEach(property => {
      // Check pending changes first, then column definition
      const value = changes?.[property] ?? columnDef[property as keyof ColDef];
      if (value !== undefined) {
        // Special handling for functions - convert to serializable format
        if (typeof value === 'function') {
          // For formatters, we just copy the function reference
          if (property === 'valueFormatter') {
            // Store the formatter function directly
            if (value && typeof value === 'function') {
              config[property] = value;
            }
          } else if (property === 'headerStyle') {
            // Special handling for headerStyle function
            try {
              // Extract styles for both regular header and floating filter
              const regularStyle = value({ floatingFilter: false });
              const floatingStyle = value({ floatingFilter: true });

              // Store as an object that preserves the conditional logic
              config[property] = {
                _isHeaderStyleConfig: true,
                regular: regularStyle,
                floating: floatingStyle
              };
            } catch (e) {
              console.warn(`Failed to extract headerStyle:`, e);
              // Try to store just the regular style
              try {
                const regularStyle = value({});
                if (regularStyle && typeof regularStyle === 'object') {
                  config[property] = regularStyle;
                }
              } catch (e2) {
                console.warn(`Failed to extract headerStyle completely:`, e2);
              }
            }
          } else if (property === 'cellStyle') {
            // Check if this is our conditional formatting function with metadata
            const formatString = (value as any).__formatString;
            const baseStyle = (value as any).__baseStyle;

            if (formatString) {
              // Store as a configuration object that can be recreated
              config[property] = {
                type: 'function',
                formatString: formatString,
                baseStyle: baseStyle
              };
            } else {
              // Extract style by calling the function if it's a function
              try {
                const extractedStyle = value({});
                if (extractedStyle && typeof extractedStyle === 'object') {
                  config[property] = extractedStyle;
                }
              } catch (e) {
                // If function fails, try to store as is
                console.warn(`Failed to extract cellStyle:`, e);
              }
            }
          } else if (property === 'comparator' || property === 'valueGetter' || property === 'valueSetter' || 
                     property === 'cellRenderer' || property === 'cellEditor' || property === 'filterValueGetter' ||
                     property === 'tooltipValueGetter') {
            // For other functions, store a flag that they exist
            config[`_has${property.charAt(0).toUpperCase() + property.slice(1)}`] = true;
          }
        } else if (value && typeof value === 'object' && property === 'headerStyle' && 
                   value._isHeaderStyleConfig) {
          // If it's already in our special format, store it as is
          config[property] = value;
        } else {
          // For non-function values, store directly
          config[property] = value;
        }
      }
    });

    console.log('[BulkActionsPanel] Template configuration captured:', {
      columnId: firstColumnId,
      propertiesFound: Object.keys(config),
      hasHeaderStyle: !!config.headerStyle,
      headerStyleType: config.headerStyle ? typeof config.headerStyle : 'none',
      hasCellStyle: !!config.cellStyle,
      hasValueFormatter: !!config.valueFormatter,
      totalProperties: Object.keys(config).length
    });

    return config;
  }, [selectedColumns, columnDefinitions, pendingChanges]);

  // Save current configuration as template
  const saveAsTemplate = useCallback(() => {
    const config = getCurrentColumnConfig();
    if (!config || !templateName.trim()) return;

    const newTemplate: ColumnTemplate = {
      id: editingTemplateId || Date.now().toString(),
      name: templateName.trim(),
      createdAt: Date.now(),
      properties: config
    };

    let newTemplates: ColumnTemplate[];
    if (editingTemplateId) {
      // Update existing template
      newTemplates = userTemplates.map(t => 
        t.id === editingTemplateId ? newTemplate : t
      );
    } else {
      // Add new template
      newTemplates = [...userTemplates, newTemplate];
    }

    saveTemplates(newTemplates);
    setShowSaveDialog(false);
    setTemplateName('');
    setEditingTemplateId('');
  }, [getCurrentColumnConfig, templateName, userTemplates, saveTemplates, editingTemplateId]);

  // Apply template to selected columns
  const applyTemplate = useCallback(() => {
    if (!selectedTemplateIds.length) return;

    console.log('[BulkActionsPanel] Applying multiple templates:', {
      templateIds: selectedTemplateIds,
      templateNames: selectedTemplateIds.map(id => templates.find(t => t.id === id)?.name).filter(Boolean),
      selectedColumnsCount: selectedColumns.size
    });

    // Merge all selected templates - later templates override earlier ones
    const mergedProperties: Partial<ColDef> = {};
    const appliedTemplateNames: string[] = [];

    selectedTemplateIds.forEach(templateId => {
      const template = templates.find(t => t.id === templateId);
      if (!template) return;

      appliedTemplateNames.push(template.name);

      // Create a copy of template properties
      const templateProps = { ...template.properties };

      // Remove field and headerName to ensure they're never applied from templates
      delete templateProps.field;
      delete templateProps.headerName;

      // Special handling for certain properties

      // If template has a valueFormatter, apply it directly
      if (templateProps.valueFormatter && typeof templateProps.valueFormatter === 'function') {
        mergedProperties.valueFormatter = templateProps.valueFormatter;
      }
      // Handle useValueFormatterForExport (boolean flag)
      if (templateProps.useValueFormatterForExport !== undefined) {
        mergedProperties.useValueFormatterForExport = templateProps.useValueFormatterForExport;
      }

      // Handle headerStyle - convert back to function if needed
      if (templateProps.headerStyle) {
        const headerStyle = templateProps.headerStyle;
        if (headerStyle._isHeaderStyleConfig) {
          // Convert to function format
          mergedProperties.headerStyle = (params: { floatingFilter?: boolean }) => {
            if (params?.floatingFilter) {
              return headerStyle.floating || null;
            }
            return headerStyle.regular || null;
          };
        } else if (typeof headerStyle === 'object') {
          // Legacy format - just a style object
          mergedProperties.headerStyle = (params: { floatingFilter?: boolean }) => {
            if (!params?.floatingFilter) {
              return headerStyle;
            }
            return null;
          };
        }
      }

      // Handle cellStyle - recreate function if needed
      if (templateProps.cellStyle) {
        const cellStyle = templateProps.cellStyle;
        if (cellStyle.type === 'function' && cellStyle.formatString) {
          // We'll handle this after merging, store the config for now
          mergedProperties._cellStyleConfig = cellStyle;
        } else if (typeof cellStyle === 'object' && !cellStyle.type) {
          // Static style object
          mergedProperties.cellStyle = cellStyle;
        }
      }

      // Merge all other properties (later templates override earlier ones)
      Object.keys(templateProps).forEach(key => {
        if (key !== 'valueFormatter' && key !== 'headerStyle' && key !== 'useValueFormatterForExport' && key !== 'cellStyle') {
          mergedProperties[key] = templateProps[key];
        }
      });
    });

    console.log('[BulkActionsPanel] Merged template properties:', {
      appliedTemplates: appliedTemplateNames,
      mergedPropertiesCount: Object.keys(mergedProperties).length,
      properties: Object.keys(mergedProperties),
      hasHeaderStyle: !!mergedProperties.headerStyle,
      headerStyleType: mergedProperties.headerStyle ? typeof mergedProperties.headerStyle : 'none',
      hasValueFormatter: !!mergedProperties.valueFormatter
    });

    // Handle cellStyle config if present
    if (mergedProperties._cellStyleConfig) {
      const cellStyleConfig = mergedProperties._cellStyleConfig;
      delete mergedProperties._cellStyleConfig; // Remove temporary config

      // Import and recreate the cellStyle function
      import('../../../utils/formatters').then(({ createCellStyleFunction }) => {
        const styleFunc = createCellStyleFunction(cellStyleConfig.formatString, cellStyleConfig.baseStyle);
        // Attach metadata for future serialization
        Object.defineProperty(styleFunc, '__formatString', { 
          value: cellStyleConfig.formatString, 
          writable: false,
          enumerable: false,
          configurable: true
        });
        Object.defineProperty(styleFunc, '__baseStyle', { 
          value: cellStyleConfig.baseStyle, 
          writable: false,
          enumerable: false,
          configurable: true
        });
        // Update cellStyle separately
        updateBulkProperties({ cellStyle: styleFunc });
      });
    }

    // Apply all merged properties at once
    updateBulkProperties(mergedProperties);

    // Track which templates were applied to each selected column
    selectedColumns.forEach(columnId => {
      // For multiple templates, we'll track the combined template names
      const combinedTemplateName = appliedTemplateNames.join(' + ');
      const combinedTemplateId = selectedTemplateIds.join('+');
      setAppliedTemplate(columnId, combinedTemplateId, combinedTemplateName);
    });

    setSelectedTemplateIds([]); // Clear selection after applying
  }, [selectedTemplateIds, templates, updateBulkProperties, selectedColumns, setAppliedTemplate]);

  // Delete template
  const deleteTemplate = useCallback(() => {
    if (!templateToDelete) return;

    // Prevent deleting system templates
    const templateToDeleteObj = templates.find(t => t.id === templateToDelete);
    if (templateToDeleteObj?.isSystem) {
      console.warn('Cannot delete system template');
      setShowDeleteDialog(false);
      setTemplateToDelete('');
      return;
    }

    const newTemplates = userTemplates.filter(t => t.id !== templateToDelete);
    saveTemplates(newTemplates);
    setShowDeleteDialog(false);
    setTemplateToDelete('');

    // Clear selection if deleted template was selected
    if (selectedTemplateIds.includes(templateToDelete)) {
      setSelectedTemplateIds(selectedTemplateIds.filter(id => id !== templateToDelete));
    }
  }, [templateToDelete, userTemplates, saveTemplates, selectedTemplateIds, templates]);

  // Clear all customizations from selected columns
  const clearSelectedCustomizations = useCallback(() => {
    // Use the same comprehensive list as templates
    const propertiesToClear = TEMPLATE_PROPERTIES.filter(prop => !prop.startsWith('_'));

    // Build object with all properties set to undefined
    const clearProperties: Record<string, undefined> = {};
    propertiesToClear.forEach(property => {
      clearProperties[property] = undefined;
    });

    // Batch clear all properties at once
    updateBulkProperties(clearProperties);
  }, [updateBulkProperties]);

  // Count pending changes
  const changeCount = useMemo(() => {
    let count = 0;
    pendingChanges.forEach((changes) => {
      count += Object.keys(changes).length;
    });
    return count;
  }, [pendingChanges]);

  const isDisabled = selectedColumns.size === 0;
  const canSaveTemplate = selectedColumns.size === 1;
  const hasTemplates = userTemplates.length > 0;

  return (
    <div className="h-full flex flex-col">
      {/* Compact header section */}
      <div className="px-4 py-2 border-b bg-card/50">
        <h3 className="text-xs font-semibold uppercase text-muted-foreground tracking-wider">
          Templates
        </h3>
        {selectedColumns.size > 1 && (
          <Alert className="mt-1">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription className="text-xs">
              Select only one column to save as a template
            </AlertDescription>
          </Alert>
        )}
      </div>

      {/* Scrollable content area - expanded */}
      <div className="flex-1 overflow-y-auto min-h-0">
        <div className="p-3">
          {/* Template Selection */}
          {!hasTemplates ? (
            <div className="px-3 py-4 text-center text-xs text-muted-foreground border rounded-md bg-muted/20">
              <p className="mb-1">No templates saved</p>
              <p className="text-xs opacity-70">Save current settings as a template</p>
            </div>
          ) : (
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <div className="text-xs text-muted-foreground">
                  {selectedTemplateIds.length} selected
                </div>
                <div className="flex gap-1">
                  <Button
                    variant="ghost"
                    size="sm"
                    className="h-6 px-2 text-xs"
                    onClick={() => setSelectedTemplateIds(templates.map(t => t.id))}
                    disabled={selectedTemplateIds.length === templates.length}
                  >
                    All
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    className="h-6 px-2 text-xs"
                    onClick={() => setSelectedTemplateIds([])}
                    disabled={selectedTemplateIds.length === 0}
                  >
                    None
                  </Button>
                </div>
              </div>

              {/* Template list - expanded height */}
              <div className="max-h-80 overflow-y-auto border rounded-lg bg-background">
                <div className="p-2 space-y-1">
                  {templates.map((template) => (
                    <div 
                      key={template.id} 
                      className={`group flex items-center gap-3 p-2 rounded-md border transition-all hover:bg-muted/50 ${
                        selectedTemplateIds.includes(template.id) 
                          ? 'bg-muted/30 border-primary/20' 
                          : 'border-transparent hover:border-border'
                      } ${
                        template.isSystem 
                          ? 'bg-gradient-to-r from-blue-50/50 to-transparent dark:from-blue-950/20 dark:to-transparent' 
                          : ''
                      }`}
                    >
                      <Checkbox
                        id={`template-${template.id}`}
                        checked={selectedTemplateIds.includes(template.id)}
                        onCheckedChange={(checked) => {
                          if (checked) {
                            setSelectedTemplateIds([...selectedTemplateIds, template.id]);
                          } else {
                            setSelectedTemplateIds(selectedTemplateIds.filter(id => id !== template.id));
                          }
                        }}
                        className="shrink-0"
                      />

                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2">
                          <label 
                            htmlFor={`template-${template.id}`} 
                            className="text-sm font-medium cursor-pointer truncate"
                          >
                            {template.name}
                          </label>
                          {template.isSystem && (
                            <Sparkles className="h-3 w-3 text-blue-500 shrink-0" title="System template" />
                          )}
                        </div>
                        {template.isSystem && (
                          <p className="text-xs text-muted-foreground mt-0.5 truncate">
                            {getSystemTemplateDescription(template.id)}
                          </p>
                        )}
                      </div>

                      {!template.isSystem && (
                        <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                          <Button
                            variant="ghost"
                            size="sm"
                            className="h-6 w-6 p-0"
                            onClick={(e) => {
                              e.stopPropagation();
                              setEditingTemplateId(template.id);
                              setTemplateName(template.name);
                              setShowSaveDialog(true);
                            }}
                            title="Edit template"
                          >
                            <Edit2 className="h-3 w-3" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            className="h-6 w-6 p-0 text-destructive hover:text-destructive"
                            onClick={(e) => {
                              e.stopPropagation();
                              setTemplateToDelete(template.id);
                              setShowDeleteDialog(true);
                            }}
                            title="Delete template"
                          >
                            <Trash2 className="h-3 w-3" />
                          </Button>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Compact footer section with horizontal layouts */}
      <div className="border-t bg-card/50 p-3 space-y-3">
        {/* Apply Templates Button - full width */}
        <Button
          variant="default"
          size="sm"
          className="w-full h-8 text-sm gap-2"
          onClick={applyTemplate}
          disabled={!selectedTemplateIds.length}
          title={selectedTemplateIds.length ? `Apply ${selectedTemplateIds.length} template${selectedTemplateIds.length !== 1 ? 's' : ''}` : "Select templates to apply"}
        >
          <Copy className="h-4 w-4" />
          Apply Templates {selectedTemplateIds.length > 0 && `(${selectedTemplateIds.length})`}
        </Button>

        {/* Action buttons - horizontal layout */}
        <div className="flex gap-2">
          <Button
            variant="outline"
            size="sm"
            className="flex-1 h-8 text-sm gap-1"
            onClick={() => {
              setEditingTemplateId('');
              setTemplateName('');
              setShowSaveDialog(true);
            }}
            disabled={!canSaveTemplate}
            title={!canSaveTemplate ? "Select only one column to save as template" : "Save current column as template"}
          >
            <Save className="h-3 w-3" />
            Save New
          </Button>
          <Button
            variant="destructive"
            size="sm"
            className="flex-1 h-8 text-sm gap-1"
            onClick={clearSelectedCustomizations}
            disabled={isDisabled}
          >
            <Eraser className="h-3 w-3" />
            Clear Selected
          </Button>
        </div>

        {/* Compact status grid */}
        <div className="grid grid-cols-2 gap-1 text-xs">
          <div className="flex items-center justify-between p-1.5 rounded bg-muted/30">
            <span className="text-muted-foreground">Selected</span>
            <span className="font-medium">{selectedColumns.size}</span>
          </div>
          <div className="flex items-center justify-between p-1.5 rounded bg-muted/30">
            <span className="text-muted-foreground">Changes</span>
            <span className="font-medium">{changeCount}</span>
          </div>
        </div>
      </div>

      {/* Save Template Dialog */}
      <Dialog open={showSaveDialog} onOpenChange={setShowSaveDialog}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle>
              {editingTemplateId ? 'Update Template' : 'Save as Template'}
            </DialogTitle>
            <DialogDescription>
              Save the current column configuration as a reusable template
            </DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="space-y-2">
              <label htmlFor="template-name" className="text-sm font-medium">
                Template Name
              </label>
              <Input
                id="template-name"
                value={templateName}
                onChange={(e) => setTemplateName(e.target.value)}
                placeholder="e.g., Currency Column"
                className="h-9"
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setShowSaveDialog(false);
                setTemplateName('');
                setEditingTemplateId('');
              }}
            >
              Cancel
            </Button>
            <Button
              onClick={saveAsTemplate}
              disabled={!templateName.trim()}
            >
              {editingTemplateId ? 'Update' : 'Save'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete Template Dialog */}
      <Dialog open={showDeleteDialog} onOpenChange={setShowDeleteDialog}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle>Delete Template</DialogTitle>
            <DialogDescription>
              Are you sure you want to delete this template? This action cannot be undone.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setShowDeleteDialog(false);
                setTemplateToDelete('');
              }}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={deleteTemplate}
            >
              Delete
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
};

import React, { useMemo, useCallback, useState } from 'react';
import { Input } from '@/components/ui/input';
import { Checkbox } from '@/components/ui/checkbox';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { Search, Columns3, Filter, Eye, EyeOff, Hash, Type, Calendar, ToggleLeft, Package, CircleDot, DollarSign, Palette, Edit3, Settings, X } from 'lucide-react';
import { ColDef } from 'ag-grid-community';
import { useColumnCustomizationStore } from '../store/column-customization.store';
import { useVirtualizer } from '@tanstack/react-virtual';
import { CustomizationBadges, CustomizationType } from '../components/CustomizationBadges';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { cn } from '@/lib/utils';

// Helper function to get icon component based on column type
const getColumnIcon = (type: string) => {
  switch (type) {
    case 'number':
    case 'numericColumn':
      return Hash;
    case 'currency':
      return DollarSign;
    case 'date':
    case 'dateColumn':
      return Calendar;
    case 'text':
    case 'textColumn':
      return Type;
    case 'boolean':
    case 'booleanColumn':
      return ToggleLeft;
    case 'object':
      return Package;
    default:
      return CircleDot;
  }
};

export const ColumnSelectorPanel: React.FC = React.memo(() => {
  const {
    selectedColumns,
    columnDefinitions,
    columnState,
    searchTerm,
    cellDataTypeFilter,
    visibilityFilter,
    templateColumns,
    appliedTemplates,
    toggleColumnSelection,
    selectColumns,
    deselectColumns,
    setSearchTerm,
    setCellDataTypeFilter,
    setVisibilityFilter,
    toggleTemplateColumn
  } = useColumnCustomizationStore();

  const parentRef = React.useRef<HTMLDivElement>(null);

  // Get all columns as array
  const allColumns = useMemo(() => {
    return Array.from(columnDefinitions.values());
  }, [columnDefinitions]);

  // Get available cellDataType options
  const availableCellDataTypes = useMemo(() => {
    const types = new Set<string>();
    allColumns.forEach(col => {
      if (col.cellDataType) {
        types.add(col.cellDataType);
      }
    });
    return Array.from(types).sort();
  }, [allColumns]);

  // Filter columns based on search, cellDataType, and visibility
  const filteredColumns = useMemo(() => {
    let filtered = allColumns;

    // Filter by search term
    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      filtered = filtered.filter(col =>
        col.field?.toLowerCase().includes(term) ||
        col.headerName?.toLowerCase().includes(term)
      );
    }

    // Filter by cellDataType
    if (cellDataTypeFilter && cellDataTypeFilter !== 'all') {
      filtered = filtered.filter(col => col.cellDataType === cellDataTypeFilter);
    }

    // Filter by visibility using column state
    if (visibilityFilter !== 'all') {
      filtered = filtered.filter(col => {
        // Try multiple ways to match column with state
        const field = col.field || '';
        const colId = col.colId || field;

        // Try to find column state by field first, then by colId
        let colState = columnState.get(field);
        if (!colState && field !== colId) {
          colState = columnState.get(colId);
        }

        // Check if column state exists at all - if not, the grid API might not be returning state for all columns
        // AG-Grid only includes columns in getColumnState if they have been modified from defaults
        // So if a column has no state, it means it's using default settings (visible)
        const isHidden = colState?.hide === true; // Only hidden if explicitly set to true

        return visibilityFilter === 'hidden' ? isHidden : !isHidden;
      });
    }

    return filtered;
  }, [allColumns, searchTerm, cellDataTypeFilter, visibilityFilter, columnState]);

  // Prepare items for virtual scrolling (simple flat list)
  const flatItems = useMemo(() => {
    return filteredColumns.map(column => ({ 
      type: 'column' as const, 
      column,
      id: column.field || column.colId || ''
    }));
  }, [filteredColumns]);

  // Virtual scrolling setup
  const virtualizer = useVirtualizer({
    count: flatItems.length,
    getScrollElement: () => parentRef.current,
    estimateSize: () => 40, // Estimated height per item
    overscan: 5
  });

  const selectAllFilteredColumns = useCallback(() => {
    const columnIds = filteredColumns
      .map(col => col.field || col.colId || '')
      .filter(id => id && !selectedColumns.has(id));
    if (columnIds.length > 0) {
      selectColumns(columnIds);
    }
  }, [filteredColumns, selectedColumns, selectColumns]);

  const deselectAllFilteredColumns = useCallback(() => {
    const columnIds = filteredColumns
      .map(col => col.field || col.colId || '')
      .filter(id => id && selectedColumns.has(id));
    if (columnIds.length > 0) {
      deselectColumns(columnIds);
    }
  }, [filteredColumns, selectedColumns, deselectColumns]);

  const isAllSelected = useMemo(() => 
    filteredColumns.length > 0 &&
    filteredColumns.every(col => selectedColumns.has(col.field || col.colId || '')),
    [filteredColumns, selectedColumns]
  );
  const isIndeterminate = useMemo(() => 
    filteredColumns.some(col => selectedColumns.has(col.field || col.colId || '')) &&
    !isAllSelected,
    [filteredColumns, selectedColumns, isAllSelected]
  );

  // Count of filtered columns that are selected
  const filteredSelectedCount = useMemo(() => 
    filteredColumns.filter(col =>
      selectedColumns.has(col.field || col.colId || '')
    ).length,
    [filteredColumns, selectedColumns]
  );

  return (
    <TooltipProvider>
      <div className="h-full flex flex-col">
        {/* Modern Header */}
        <div className="px-4 py-3 border-b border-border/40 bg-gradient-to-r from-muted/15 to-muted/5 backdrop-blur-sm">
          <div className="flex items-center gap-3">
            <div className="p-1.5 rounded-md bg-gradient-to-br from-primary/20 to-primary/10 border border-primary/20">
              <Columns3 className="h-4 w-4 text-primary" />
            </div>
            <span className="text-sm font-semibold tracking-tight">Columns</span>
            <Badge variant="outline" className="text-xs px-2 py-1 ml-auto font-medium rounded-md border-border/60">
              {filteredColumns.length}
            </Badge>
          </div>
        </div>

        <div className="flex-1 flex flex-col p-4">
          {/* Modern Search Bar */}
          <div className="relative mb-3">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
            <Input
              placeholder="Search columns..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-9 h-9 text-sm rounded-lg border-border/60 bg-background/80 backdrop-blur-sm focus:border-primary/60 focus:ring-primary/20 transition-all duration-200"
            />
          </div>

          {/* Filters */}
          <div className="space-y-3 mb-4">
            {/* CellDataType Filter */}
            <Select
              value={cellDataTypeFilter}
              onValueChange={setCellDataTypeFilter}
            >
              <SelectTrigger className="h-9 text-sm rounded-lg border-border/60 bg-background/80 backdrop-blur-sm">
                <Filter className="h-4 w-4 mr-2" />
                <SelectValue placeholder="Filter by data type" />
              </SelectTrigger>
              <SelectContent className="rounded-lg border-border/60 bg-background/95 backdrop-blur-md">
                <SelectItem value="all" className="text-sm">All Data Types</SelectItem>
                {availableCellDataTypes.map(type => {
                  const TypeIcon = getColumnIcon(type);
                  return (
                    <SelectItem key={type} value={type} className="text-sm">
                      <div className="flex items-center gap-2">
                        <TypeIcon className="h-4 w-4 text-muted-foreground" />
                        <span className="capitalize">{type}</span>
                      </div>
                    </SelectItem>
                  );
                })}
              </SelectContent>
            </Select>

            {/* Visibility Filter */}
            <Select
              value={visibilityFilter}
              onValueChange={(value: 'all' | 'visible' | 'hidden') => setVisibilityFilter(value)}
            >
              <SelectTrigger className="h-9 text-sm rounded-lg border-border/60 bg-background/80 backdrop-blur-sm">
                {visibilityFilter === 'visible' ? (
                  <Eye className="h-4 w-4 mr-2" />
                ) : visibilityFilter === 'hidden' ? (
                  <EyeOff className="h-4 w-4 mr-2" />
                ) : (
                  <Eye className="h-4 w-4 mr-2" />
                )}
                <SelectValue placeholder="Filter by visibility" />
              </SelectTrigger>
              <SelectContent className="rounded-lg border-border/60 bg-background/95 backdrop-blur-md">
                <SelectItem value="all" className="text-sm">
                  <div className="flex items-center gap-2">
                    <Eye className="h-4 w-4" />
                    <span>All Columns</span>
                  </div>
                </SelectItem>
                <SelectItem value="visible" className="text-sm">
                  <div className="flex items-center gap-2">
                    <Eye className="h-4 w-4 text-green-600" />
                    <span>Visible Columns</span>
                  </div>
                </SelectItem>
                <SelectItem value="hidden" className="text-sm">
                  <div className="flex items-center gap-2">
                    <EyeOff className="h-4 w-4 text-red-600" />
                    <span>Hidden Columns</span>
                  </div>
                </SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Modern Selection Controls */}
          <div className="flex items-center gap-2.5 mb-4">
            <Checkbox
              checked={isAllSelected ? true : isIndeterminate ? "indeterminate" : false}
              onCheckedChange={(checked) => {
                if (checked === true || checked === "indeterminate") {
                  selectAllFilteredColumns();
                } else {
                  deselectAllFilteredColumns();
                }
              }}
              className="rounded"
            />
            <span className="text-xs font-medium text-foreground">
              {(searchTerm || cellDataTypeFilter !== 'all' || visibilityFilter !== 'all') ? 'All Filtered' : 'All'}
            </span>
            {filteredSelectedCount > 0 && (
              <Badge variant="secondary" className="text-xs px-2 py-0.5 font-medium rounded-md bg-secondary/80 border border-secondary/40">
                {filteredSelectedCount}/{filteredColumns.length}
              </Badge>
            )}
          </div>

          {/* Virtual Column List */}
          <div className="flex-1 -mx-1">
            <div
              ref={parentRef}
              className="h-full overflow-auto px-1 scrollbar-thin"
              style={{ contain: 'strict' }}
            >
              <div
                style={{
                  height: `${virtualizer.getTotalSize()}px`,
                  width: '100%',
                  position: 'relative',
                }}
              >
                {virtualizer.getVirtualItems().map((virtualItem) => {
                  const item = flatItems[virtualItem.index];

                  return (
                    <div
                      key={virtualItem.key}
                      style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: `${virtualItem.size}px`,
                        transform: `translateY(${virtualItem.start}px)`,
                      }}
                    >
                      <ColumnItem
                        column={item.column}
                        columnId={item.id}
                        selected={selectedColumns.has(item.id)}
                        isTemplate={templateColumns instanceof Set ? templateColumns.has(item.id) : false}
                        isHidden={(() => {
                          // Try to find column state by field first, then by colId
                          const field = item.column.field || '';
                          const colId = item.column.colId || field;
                          let colState = columnState.get(field);
                          if (!colState && field !== colId) {
                            colState = columnState.get(colId);
                          }
                          return colState?.hide || false;
                        })()}
                        appliedTemplate={appliedTemplates.get(item.id)}
                        onToggle={toggleColumnSelection}
                        onToggleTemplate={toggleTemplateColumn}
                      />
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      </div>
    </TooltipProvider>
  );
});

ColumnSelectorPanel.displayName = 'ColumnSelectorPanel';

// Clean Column Item Component
const ColumnItem: React.FC<{
  column: ColDef;
  columnId: string;
  selected: boolean;
  isTemplate: boolean;
  isHidden: boolean;
  appliedTemplate?: { templateId: string; templateName: string; appliedAt: number };
  onToggle: (columnId: string) => void;
  onToggleTemplate: (columnId: string) => void;
}> = React.memo(({ column, columnId, selected, isTemplate: _isTemplate, isHidden, appliedTemplate, onToggle, onToggleTemplate: _onToggleTemplate }) => {
  const { removeColumnCustomization, removeAppliedTemplate, pendingChanges } = useColumnCustomizationStore();
  const [isHoveringTemplate, setIsHoveringTemplate] = useState(false);
  const iconKey = (column.cellDataType || column.type || 'text') as string;
  const IconComponent = getColumnIcon(iconKey);

  // Detect customizations including template info
  const customizations = useMemo(() => {
    const customs: CustomizationType[] = [];

    // Get pending changes for this column
    const pending = pendingChanges.get(columnId) || {};

    // Merge original column with pending changes to get effective column state
    const effectiveColumn = { ...column, ...pending };

    // Helper function to check if a value exists and is not undefined
    const hasValue = (value: any) => value !== undefined && value !== null;

    // Check for styling customizations
    const hasStyle = hasValue(effectiveColumn.cellStyle) || hasValue(effectiveColumn.headerStyle) || 
                     hasValue(effectiveColumn.cellClass) || hasValue(effectiveColumn.headerClass);
    if (hasStyle) {
      let styleCount = 0;
      if (hasValue(effectiveColumn.cellStyle)) styleCount++;
      if (hasValue(effectiveColumn.headerStyle)) styleCount++;
      if (hasValue(effectiveColumn.cellClass)) styleCount++;
      if (hasValue(effectiveColumn.headerClass)) styleCount++;
      customs.push({ 
        type: 'style', 
        label: 'Styling customizations', 
        icon: Palette,
        count: styleCount
      });
    }

    // Check for formatter
    if (hasValue(effectiveColumn.valueFormatter)) {
      customs.push({ 
        type: 'formatter', 
        label: 'Value formatter', 
        icon: Hash 
      });
    }

    // Check for filter
    if (hasValue(effectiveColumn.filter) || hasValue(effectiveColumn.filterParams)) {
      customs.push({ 
        type: 'filter', 
        label: 'Filter settings', 
        icon: Filter 
      });
    }

    // Check for editor
    if (hasValue(effectiveColumn.cellEditor) || hasValue(effectiveColumn.cellEditorParams)) {
      customs.push({ 
        type: 'editor', 
        label: 'Cell editor', 
        icon: Edit3 
      });
    }

    // Check for general settings (width, pinning, etc)
    const hasGeneral = hasValue(effectiveColumn.width) || hasValue(effectiveColumn.minWidth) || hasValue(effectiveColumn.maxWidth) || 
                      hasValue(effectiveColumn.pinned) || hasValue(effectiveColumn.lockPosition) || hasValue(effectiveColumn.lockVisible);
    if (hasGeneral) {
      let generalCount = 0;
      if (hasValue(effectiveColumn.width) || hasValue(effectiveColumn.minWidth) || hasValue(effectiveColumn.maxWidth)) generalCount++;
      if (hasValue(effectiveColumn.pinned)) generalCount++;
      if (hasValue(effectiveColumn.lockPosition) || hasValue(effectiveColumn.lockVisible)) generalCount++;
      customs.push({ 
        type: 'general', 
        label: 'General settings', 
        icon: Settings,
        count: generalCount
      });
    }

    return customs;
  }, [column, columnId, pendingChanges]);

  const handleToggle = useCallback(() => {
    onToggle(columnId);
  }, [columnId, onToggle]);

  const _handleToggleTemplate = useCallback((e: React.MouseEvent) => {
    e.stopPropagation();
    _onToggleTemplate(columnId);
  }, [columnId, _onToggleTemplate]);

  const handleRemoveCustomization = useCallback((type: string) => {
    removeColumnCustomization(columnId, type);
  }, [columnId, removeColumnCustomization]);

  return (
    <div 
      className={`relative flex items-center gap-2 px-3 py-1.5 rounded-md hover:bg-muted/50 transition-colors group cursor-pointer ${
        selected ? 'bg-muted/30' : ''
      }`}
      onClick={handleToggle}
    >
      {selected && (
        <div className="absolute left-0 h-full w-1 bg-primary rounded-r" />
      )}
      <Checkbox
        checked={selected}
        onCheckedChange={handleToggle}
        onClick={(e) => e.stopPropagation()}
        className="shrink-0"
      />
      <IconComponent className="h-4 w-4 shrink-0 text-muted-foreground" />
      <span className="text-sm flex-1 flex items-center gap-1 min-w-0">
        <Tooltip>
          <TooltipTrigger asChild>
            <span className={`truncate ${isHidden ? 'opacity-50' : ''}`}>
              {column.headerName || column.field}
            </span>
          </TooltipTrigger>
          <TooltipContent>
            {column.headerName || column.field}
          </TooltipContent>
        </Tooltip>
        {isHidden && (
          <EyeOff className="h-3 w-3 text-muted-foreground shrink-0" />
        )}
      </span>

      {/* Customization Count with Popover - moved to the right */}
      {(customizations.length > 0 || appliedTemplate) && (
        <Popover>
          <PopoverTrigger asChild>
            <div 
              className="h-5 w-5 shrink-0 rounded-full bg-primary/10 flex items-center justify-center cursor-pointer hover:bg-primary/20 transition-colors ml-auto"
              title={`${customizations.length + (appliedTemplate ? 1 : 0)} customization${customizations.length + (appliedTemplate ? 1 : 0) !== 1 ? 's' : ''} applied`}
            >
              <span className="text-[10px] font-medium text-primary">{customizations.length + (appliedTemplate ? 1 : 0)}</span>
            </div>
          </PopoverTrigger>
          <PopoverContent side="left" align="center" className="w-auto p-3">
            <div className="space-y-2">
              {appliedTemplate && (
                <div className="mb-2">
                  <p className="text-xs font-medium text-muted-foreground mb-1">Template:</p>
                  <div 
                    className="relative inline-block"
                    onMouseEnter={() => setIsHoveringTemplate(true)}
                    onMouseLeave={() => setIsHoveringTemplate(false)}
                  >
                    <Badge 
                      variant="outline" 
                      className={cn(
                        "text-xs px-2 py-0.5 transition-all",
                        isHoveringTemplate && "pr-7"
                      )}
                    >
                      {appliedTemplate.templateName}
                    </Badge>
                    {isHoveringTemplate && (
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          console.log('[ColumnSelector] Removing template:', appliedTemplate.templateName, 'from column:', columnId);
                          removeAppliedTemplate(columnId);
                          setIsHoveringTemplate(false);
                        }}
                        className="absolute right-1 top-1/2 -translate-y-1/2 h-4 w-4 flex items-center justify-center rounded-sm hover:bg-black/10 dark:hover:bg-white/10 transition-colors"
                        aria-label="Remove template"
                      >
                        <X className="h-3 w-3" />
                      </button>
                    )}
                  </div>
                </div>
              )}
              {customizations.length > 0 && (
                <>
                  <p className="text-xs font-medium mb-2">Customizations:</p>
                  <CustomizationBadges
                    customizations={customizations}
                    onRemove={handleRemoveCustomization}
                    className="flex-wrap"
                    maxVisible={10} // Show all in popover
                  />
                </>
              )}
            </div>
          </PopoverContent>
        </Popover>
      )}
    </div>
  );
}, (prevProps, nextProps) => {
  // Custom comparison to prevent unnecessary re-renders
  return (
    prevProps.selected === nextProps.selected &&
    prevProps.isTemplate === nextProps.isTemplate &&
    prevProps.isHidden === nextProps.isHidden &&
    prevProps.columnId === nextProps.columnId &&
    prevProps.column === nextProps.column && // Compare entire column object for customization changes
    prevProps.appliedTemplate?.templateId === nextProps.appliedTemplate?.templateId // Check template changes
  );
});

ColumnItem.displayName = 'ColumnItem';

import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import { subscribeWithSelector } from 'zustand/middleware';
import { ColDef as AgColDef, ColumnState } from 'ag-grid-community';
import { createCellStyleFunction, hasConditionalStyling } from '@/components/datatable/utils/style-utils';

// Use AG-Grid's ColDef directly
export type ColDef = AgColDef;

export interface DialogState {
  // Dialog state
  open: boolean;

  // Column management
  selectedColumns: Set<string>;
  columnDefinitions: Map<string, ColDef>;
  columnState: Map<string, ColumnState>; // AG-Grid column state by colId
  pendingChanges: Map<string, Partial<ColDef>>;

  // UI state
  activeTab: string;
  showOnlyCommon: boolean;
  compareMode: boolean;
  searchTerm: string;
  cellDataTypeFilter: string;
  visibilityFilter: 'all' | 'visible' | 'hidden';
  uiMode: 'simple' | 'advanced';
  showPreviewPane: boolean;
  collapsedSections: Set<string>;
  quickFormatPinned: string[];

  // Panel states
  bulkActionsPanelCollapsed: boolean;
  showColumnDrawer: boolean;

  // Template columns for quick copy
  templateColumns: Set<string>;

  // Applied templates tracking
  appliedTemplates: Map<string, { templateId: string; templateName: string; appliedAt: number }>;
}

export interface DialogActions {
  // Dialog actions
  setOpen: (open: boolean) => void;

  // Column management
  setSelectedColumns: (columns: Set<string>) => void;
  toggleColumnSelection: (columnId: string) => void;
  selectColumns: (columnIds: string[]) => void;
  deselectColumns: (columnIds: string[]) => void;
  setColumnDefinitions: (columns: Map<string, ColDef>) => void;
  setColumnState: (columnState: ColumnState[]) => void;
  updateBulkProperty: (property: string, value: unknown) => void;
  updateBulkProperties: (properties: Record<string, unknown>) => void;
  applyChanges: () => ColDef[];
  resetChanges: () => void;

  // UI actions
  setActiveTab: (tab: string) => void;
  setShowOnlyCommon: (show: boolean) => void;
  setCompareMode: (compare: boolean) => void;
  setSearchTerm: (term: string) => void;
  setCellDataTypeFilter: (filter: string) => void;
  setVisibilityFilter: (filter: 'all' | 'visible' | 'hidden') => void;
  setUiMode: (mode: 'simple' | 'advanced') => void;
  setShowPreviewPane: (show: boolean) => void;
  toggleSectionCollapse: (section: string) => void;
  setQuickFormatPinned: (formats: string[]) => void;
  toggleQuickFormat: (format: string) => void;

  // Panel actions
  setBulkActionsPanelCollapsed: (collapsed: boolean) => void;
  setShowColumnDrawer: (show: boolean) => void;

  // Template column actions
  toggleTemplateColumn: (columnId: string) => void;
  clearTemplateColumns: () => void;

  // Customization removal
  removeColumnCustomization: (columnId: string, type: string) => void;
  clearAllCustomizations: () => void;

  // Template tracking
  setAppliedTemplate: (columnId: string, templateId: string, templateName: string) => void;
  removeAppliedTemplate: (columnId: string) => void;
}

export type ColumnCustomizationStore = DialogState & DialogActions;


/**
 * Ensure a column has a cellStyle function if its valueFormatter has conditional styling
 * Returns true if the column was modified
 */
function ensureCellStyleForValueFormatter(column: ColDef): boolean {
  // Check if valueFormatter has conditional styling
  if (column.valueFormatter && typeof column.valueFormatter === 'function') {
    const formatString = (column.valueFormatter as any).__formatString;

    if (formatString && hasConditionalStyling(formatString)) {
      // Check if cellStyle already exists and is properly configured
      if (!column.cellStyle || 
          (typeof column.cellStyle === 'function' && 
           (column.cellStyle as any).__formatString !== formatString)) {

        console.log('[Store] Creating cellStyle for conditional formatting:', {
          field: column.field,
          formatString,
          hasExistingCellStyle: !!column.cellStyle,
          existingCellStyleType: typeof column.cellStyle
        });

        // Extract base style if it exists
        let baseStyle: React.CSSProperties = {};
        if (column.cellStyle) {
          if (typeof column.cellStyle === 'object') {
            baseStyle = column.cellStyle;
          } else if (typeof column.cellStyle === 'function' && (column.cellStyle as any).__baseStyle) {
            baseStyle = (column.cellStyle as any).__baseStyle;
          }
        }

        // Create cellStyle function
        const styleFunc = createCellStyleFunction(formatString, baseStyle);

        // Attach metadata for future serialization
        Object.defineProperty(styleFunc, '__formatString', { 
          value: formatString, 
          writable: false,
          enumerable: false,
          configurable: true
        });
        Object.defineProperty(styleFunc, '__baseStyle', { 
          value: baseStyle, 
          writable: false,
          enumerable: false,
          configurable: true
        });

        column.cellStyle = styleFunc;
        return true; // Column was modified
      }
    }
  }
  return false; // Column was not modified
}

// Performance optimization: memoized selectors
export const useSelectedColumns = () => useColumnCustomizationStore(state => state.selectedColumns);
export const useColumnDefinitions = () => useColumnCustomizationStore(state => state.columnDefinitions);
export const usePendingChanges = () => useColumnCustomizationStore(state => state.pendingChanges);

export const useColumnCustomizationStore = create<ColumnCustomizationStore>()(
  subscribeWithSelector(
    persist(
      (set, get) => ({
      // Initial state
      open: false,
      selectedColumns: new Set<string>(),
      columnDefinitions: new Map<string, ColDef>(),
      columnState: new Map<string, ColumnState>(),
      pendingChanges: new Map<string, Partial<ColDef>>(),
      activeTab: 'general',
      showOnlyCommon: false,
      compareMode: false,
      searchTerm: '',
      cellDataTypeFilter: 'all',
      visibilityFilter: 'all',
      bulkActionsPanelCollapsed: false,
      templateColumns: new Set<string>(),
      appliedTemplates: new Map<string, { templateId: string; templateName: string; appliedAt: number }>(),
      uiMode: 'simple',
      showPreviewPane: false,
      collapsedSections: new Set<string>(),
      quickFormatPinned: ['number', 'currency', 'percentage', 'date', 'text'],
      showColumnDrawer: false,

      // Actions
      setOpen: (open) => set({ open }),

      setSelectedColumns: (columns) => set({ selectedColumns: columns }),

      toggleColumnSelection: (columnId: string) => {
        const { selectedColumns } = get();
        const newSelected = new Set(selectedColumns);
        if (newSelected.has(columnId)) {
          newSelected.delete(columnId);
        } else {
          newSelected.add(columnId);
        }
        set({ selectedColumns: newSelected });
      },

      selectColumns: (columnIds: string[]) => {
        const { selectedColumns } = get();
        const newSelected = new Set(selectedColumns);
        columnIds.forEach(id => newSelected.add(id));
        set({ selectedColumns: newSelected });
      },

      deselectColumns: (columnIds: string[]) => {
        const { selectedColumns } = get();
        const newSelected = new Set(selectedColumns);
        columnIds.forEach(id => newSelected.delete(id));
        set({ selectedColumns: newSelected });
      },

      setColumnDefinitions: (columns) => set({ columnDefinitions: columns }),

      setColumnState: (columnStateArray) => {
        const stateMap = new Map();
        if (columnStateArray) {
          // console.log('[Store] Setting column state, array length:', columnStateArray.length);
          let _visibleCount = 0;
          let _hiddenCount = 0;

          columnStateArray.forEach(colState => {
            stateMap.set(colState.colId, colState);
            if (colState.hide) {
              _hiddenCount++;
            } else {
              _visibleCount++;
            }
          });

          // console.log('[Store] Column state summary:', {
          //   total: columnStateArray.length,
          //   visible: visibleCount,
          //   hidden: hiddenCount,
          //   stateMapSize: stateMap.size
          // });
        }
        set({ columnState: stateMap });
      },

      updateBulkProperty: (property, value) => {
        const { selectedColumns, pendingChanges } = get();
        if (selectedColumns.size === 0) return;

        // Prevent editing field property
        if (property === 'field') {
          console.warn('[Store] Field property cannot be edited');
          return;
        }

        // Prevent bulk editing of headerName
        if (property === 'headerName' && selectedColumns.size > 1) {
          console.warn('[Store] Header Name cannot be edited for multiple columns');
          return;
        }

        // Reuse existing Map for better performance
        const newPendingChanges = new Map(pendingChanges);

        // Process all updates in a single pass
        if (value === undefined) {
          // Delete property from selected columns
          for (const colId of selectedColumns) {
            const existing = newPendingChanges.get(colId);
            if (existing && property in existing) {
              const updated = { ...existing };
              delete updated[property as keyof ColDef];
              if (Object.keys(updated).length === 0) {
                newPendingChanges.delete(colId);
              } else {
                newPendingChanges.set(colId, updated);
              }
            }
          }
        } else {
          // Add/update property for selected columns
          for (const colId of selectedColumns) {
            const existing = newPendingChanges.get(colId);
            newPendingChanges.set(colId, existing ? { ...existing, [property]: value } : { [property]: value });
          }
        }

        set({ pendingChanges: newPendingChanges });
      },

      updateBulkProperties: (properties) => {
        const { selectedColumns, pendingChanges } = get();

        // Filter out field and headerName for safety
        const filteredProperties = { ...properties };

        // Always remove field from bulk updates
        delete filteredProperties.field;

        // Remove headerName if multiple columns are selected
        if (selectedColumns.size > 1) {
          delete filteredProperties.headerName;
        }

        const newPendingChanges = new Map(pendingChanges);

        selectedColumns.forEach(colId => {
          const existing = newPendingChanges.get(colId) || {};
          const updated = { ...existing };

          Object.entries(filteredProperties).forEach(([property, value]) => {
            if (value === undefined) {
              delete updated[property as keyof ColDef];
            } else {
              // For headerStyle, just save the style object directly
              // We'll convert it to a function when loading from storage
              (updated as Record<string, unknown>)[property] = value;
            }
          });

          if (Object.keys(updated).length === 0) {
            newPendingChanges.delete(colId);
          } else {
            newPendingChanges.set(colId, updated);
          }
        });

        set({ pendingChanges: newPendingChanges });

        // Never apply immediately - changes are only saved when Apply buttons are clicked
      },

      applyChanges: () => {
        const startTime = performance.now();
        const { columnDefinitions, pendingChanges } = get();

        // Early return if no changes
        if (pendingChanges.size === 0) {
          console.log('[ColumnCustomizationStore] No pending changes, returning original columns');
          return Array.from(columnDefinitions.values());
        }

        console.log('[ColumnCustomizationStore] applyChanges called:', {
          columnDefinitionsCount: columnDefinitions.size,
          pendingChangesCount: pendingChanges.size
        });

        // List of properties that should be explicitly cleared (set to undefined)
        const clearableProperties = [
          'cellStyle', 'headerStyle', 'cellClass', 'headerClass', 'cellClassRules',
          'valueFormatter', 'valueGetter', 'valueSetter', 'useValueFormatterForExport',
          'filter', 'filterParams', 'floatingFilter', 'floatingFilterComponent', 
          'floatingFilterComponentParams', 'suppressHeaderMenuButton', 
          'suppressFiltersToolPanel', 'filterValueGetter',
          'editable', 'cellEditor', 'cellEditorParams', 'cellEditorPopup',
          'cellEditorPopupPosition', 'singleClickEdit', 'stopEditingWhenCellsLoseFocus',
          'cellEditorSelector', 'cellRenderer', 'cellRendererParams', 'cellRendererSelector',
          'wrapText', 'autoHeight', 'rowSpan', 'colSpan', 'textAlign', 'verticalAlign',
          'headerTooltip', 'headerComponent', 'headerComponentParams', 'headerTextAlign',
          'headerCheckboxSelection', 'headerCheckboxSelectionFilteredOnly',
          'wrapHeaderText', 'autoHeaderHeight', 'sortable', 'sort', 'sortingOrder',
          'comparator', 'unSortIcon', 'aggFunc', 'allowedAggFuncs',
          'pinned', 'lockPosition', 'lockPinned', 'lockVisible',
          'width', 'minWidth', 'maxWidth', 'flex', 'resizable', 'suppressSizeToFit',
          'initialWidth', 'initialHide', 'initialPinned',
          'tooltip', 'tooltipField', 'tooltipValueGetter', 'tooltipComponent',
          'tooltipComponentParams', 'suppressKeyboardEvent', 'suppressNavigable',
          'suppressPaste', 'checkboxSelection', 'showDisabledCheckboxes'
        ];

        // Pre-allocate array with exact size for better performance
        const updatedColumns = new Array(columnDefinitions.size);
        let index = 0;

        // Process columns in a single pass with minimal object creation
        for (const [colId, colDef] of columnDefinitions) {
          const changes = pendingChanges.get(colId);
          if (changes && Object.keys(changes).length > 0) {
            // Start with the original column to preserve all properties
            const mergedColumn = { ...colDef };

            // Apply changes
            Object.entries(changes).forEach(([key, value]) => {
              if (value === undefined && clearableProperties.includes(key)) {
                // Explicitly set clearable properties to undefined
                (mergedColumn as any)[key] = undefined;
              } else if (value !== undefined) {
                // Set new values
                (mergedColumn as any)[key] = value;
              }
            });

            // Check if we need to create cellStyle for conditional formatting
            ensureCellStyleForValueFormatter(mergedColumn);

            updatedColumns[index] = mergedColumn;
          } else {
            // Even for unchanged columns, ensure cellStyle exists if needed
            const columnCopy = { ...colDef };
            if (ensureCellStyleForValueFormatter(columnCopy)) {
              updatedColumns[index] = columnCopy;
            } else {
              // Reuse existing object reference for unchanged columns
              updatedColumns[index] = colDef;
            }
          }
          index++;
        }

        // Clear pending changes immediately
        set({ pendingChanges: new Map() });

        const endTime = performance.now();
        console.log('[ColumnCustomizationStore] applyChanges completed:', {
          totalColumns: updatedColumns.length,
          columnsWithChanges: pendingChanges.size,
          executionTime: `${(endTime - startTime).toFixed(2)}ms`
        });

        return updatedColumns;
      },

      resetChanges: () => set({ pendingChanges: new Map() }),

      setActiveTab: (tab) => set({ activeTab: tab }),
      setShowOnlyCommon: (show) => set({ showOnlyCommon: show }),
      setCompareMode: (compare) => set({ compareMode: compare }),
      setSearchTerm: (term) => set({ searchTerm: term }),
      setCellDataTypeFilter: (filter) => set({ cellDataTypeFilter: filter }),
      setVisibilityFilter: (filter) => set({ visibilityFilter: filter }),
      setBulkActionsPanelCollapsed: (collapsed) => set({ bulkActionsPanelCollapsed: collapsed }),
      setUiMode: (mode) => set({ uiMode: mode }),
      setShowPreviewPane: (show) => set({ showPreviewPane: show }),
      toggleSectionCollapse: (section) => {
        const { collapsedSections } = get();
        const newCollapsed = new Set(collapsedSections);
        if (newCollapsed.has(section)) {
          newCollapsed.delete(section);
        } else {
          newCollapsed.add(section);
        }
        set({ collapsedSections: newCollapsed });
      },
      setQuickFormatPinned: (formats) => set({ quickFormatPinned: formats }),
      toggleQuickFormat: (format) => {
        const { quickFormatPinned } = get();
        const newPinned = quickFormatPinned.includes(format)
          ? quickFormatPinned.filter(f => f !== format)
          : [...quickFormatPinned, format];
        set({ quickFormatPinned: newPinned });
      },
      setShowColumnDrawer: (show) => set({ showColumnDrawer: show }),

      // Template column actions
      toggleTemplateColumn: (columnId) => {
        const { templateColumns } = get();
        const newTemplates = new Set(templateColumns);
        if (newTemplates.has(columnId)) {
          newTemplates.delete(columnId);
        } else {
          newTemplates.add(columnId);
        }
        set({ templateColumns: newTemplates });
      },

      clearTemplateColumns: () => set({ templateColumns: new Set() }),

      removeColumnCustomization: (columnId, type) => {
        const { columnDefinitions, pendingChanges } = get();
        const column = columnDefinitions.get(columnId);
        if (!column) return;

        const newPendingChanges = new Map(pendingChanges);
        const existing = newPendingChanges.get(columnId) || {};
        const updated = { ...existing };

        // Also update the column definitions immediately for UI feedback
        const newColumnDefinitions = new Map(columnDefinitions);
        const updatedColumn = { ...column };

        // Remove customizations based on type by setting them to undefined
        // This will override any existing values in the original column definition
        switch (type) {
          case 'style':
            updated.cellStyle = undefined;
            updated.headerStyle = undefined;
            updated.cellClass = undefined;
            updated.headerClass = undefined;
            // Also remove from column definition for immediate UI update
            delete updatedColumn.cellStyle;
            delete updatedColumn.headerStyle;
            delete updatedColumn.cellClass;
            delete updatedColumn.headerClass;
            break;
          case 'formatter':
            updated.valueFormatter = undefined;
            delete updatedColumn.valueFormatter;
            break;
          case 'filter':
            updated.filter = undefined;
            updated.filterParams = undefined;
            delete updatedColumn.filter;
            delete updatedColumn.filterParams;
            break;
          case 'editor':
            updated.cellEditor = undefined;
            updated.cellEditorParams = undefined;
            delete updatedColumn.cellEditor;
            delete updatedColumn.cellEditorParams;
            break;
          case 'general':
            updated.width = undefined;
            updated.minWidth = undefined;
            updated.maxWidth = undefined;
            updated.pinned = undefined;
            updated.lockPosition = undefined;
            updated.lockVisible = undefined;
            delete updatedColumn.width;
            delete updatedColumn.minWidth;
            delete updatedColumn.maxWidth;
            delete updatedColumn.pinned;
            delete updatedColumn.lockPosition;
            delete updatedColumn.lockVisible;
            break;
        }

        // Update both column definitions and pending changes
        newColumnDefinitions.set(columnId, updatedColumn);
        newPendingChanges.set(columnId, updated);

        set({ 
          columnDefinitions: newColumnDefinitions,
          pendingChanges: newPendingChanges 
        });

        console.log('[Store] Removed customization:', { columnId, type, updatedChanges: updated });
      },

      clearAllCustomizations: () => {
        const { columnDefinitions } = get();

        console.log('[Store] Clearing all customizations from all columns');

        // Create new pending changes for all columns
        const newPendingChanges = new Map<string, Partial<ColDef>>();

        let clearedCount = 0;

        // Comprehensive list of all customization properties to clear
        const customizationProperties = [
          // Data type and basic properties
          'cellDataType', 'type', 'valueGetter', 'valueSetter',

          // Filter configurations
          'filter', 'filterParams', 'floatingFilter', 'floatingFilterComponent', 'floatingFilterComponentParams',
          'suppressHeaderMenuButton', 'suppressFiltersToolPanel', 'filterValueGetter',

          // Editor configurations
          'editable', 'cellEditor', 'cellEditorParams', 'cellEditorPopup', 'cellEditorPopupPosition',
          'singleClickEdit', 'stopEditingWhenCellsLoseFocus', 'cellEditorSelector',

          // Format configurations
          'valueFormatter', 'useValueFormatterForExport',
          'cellClass', 'cellClassRules', 'cellStyle',

          // Header configurations
          'headerClass', 'headerStyle', 'headerTooltip', 'headerComponent', 'headerComponentParams',
          'headerTextAlign', 'headerCheckboxSelection', 'headerCheckboxSelectionFilteredOnly',
          'wrapHeaderText', 'autoHeaderHeight',

          // Cell renderer
          'cellRenderer', 'cellRendererParams', 'cellRendererSelector',

          // Layout and display
          'wrapText', 'autoHeight', 'rowSpan', 'colSpan',
          'textAlign', 'verticalAlign',

          // Sorting and aggregation
          'sortable', 'sort', 'sortingOrder', 'comparator',
          'unSortIcon', 'aggFunc', 'allowedAggFuncs',

          // Pinning and sizing
          'pinned', 'lockPosition', 'lockPinned', 'lockVisible',
          'width', 'minWidth', 'maxWidth', 'flex',
          'resizable', 'suppressSizeToFit',
          'initialWidth', 'initialHide', 'initialPinned',

          // Tooltips
          'tooltip', 'tooltipField', 'tooltipValueGetter', 'tooltipComponent', 'tooltipComponentParams',

          // Other properties
          'suppressKeyboardEvent', 'suppressNavigable', 'suppressPaste',
          'checkboxSelection', 'showDisabledCheckboxes'
        ];

        // Process all columns
        for (const [columnId, _column] of columnDefinitions) {
          const pendingChanges: Partial<ColDef> = {};

          // Set all customization properties to undefined
          customizationProperties.forEach(prop => {
            (pendingChanges as any)[prop] = undefined;
          });

          // Add to pending changes (will be applied when user clicks Apply)
          newPendingChanges.set(columnId, pendingChanges);
          clearedCount++;
        }

        // Clear applied templates as well
        const templateCount = get().appliedTemplates.size;
        const newAppliedTemplates = new Map<string, { templateId: string; templateName: string; appliedAt: number }>();

        // Update store with pending changes (not applied yet)
        set({ 
          pendingChanges: newPendingChanges,
          appliedTemplates: newAppliedTemplates
        });

        const totalCleared = clearedCount + (templateCount > 0 ? templateCount : 0);
        console.log('[Store] Set pending clear for', clearedCount, 'columns and', templateCount, 'templates');

        return totalCleared;
      },

      setAppliedTemplate: (columnId, templateId, templateName) => {
        const { appliedTemplates } = get();
        const newAppliedTemplates = new Map(appliedTemplates);
        newAppliedTemplates.set(columnId, {
          templateId,
          templateName,
          appliedAt: Date.now()
        });
        set({ appliedTemplates: newAppliedTemplates });
      },

      removeAppliedTemplate: (columnId) => {
        const { appliedTemplates } = get();
        const newAppliedTemplates = new Map(appliedTemplates);
        newAppliedTemplates.delete(columnId);
        set({ appliedTemplates: newAppliedTemplates });
        console.log('[Store] Removed template from column:', columnId, 'Remaining templates:', newAppliedTemplates.size);
      },
      }),
      {
        name: 'column-customization-store',
        partialize: (state) => ({
          // Only persist UI preferences, not data
          activeTab: state.activeTab,
          showOnlyCommon: state.showOnlyCommon,
          compareMode: state.compareMode,
          cellDataTypeFilter: state.cellDataTypeFilter,
          visibilityFilter: state.visibilityFilter,
          bulkActionsPanelCollapsed: state.bulkActionsPanelCollapsed,
          templateColumns: Array.from(state.templateColumns), // Convert Set to Array for serialization
          appliedTemplates: Array.from(state.appliedTemplates.entries()), // Convert Map to Array
          uiMode: state.uiMode,
          showPreviewPane: state.showPreviewPane,
          collapsedSections: Array.from(state.collapsedSections), // Convert Set to Array
          quickFormatPinned: state.quickFormatPinned,
          showColumnDrawer: state.showColumnDrawer,
        }),
        onRehydrateStorage: () => (state) => {
          // Convert Sets back from Arrays after rehydration
          if (state) {
            if (Array.isArray(state.templateColumns)) {
              state.templateColumns = new Set(state.templateColumns);
            }
            if (Array.isArray(state.collapsedSections)) {
              state.collapsedSections = new Set(state.collapsedSections);
            }
            if (Array.isArray(state.appliedTemplates)) {
              state.appliedTemplates = new Map(state.appliedTemplates);
            }
          }
        },
      }
    )
  )
);

