        </Card>

        {/* Custom Format Editor */}
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2">
              <Hash className="h-4 w-4 text-primary" />
              Custom Format
            </CardTitle>
            <CardDescription className="text-sm">
              Create advanced formatting with Excel-like syntax
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            {isDisabled ? (
              <Alert className="bg-muted/50">
                <Info className="h-4 w-4" />
                <AlertDescription className="text-sm">
                  Select one or more columns to apply custom formatting
                </AlertDescription>
              </Alert>
            ) : (
              <>
                {!hasConsistentFormatter && currentFormatters.size > 0 && (
                  <Alert className="bg-amber-50 dark:bg-amber-950/20 border-amber-200 dark:border-amber-800/50">
                    <Lightbulb className="h-4 w-4 text-amber-600 dark:text-amber-400" />
                    <AlertDescription className="text-sm text-amber-800 dark:text-amber-200">
                      Selected columns have different formats. Applying a new format will override all of them.
                    </AlertDescription>
                  </Alert>
                )}

                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <Label className="text-sm font-medium">Current Format</Label>
                    <Badge variant="outline" className="text-xs font-mono">
                      {currentFormatString || 'None'}
                    </Badge>
                  </div>

                  <Button
                    variant="default"
                    size="sm"
                    onClick={openFormatterEditor}
                    disabled={isDisabled}
                    className="w-full justify-center gap-2 h-9"
                  >
                    <Palette className="h-4 w-4" />
                    <span>Open Format Editor</span>
                  </Button>
                </div>

                <Separator />

                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <Label className="text-sm font-medium">Format Guide</Label>
                    <Button
                      variant="ghost"
                      size="sm"
                      className="h-7 px-2 gap-1"
                      onClick={() => window.open('https://support.microsoft.com/en-us/office/number-format-codes-5026bbd6-04bc-48cd-bf33-80f18b4eae68', '_blank')}
                    >
                      <HelpCircle className="h-3.5 w-3.5" />
                      <span className="text-xs">Help</span>
                    </Button>
                  </div>

                  <div className="text-xs text-muted-foreground space-y-1.5">
                    <p><strong>Basic:</strong> <code className="bg-muted px-1 py-0.5 rounded">#,##0.00</code> - Number with thousands separator and 2 decimals</p>
                    <p><strong>Currency:</strong> <code className="bg-muted px-1 py-0.5 rounded">$#,##0.00</code> - Currency with symbol</p>
                    <p><strong>Percentage:</strong> <code className="bg-muted px-1 py-0.5 rounded">0.00%</code> - Percentage with 2 decimals</p>
                    <p><strong>Conditional:</strong> <code className="bg-muted px-1 py-0.5 rounded">[&gt;0][Green]#,##0;[Red]#,##0</code> - Color based on value</p>
                  </div>
                </div>
              </>
            )}
          </CardContent>
        </Card>

        {/* Format Preview */}
        {!isDisabled && (
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-base flex items-center gap-2">
                <Eye className="h-4 w-4 text-primary" />
                Format Preview
              </CardTitle>
              <CardDescription className="text-sm">
                See how your format will look with sample data
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-1">
                    <Label className="text-xs text-muted-foreground">Original</Label>
                    <div className="p-2 bg-muted/30 rounded-md border text-sm">
                      {singleDataType === 'number' ? '1234.56' : 
                       singleDataType === 'date' ? '2023-01-15' : 
                       'Sample Text'}
                    </div>
                  </div>
                  <div className="space-y-1">
                    <Label className="text-xs text-muted-foreground">Formatted</Label>
                    <div className="p-2 bg-primary/5 rounded-md border border-primary/20 text-sm font-medium">
                      {currentFormatString ? (
                        singleDataType === 'number' ? '$1,234.56' : 
                        singleDataType === 'date' ? 'Jan 15, 2023' : 
                        'Sample Text'
                      ) : (
                        <span className="text-muted-foreground italic">No format applied</span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Value Formatter Editor Dialog */}
        <ValueFormatterEditor
          open={showFormatterEditor}
          onOpenChange={setShowFormatterEditor}
          initialFormatter={currentFormatters.size > 0 && hasConsistentFormatter ? 
            Array.from(currentFormatters.values())[0] : undefined}
          onSave={handleSaveFormatter}
          title="Custom Value Formatter"
          columnType={singleDataType as 'text' | 'number' | 'date' | 'boolean'}
        />
      </div>
    </ScrollArea>
  );
};

export default FormatTab;

import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { ScrollArea } from '@/components/ui/scroll-area';
import { ThreeStateCheckbox } from '../components/ThreeStateCheckbox';
import { AlignmentIconPicker } from '../components/AlignmentIconPicker';
import { Button } from '@/components/ui/button';
import { Palette, Eraser } from 'lucide-react';
import { StyleEditor } from '../editors/StyleEditor';
import { useColumnCustomizationStore } from '../store/column-customization.store';
import { createCellStyleFunction } from '@/components/datatable/utils/formatters';

interface StylingTabProps {
  uiMode?: 'simple' | 'advanced';
}

export const StylingTab: React.FC<StylingTabProps> = ({ uiMode: _uiMode = 'simple' }) => {
  const {
    selectedColumns,
    columnDefinitions,
    pendingChanges,
    updateBulkProperty
  } = useColumnCustomizationStore();

  const [showCellStyleEditor, setShowCellStyleEditor] = useState(false);
  const [showHeaderStyleEditor, setShowHeaderStyleEditor] = useState(false);

  const getMixedValue = (property: string) => {
    const values = new Set();
    const allValues: unknown[] = [];

    selectedColumns.forEach(colId => {
      const colDef = columnDefinitions.get(colId);
      const pendingChange = pendingChanges.get(colId);

      // Check pending changes first, then fall back to column definition
      let value;
      if (pendingChange && property in pendingChange) {
        value = pendingChange[property as keyof typeof pendingChange];
      } else if (colDef) {
        value = colDef[property as keyof typeof colDef];
      }

      values.add(value);
      allValues.push(value);
    });

    if (values.size === 0) return { value: undefined, isMixed: false };
    if (values.size === 1) return { value: Array.from(values)[0], isMixed: false };
    return { value: undefined, isMixed: true, values: allValues };
  };

  const isDisabled = selectedColumns.size === 0;
  const isMultipleSelection = selectedColumns.size > 1;

  const handleCellStyleSave = (style: React.CSSProperties) => {
    // Check if we have conditional formatting that needs to be preserved
    let hasConditionalFormatting = false;
    let formatString = '';

    selectedColumns.forEach(colId => {
      const colDef = columnDefinitions.get(colId);
      const pendingChange = pendingChanges.get(colId);

      // Check pending changes first, then column definition
      const valueFormatter = pendingChange?.valueFormatter || colDef?.valueFormatter;
      const cellStyle = pendingChange?.cellStyle || colDef?.cellStyle;

      // First check if valueFormatter has format string metadata
      if (valueFormatter && typeof valueFormatter === 'function') {
        const metadata = (valueFormatter as any).__formatString;
        if (metadata && metadata.includes('[') && metadata.includes(']')) {
          // Check if format string contains style directives OR color specifications
          const hasStyleDirectives = metadata.match(/\[(BG:|Background:|Border:|B:|Size:|FontSize:|Align:|TextAlign:|Padding:|P:|Weight:|FontWeight:|Bold|Italic|Underline|Center|Left|Right|#[0-9A-Fa-f]{3,6}|Red|Green|Blue|Yellow|Orange|Purple|Gray|Grey|Black|White|Magenta|Cyan)/i);
          if (hasStyleDirectives) {
            hasConditionalFormatting = true;
            formatString = metadata;
          }
        }
      }

      // Also check if existing cellStyle has format string metadata
      if (!hasConditionalFormatting && cellStyle && typeof cellStyle === 'function') {
        const metadata = (cellStyle as any).__formatString;
        if (metadata && metadata.includes('[') && metadata.includes(']')) {
          hasConditionalFormatting = true;
          formatString = metadata;
        }
      }
    });

    if (hasConditionalFormatting) {
      // We have conditional formatting from valueFormatter
      // Create a cellStyle function that merges base styles with conditional styles
      // Conditional styles take precedence (similar to the reference implementation)
      // Create a custom cellStyle function that always merges base and conditional styles
      // This is different from createCellStyleFunction which returns EITHER conditional OR base styles
      const cellStyleFn = (params: { value: unknown }) => {
        // Always start with base styles
        const baseStyles = style && Object.keys(style).length > 0 ? { ...style } : {};

        // Get conditional styles using createCellStyleFunction with empty base
        // This ensures we only get the conditional styles when conditions match
        const conditionalStyleFn = createCellStyleFunction(formatString, {});
        const conditionalStyles = conditionalStyleFn(params) || {};

        // Always merge base and conditional styles, with conditional taking precedence
        // This matches the reference implementation pattern
        const mergedStyles = { ...baseStyles, ...conditionalStyles };

        // Return merged styles if we have any, otherwise undefined
        return Object.keys(mergedStyles).length > 0 ? mergedStyles : undefined;
      };

      // Attach metadata for future serialization
      Object.defineProperty(cellStyleFn, '__formatString', { 
        value: formatString, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      Object.defineProperty(cellStyleFn, '__baseStyle', { 
        value: style, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      updateBulkProperty('cellStyle', cellStyleFn);
    } else {
      // No conditional formatting, just save the style directly
      updateBulkProperty('cellStyle', style);
    }
  };

  const handleHeaderStyleSave = (style: React.CSSProperties) => {
    console.log('[StylingTab] Saving headerStyle:', {
      style,
      selectedColumnsCount: selectedColumns.size,
      selectedColumns: Array.from(selectedColumns)
    });

    // Create a function that applies styles only to regular headers, not floating filters
    const headerStyleFn = (params: { floatingFilter?: boolean }) => {
      // Don't apply styles to floating filter row
      if (params?.floatingFilter) {
        return null;
      }
      return style;
    };

    updateBulkProperty('headerStyle', headerStyleFn);
  };

  // Clear all styles
  const _clearAllStyles = () => {
    updateBulkProperty('cellStyle', undefined);
    updateBulkProperty('headerStyle', undefined);
    updateBulkProperty('cellClass', undefined);
    updateBulkProperty('headerClass', undefined);
  };

  // Clear only cell styles
  const clearCellStyles = () => {
    // Check if we have conditional formatting from valueFormatter that needs to be preserved
    let hasConditionalFormatting = false;
    let formatString = '';

    selectedColumns.forEach(colId => {
      const colDef = columnDefinitions.get(colId);
      const pendingChange = pendingChanges.get(colId);

      // Check pending changes first, then column definition
      const valueFormatter = pendingChange?.valueFormatter || colDef?.valueFormatter;

      // Check if valueFormatter has format string metadata (from conditional formatting)
      if (valueFormatter && typeof valueFormatter === 'function') {
        const metadata = (valueFormatter as any).__formatString;
        if (metadata && metadata.includes('[') && metadata.includes(']')) {
          // Check if format string contains style directives OR color specifications
          const hasStyleDirectives = metadata.match(/\[(BG:|Background:|Border:|B:|Size:|FontSize:|Align:|TextAlign:|Padding:|P:|Weight:|FontWeight:|Bold|Italic|Underline|Center|Left|Right|#[0-9A-Fa-f]{3,6}|Red|Green|Blue|Yellow|Orange|Purple|Gray|Grey|Black|White|Magenta|Cyan)/i);
          if (hasStyleDirectives) {
            hasConditionalFormatting = true;
            formatString = metadata;
          }
        }
      }
    });

    if (hasConditionalFormatting) {
      // Keep conditional formatting but clear base styles
      // This is the same as handleCellStyleSave but with empty base styles
      const cellStyleFn = (params: { value: unknown }) => {
        // No base styles (cleared)
        const _baseStyles = {};

        // Get conditional styles using createCellStyleFunction with empty base
        const conditionalStyleFn = createCellStyleFunction(formatString, {});
        const conditionalStyles = conditionalStyleFn(params) || {};

        // Return only conditional styles since base styles are cleared
        return Object.keys(conditionalStyles).length > 0 ? conditionalStyles : undefined;
      };

      // Attach metadata for future serialization
      Object.defineProperty(cellStyleFn, '__formatString', { 
        value: formatString, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      Object.defineProperty(cellStyleFn, '__baseStyle', { 
        value: {}, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      updateBulkProperty('cellStyle', cellStyleFn);
    } else {
      updateBulkProperty('cellStyle', undefined);
    }

    // Clear non-format related classes
    const currentCellClass = getMixedValue('cellClass');
    if (currentCellClass.value) {
      const classes = (currentCellClass.value as string).split(' ').filter(c => 
        c.startsWith('ag-numeric-cell') || c.startsWith('ag-currency-cell') || 
        c.startsWith('ag-percentage-cell') || c.startsWith('ag-date-cell')
      );
      updateBulkProperty('cellClass', classes.length > 0 ? classes.join(' ') : undefined);
    }
  };

  // Clear only header styles
  const clearHeaderStyles = () => {
    updateBulkProperty('headerStyle', undefined);
    updateBulkProperty('headerClass', undefined);
  };

  // Clear only alignment classes (keep other styles)
  const _clearAlignment = () => {
    const currentHeaderClass = getMixedValue('headerClass');
    const currentCellClass = getMixedValue('cellClass');

    // Remove alignment classes from headerClass
    if (currentHeaderClass.value) {
      const headerClasses = (currentHeaderClass.value as string).split(' ').filter(c =>
        !c.startsWith('header-align-') && !c.startsWith('header-valign-')
      );
      updateBulkProperty('headerClass', headerClasses.length > 0 ? headerClasses.join(' ') : undefined);
    }

    // Remove alignment classes from cellClass
    if (currentCellClass.value) {
      const cellClasses = (currentCellClass.value as string).split(' ').filter(c =>
        !c.startsWith('cell-align-') && !c.startsWith('cell-valign-')
      );
      updateBulkProperty('cellClass', cellClasses.length > 0 ? cellClasses.join(' ') : undefined);
    }
  };

  // Get current cell style (if consistent across selected columns)
  const currentCellStyle = getMixedValue('cellStyle');
  const currentHeaderStyle = getMixedValue('headerStyle');

  // Extract style object from headerStyle if it's a function
  const getHeaderStyleObject = () => {
    if (currentHeaderStyle.value && typeof currentHeaderStyle.value === 'function') {
      // Call the function with non-floating filter context to get the style object
      return currentHeaderStyle.value({ floatingFilter: false });
    }
    return currentHeaderStyle.value;
  };

  // Extract style object from cellStyle if it's a function
  const getCellStyleObject = () => {
    if (currentCellStyle.value && typeof currentCellStyle.value === 'function') {
      // Check if this function has base style metadata
      const baseStyle = (currentCellStyle.value as any).__baseStyle;
      if (baseStyle) {
        return baseStyle;
      }
      // Don't pass function-based styles to the editor without base style
      return {};
    }
    return currentCellStyle.value;
  };

  // Handle header alignment changes using headerClass (separate from headerStyle)
  const handleHeaderAlignmentChange = (alignment: string, type: 'horizontal' | 'vertical') => {
    const currentHeaderClass = getMixedValue('headerClass');
    // Ensure we always work with strings, not booleans
    const currentClassesRaw = currentHeaderClass.value;
    const currentClasses = (typeof currentClassesRaw === 'string' ? currentClassesRaw : '').trim();
    const classArray = currentClasses ? currentClasses.split(' ').filter(c => c) : [];

    // Remove existing alignment classes
    const filteredClasses = classArray.filter(c => {
      if (type === 'horizontal') {
        return !c.startsWith('header-align-');
      } else {
        return !c.startsWith('header-valign-');
      }
    });

    // Add new alignment class if not default
    if (alignment !== 'default') {
      const prefix = type === 'horizontal' ? 'header-align-' : 'header-valign-';
      const newClass = prefix + alignment;
      filteredClasses.push(newClass);
    }

    const newHeaderClass = filteredClasses.join(' ').trim();
    updateBulkProperty('headerClass', newHeaderClass || undefined);
  };

  // Handle cell alignment changes using cellClass
  const handleCellAlignmentChange = (alignment: string, type: 'horizontal' | 'vertical') => {
    const currentCellClass = getMixedValue('cellClass');
    // Ensure we always work with strings, not booleans
    const currentClassesRaw = currentCellClass.value;
    const currentClasses = (typeof currentClassesRaw === 'string' ? currentClassesRaw : '').trim();
    const classArray = currentClasses ? currentClasses.split(' ').filter(c => c) : [];

    // Remove existing alignment classes
    const filteredClasses = classArray.filter(c => {
      if (type === 'horizontal') {
        return !c.startsWith('cell-align-');
      } else {
        return !c.startsWith('cell-valign-');
      }
    });

    // Add new alignment class if not default
    if (alignment !== 'default') {
      const prefix = type === 'horizontal' ? 'cell-align-' : 'cell-valign-';
      const newClass = prefix + alignment;
      filteredClasses.push(newClass);
    }

    const newCellClass = filteredClasses.join(' ').trim();
    updateBulkProperty('cellClass', newCellClass || undefined);
  };

  // Extract current header alignment from headerClass
  const getCurrentHeaderAlignment = (type: 'horizontal' | 'vertical') => {
    const headerClassValue = getMixedValue('headerClass');
    const headerClassRaw = headerClassValue.value;
    const headerClass = (typeof headerClassRaw === 'string' ? headerClassRaw : '').trim();

    if (!headerClass) return 'default';

    const prefix = type === 'horizontal' ? 'header-align-' : 'header-valign-';
    const regex = new RegExp(`${prefix}(\\w+)`);
    const match = headerClass.match(regex);

    return match ? match[1] : 'default';
  };

  // Extract current cell alignment from cellClass
  const getCurrentCellAlignment = (type: 'horizontal' | 'vertical') => {
    const cellClassValue = getMixedValue('cellClass');
    const cellClassRaw = cellClassValue.value;
    const cellClass = (typeof cellClassRaw === 'string' ? cellClassRaw : '').trim();

    if (!cellClass) return 'default';

    const prefix = type === 'horizontal' ? 'cell-align-' : 'cell-valign-';
    const regex = new RegExp(`${prefix}(\\w+)`);
    const match = cellClass.match(regex);

    return match ? match[1] : 'default';
  };

  // Check if header alignment is mixed
  const isHeaderAlignmentMixed = (_type: 'horizontal' | 'vertical') => {
    const headerClassValue = getMixedValue('headerClass');
    return headerClassValue.isMixed;
  };

  // Check if cell alignment is mixed
  const isCellAlignmentMixed = (_type: 'horizontal' | 'vertical') => {
    const cellClassValue = getMixedValue('cellClass');
    return cellClassValue.isMixed;
  };

  return (
    <ScrollArea className="h-full">
      <div className="px-6 py-4 space-y-6">
        {/* Two-column layout for better space utilization */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Left Column */}
          <div className="space-y-6">
            {/* Header Alignment */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Header Alignment</CardTitle>
                <CardDescription className="text-sm">
                  Set text alignment for column headers
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <AlignmentIconPicker
                  label="Horizontal"
                  type="horizontal"
                  value={getCurrentHeaderAlignment('horizontal')}
                  onChange={(value) => handleHeaderAlignmentChange(value, 'horizontal')}
                  disabled={isDisabled || (isMultipleSelection && isHeaderAlignmentMixed('horizontal'))}
                  isMixed={isMultipleSelection && isHeaderAlignmentMixed('horizontal')}
                />

                <AlignmentIconPicker
                  label="Vertical"
                  type="vertical"
                  value={getCurrentHeaderAlignment('vertical')}
                  onChange={(value) => handleHeaderAlignmentChange(value, 'vertical')}
                  disabled={isDisabled || (isMultipleSelection && isHeaderAlignmentMixed('vertical'))}
                  isMixed={isMultipleSelection && isHeaderAlignmentMixed('vertical')}
                />
              </CardContent>
            </Card>

            {/* Style Editors */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Style Editors</CardTitle>
                <CardDescription className="text-sm">
                  Customize visual appearance
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-3">
                <Button
                  variant="outline"
                  size="sm"
                  className="w-full justify-start gap-2 h-9"
                  disabled={isDisabled}
                  onClick={() => setShowHeaderStyleEditor(true)}
                >
                  <Palette className="h-4 w-4" />
                  Edit Header Style
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  className="w-full justify-start gap-2 h-9"
                  disabled={isDisabled}
                  onClick={() => setShowCellStyleEditor(true)}
                >
                  <Palette className="h-4 w-4" />
                  Edit Cell Style
                </Button>
              </CardContent>
            </Card>

            {/* Text Display Options */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Text Display</CardTitle>
                <CardDescription className="text-sm">
                  Control how text is displayed in cells
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-3">
                <ThreeStateCheckbox
                  label="Wrap Text"
                  property="wrapText"
                  mixedValue={getMixedValue('wrapText')}
                  onChange={(value) => updateBulkProperty('wrapText', value)}
                  disabled={isDisabled || (isMultipleSelection && getMixedValue('wrapText').isMixed)}
                  description="Wrap long text within cells"
                />

                <ThreeStateCheckbox
                  label="Auto Height"
                  property="autoHeight"
                  mixedValue={getMixedValue('autoHeight')}
                  onChange={(value) => updateBulkProperty('autoHeight', value)}
                  disabled={isDisabled || (isMultipleSelection && getMixedValue('autoHeight').isMixed)}
                  description="Automatically adjust row height to fit content"
                />
              </CardContent>
            </Card>
          </div>

          {/* Right Column */}
          <div className="space-y-6">
            {/* Cell Alignment */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Cell Alignment</CardTitle>
                <CardDescription className="text-sm">
                  Set text alignment for cell content
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <AlignmentIconPicker
                  label="Horizontal"
                  type="horizontal"
                  value={getCurrentCellAlignment('horizontal')}
                  onChange={(value) => handleCellAlignmentChange(value, 'horizontal')}
                  disabled={isDisabled || (isMultipleSelection && isCellAlignmentMixed('horizontal'))}
                  isMixed={isMultipleSelection && isCellAlignmentMixed('horizontal')}
                />

                <AlignmentIconPicker
                  label="Vertical"
                  type="vertical"
                  value={getCurrentCellAlignment('vertical')}
                  onChange={(value) => handleCellAlignmentChange(value, 'vertical')}
                  disabled={isDisabled || (isMultipleSelection && isCellAlignmentMixed('vertical'))}
                  isMixed={isMultipleSelection && isCellAlignmentMixed('vertical')}
                />
              </CardContent>
            </Card>

            {/* Clear Styles */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Reset Styles</CardTitle>
                <CardDescription className="text-sm">
                  Remove custom styling and formatting
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-3">
                <Button
                  variant="outline"
                  size="sm"
                  className="w-full justify-start gap-2 h-9"
                  disabled={isDisabled}
                  onClick={clearCellStyles}
                >
                  <Eraser className="h-4 w-4" />
                  Clear Cell Styles
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  className="w-full justify-start gap-2 h-9"
                  disabled={isDisabled}
                  onClick={clearHeaderStyles}
                >
                  <Eraser className="h-4 w-4" />
                  Clear Header Styles
                </Button>
              </CardContent>
            </Card>

            {/* Header Text */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Header Text</CardTitle>
                <CardDescription className="text-sm">
                  Header text display options
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-3">
                <ThreeStateCheckbox
                  label="Wrap Header Text"
                  property="wrapHeaderText"
                  mixedValue={getMixedValue('wrapHeaderText')}
                  onChange={(value) => updateBulkProperty('wrapHeaderText', value)}
                  disabled={isDisabled || (isMultipleSelection && getMixedValue('wrapHeaderText').isMixed)}
                  description="Wrap long text in column headers"
                />

                <ThreeStateCheckbox
                  label="Auto Header Height"
                  property="autoHeaderHeight"
                  mixedValue={getMixedValue('autoHeaderHeight')}
                  onChange={(value) => updateBulkProperty('autoHeaderHeight', value)}
                  disabled={isDisabled || (isMultipleSelection && getMixedValue('autoHeaderHeight').isMixed)}
                  description="Automatically adjust header height"
                />
              </CardContent>
            </Card>
          </div>
        </div>

        {/* Style Editors */}
        <StyleEditor
          open={showCellStyleEditor}
          onOpenChange={setShowCellStyleEditor}
          title="Cell Style Editor"
          initialStyle={currentCellStyle.isMixed ? {} : (getCellStyleObject() as React.CSSProperties || {})}
          onSave={handleCellStyleSave}
          isHeaderStyle={false}
        />

        <StyleEditor
          open={showHeaderStyleEditor}
          onOpenChange={setShowHeaderStyleEditor}
          title="Header Style Editor"
          initialStyle={currentHeaderStyle.isMixed ? {} : (getHeaderStyleObject() as React.CSSProperties || {})}
          onSave={handleHeaderStyleSave}
          isHeaderStyle={true}
        />
      </div>
    </ScrollArea>
  );
};

import { useCallback, useRef, useMemo } from 'react';
import { ColDef, GridApi } from 'ag-grid-community';
import { useActiveProfile } from '@/stores/profile.store';
import { profileOptimizer } from '@/lib/profile-optimizer';
import { debounce } from '@/lib/utils';
import { ColumnDef } from '../types';
import { STATE_RESTORATION_DELAY_MS, COLUMN_UPDATE_DEBOUNCE_MS } from '../utils/constants';

/**
 * Custom hook for handling column operations and updates
 */
export function useColumnOperations(
  gridApiRef: React.MutableRefObject<GridApi | null>,
  setCurrentColumnDefs: (columns: ColumnDef[]) => void
) {
  const activeProfile = useActiveProfile();
  const columnDefsWithStylesRef = useRef<ColumnDef[]>([]);

  // Debounced column update to prevent rapid re-renders
  const applyColumnChangesDebounced = useMemo(
    () => debounce((columns: ColDef[]) => {
      if (gridApiRef.current) {
        gridApiRef.current.setGridOption('columnDefs', columns);

        // Force header refresh to ensure styles are applied
        gridApiRef.current.refreshHeader();

        // Also refresh cells to ensure cell styles are applied/cleared
        gridApiRef.current.refreshCells({ 
          force: true,
          suppressFlash: false 
        });

        // Redraw rows to ensure all styling is updated
        gridApiRef.current.redrawRows();
      }
    }, COLUMN_UPDATE_DEBOUNCE_MS),
    []
  );

  const handleApplyColumnChanges = useCallback((updatedColumns: ColDef[]) => {
    console.log('[useColumnOperations] handleApplyColumnChanges:', {
      updatedColumnsCount: updatedColumns.length,
      hasGridApi: !!gridApiRef.current,
      hasCustomizations: updatedColumns.some(col => 
        col.cellStyle || col.valueFormatter || col.cellClass
      ),
      firstColumn: updatedColumns[0]?.field
    });

    // IMPORTANT: We need to preserve the current column state (width, position, visibility)
    // and only update the customization properties
    if (gridApiRef.current) {
      // Get current column state to preserve
      const currentColumnState = gridApiRef.current.getColumnState();
      const currentFilterModel = gridApiRef.current.getFilterModel();
      const currentSortModel = gridApiRef.current.getColumnState().filter(col => col.sort);
      const currentColumnDefs = gridApiRef.current.getColumnDefs() || [];

      console.log('[useColumnOperations] Preserving AG-Grid state:', {
        columnStateCount: currentColumnState.length,
        visibleColumns: currentColumnState.filter(cs => !cs.hide).length,
        hiddenColumns: currentColumnState.filter(cs => cs.hide).length,
        pinnedColumns: currentColumnState.filter(cs => cs.pinned).length,
        sortedColumns: currentSortModel.length,
        hasFilters: Object.keys(currentFilterModel).length > 0
      });

      // Create a map of current columns for quick lookup
      const currentColMap = new Map();
      currentColumnDefs.forEach(col => {
        if ('field' in col && col.field) {
          currentColMap.set(col.field, col);
        } else if ('colId' in col && col.colId) {
          currentColMap.set(col.colId, col);
        }
      });

      // Merge the customizations with existing column definitions
      const mergedColumns = updatedColumns.map(updatedCol => {
        const field = updatedCol.field || updatedCol.colId;
        const currentCol = currentColMap.get(field);

        if (currentCol) {
          // Enhanced logging for debugging style application
          const hasCellStyle = updatedCol.cellStyle !== undefined;
          const hasValueFormatter = updatedCol.valueFormatter !== undefined;

          if (hasCellStyle || hasValueFormatter) {
            console.log(`[useColumnOperations] Column ${field} style/formatter update:`, {
              field,
              hasCellStyle,
              cellStyleType: typeof updatedCol.cellStyle,
              hasValueFormatter,
              valueFormatterType: typeof updatedCol.valueFormatter,
            });
          }

          // For columns being updated, use the updated version entirely
          // The store has already handled property removal/addition
          return updatedCol;
        }

        // If column not found in current state, return as is
        return updatedCol;
      });

      // Store the merged columns with styles for later retrieval
      columnDefsWithStylesRef.current = mergedColumns as ColumnDef[];

      // Update the currentColumnDefs state to ensure consistency
      setCurrentColumnDefs(mergedColumns as ColumnDef[]);

      // Apply the merged column definitions with debouncing
      applyColumnChangesDebounced(mergedColumns);

      // Restore the column state, filters, and sorts to preserve user's current view
      // Small delay to ensure column definitions are fully applied
      setTimeout(() => {
        if (gridApiRef.current) {
          // Restore column state (width, position, visibility, pinning)
          if (currentColumnState) {
            console.log('[useColumnOperations] Restoring column state after customization');
            gridApiRef.current.applyColumnState({
              state: currentColumnState,
              applyOrder: true
            });
          }

          // Restore filters
          if (currentFilterModel && Object.keys(currentFilterModel).length > 0) {
            console.log('[useColumnOperations] Restoring filters:', currentFilterModel);
            gridApiRef.current.setFilterModel(currentFilterModel);
          }

          // Restore sorts
          if (currentSortModel && currentSortModel.length > 0) {
            console.log('[useColumnOperations] Restoring sorts:', currentSortModel);
            const sortState = currentSortModel.map(col => ({
              colId: col.colId,
              sort: col.sort,
              sortIndex: col.sortIndex
            }));
            gridApiRef.current.applyColumnState({
              state: sortState
            });
          }

          // Force a final refresh to ensure everything is applied correctly
          gridApiRef.current.refreshCells({ force: true });
        }

        // Don't show toast here as this is called frequently during editing
        // Toast will be shown when the user saves the profile
      }, STATE_RESTORATION_DELAY_MS); // Small delay to ensure column definitions are fully applied
    }

    // Update React state
    setCurrentColumnDefs(updatedColumns.map((col) => {
      if (col.field && col.headerName) {
        return col as ColumnDef;
      }

      return {
        ...col,
        field: col.field || '',
        headerName: col.headerName || col.field || ''
      };
    }));

    // Clear optimizer cache for active profile since columns changed
    if (activeProfile) {
      profileOptimizer.clearCache(activeProfile.id);
    }

    // Column changes from dialog are only saved when Save Profile button is clicked
  }, [activeProfile, setCurrentColumnDefs, applyColumnChangesDebounced]);

  // Get column definitions with styles
  const getColumnDefsWithStyles = useCallback(() => {
    return columnDefsWithStylesRef.current;
  }, []);

  return {
    handleApplyColumnChanges,
    getColumnDefsWithStyles,
  };
}

import { useMemo } from 'react';
import { createCellStyleFunction, hasConditionalStyling } from '../utils/style-utils';
import { ColumnDef } from '../types';

/**
 * Custom hook that processes columns to ensure cellStyle functions are created
 * for columns with conditional formatting in their valueFormatters.
 * 
 * This preserves the CRITICAL functionality from ensureCellStyleForColumns
 * that merges base styles with conditional styles.
 */
export function useColumnProcessor(columns: ColumnDef[]): ColumnDef[] {
  return useMemo(() => {
    let hasChanges = false;

    const processed = columns.map(col => {
      // Check if valueFormatter has conditional styling that needs cellStyle
      if (!col.valueFormatter || typeof col.valueFormatter !== 'function') {
        return col;
      }

      const formatString = (col.valueFormatter as any).__formatString;

      if (!formatString || !hasConditionalStyling(formatString)) {
        return col;
      }

      // Check if cellStyle needs to be created or updated
      const existingCellStyle = col.cellStyle as any;
      const needsCellStyle = !col.cellStyle || 
                            (typeof col.cellStyle === 'function' && 
                             existingCellStyle.__formatString !== formatString);

      if (!needsCellStyle) {
        return col;
      }

      hasChanges = true;

      console.log('[useColumnProcessor] Creating cellStyle for conditional formatting:', {
        field: col.field,
        formatString,
        hasExistingCellStyle: !!col.cellStyle
      });

      // Extract base style if it exists
      let baseStyle: React.CSSProperties = {};
      if (col.cellStyle) {
        if (typeof col.cellStyle === 'object') {
          baseStyle = col.cellStyle as React.CSSProperties;
        } else if (typeof col.cellStyle === 'function' && existingCellStyle.__baseStyle) {
          baseStyle = existingCellStyle.__baseStyle;
        }
      }

      // Create cellStyle function with proper base style merging
      const cellStyleFn = createCellStyleFunction(formatString, baseStyle);

      // Attach metadata for future serialization
      Object.defineProperty(cellStyleFn, '__formatString', { 
        value: formatString, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      Object.defineProperty(cellStyleFn, '__baseStyle', { 
        value: baseStyle, 
        writable: false,
        enumerable: false,
        configurable: true
      });

      return { ...col, cellStyle: cellStyleFn };
    });

    // Only return new array if changes were made
    return hasChanges ? processed : columns;
  }, [columns]);
}

