import { ValueFormatterParams, CellStyleParams, ColDef as AgColDef } from 'ag-grid-community';

/**
 * Enhanced column definition with required fields
 */
export interface ColumnDef extends Omit<AgColDef, 'field' | 'headerName' | 'type'> {
  field: string;
  headerName: string;
  type?: string | string[]; // For legacy/custom use
  cellDataType?: 'text' | 'number' | 'date' | 'boolean' | string | boolean; // ag-Grid v33+ optimization
}

/**
 * Metadata attached to formatter functions for serialization
 */
export interface FormatterMetadata {
  __formatString: string;
  __formatterType: 'excel' | 'visual' | 'custom';
  __visualRules?: SerializedFormattingRule[];
  __visualDefaultFallback?: SerializedDefaultFallback;
}

/**
 * Metadata attached to cell style functions
 */
export interface CellStyleMetadata {
  __formatString?: string;
  __baseStyle?: React.CSSProperties;
  __cacheKey?: string;
}

/**
 * Enhanced formatter function type with metadata
 */
export type FormatterFunction = ((params: ValueFormatterParams) => string) & FormatterMetadata;

/**
 * Enhanced cell style function type with metadata
 */
export type CellStyleFunction = ((params: CellStyleParams) => React.CSSProperties | undefined) & CellStyleMetadata;

/**
 * Serialized formatting rule for visual formatter
 */
export interface SerializedFormattingRule {
  id: string;
  condition: {
    type: 'equals' | 'contains' | 'startsWith' | 'endsWith' | 'greaterThan' | 'lessThan' | 'greaterEqual' | 'lessEqual' | 'between' | 'isEmpty' | 'isNotEmpty';
    value: string;
    value2?: string;
  };
  display: {
    type: 'text' | 'original' | 'custom';
    text: string;
  };
  styling: {
    backgroundColor?: string;
    textColor?: string;
    fontSize?: number;
    fontWeight?: 'normal' | 'bold' | '100' | '200' | '300' | '400' | '500' | '600' | '700' | '800' | '900';
    fontStyle?: 'normal' | 'italic';
    textDecoration?: 'none' | 'underline' | 'line-through';
    textAlign?: 'left' | 'center' | 'right';
    border?: {
      width: number;
      style: 'solid' | 'dashed' | 'dotted' | 'double';
      color: string;
      sides: {
        top: boolean;
        right: boolean;
        bottom: boolean;
        left: boolean;
      };
    };
    padding?: {
      top: number;
      right: number;
      bottom: number;
      left: number;
    };
  };
  enabled: boolean;
}

/**
 * Serialized default fallback for visual formatter
 */
export interface SerializedDefaultFallback {
  display: {
    type: 'original' | 'text';
    text: string;
  };
  styling: SerializedFormattingRule['styling'];
}

/**
 * Props for DataTable component
 */
export interface DataTableProps {
  columnDefs: ColumnDef[];
  dataRow: Record<string, unknown>[];
}

/**
 * Context value for DataTable
 */
export interface DataTableContextValue {
  processedColumns: ColumnDef[];
  selectedFont: string;
  handleFontChange: (font: string) => void;
  showColumnDialog: boolean;
  setShowColumnDialog: (show: boolean) => void;
  gridApiRef: React.MutableRefObject<any | null>;
  getColumnDefsWithStyles: () => ColumnDef[];
}

import { FormatterFunction, CellStyleFunction } from '../types';

/**
 * Type guard to check if a function is a formatter with metadata
 */
export function isFormatterFunction(fn: unknown): fn is FormatterFunction {
  return typeof fn === 'function' && '__formatString' in fn;
}

/**
 * Type guard to check if a function is a cell style function with metadata
 */
export function isCellStyleFunction(fn: unknown): fn is CellStyleFunction {
  return typeof fn === 'function' && ('__formatString' in fn || '__baseStyle' in fn);
}

/**
 * Safely extract formatter metadata from a function
 */
export function getFormatterMetadata(fn: unknown): {
  formatString?: string;
  formatterType?: 'excel' | 'visual' | 'custom';
  visualRules?: any[];
  visualDefaultFallback?: any;
} | null {
  if (!isFormatterFunction(fn)) {
    return null;
  }

  return {
    formatString: (fn as any).__formatString,
    formatterType: (fn as any).__formatterType,
    visualRules: (fn as any).__visualRules,
    visualDefaultFallback: (fn as any).__visualDefaultFallback,
  };
}

/**
 * Safely extract cell style metadata from a function
 */
export function getCellStyleMetadata(fn: unknown): {
  formatString?: string;
  baseStyle?: React.CSSProperties;
  cacheKey?: string;
} | null {
  if (!isCellStyleFunction(fn)) {
    return null;
  }

  return {
    formatString: (fn as any).__formatString,
    baseStyle: (fn as any).__baseStyle,
    cacheKey: (fn as any).__cacheKey,
  };
}

import { createContext, useContext, useEffect, useState } from 'react';

type Theme = 'dark' | 'light';

type ThemeProviderProps = {
  children: React.ReactNode;
  defaultTheme?: Theme;
  enableSystem?: boolean;
};

type ThemeProviderState = {
  theme: Theme;
  setTheme: (theme: Theme) => void;
};

const initialState: ThemeProviderState = {
  theme: 'dark',
  setTheme: () => null,
};

const ThemeProviderContext = createContext<ThemeProviderState>(initialState);

export function ThemeProvider({
  children,
  defaultTheme = 'dark',
  ...props
}: ThemeProviderProps) {
  const [theme, setTheme] = useState<Theme>(defaultTheme);

  useEffect(() => {
    const root = window.document.documentElement;
    root.classList.remove('light', 'dark');
    root.classList.add(theme);
  }, [theme]);

  const value = {
    theme,
    setTheme: (theme: Theme) => {
      setTheme(theme);
    },
  };

  return (
    <ThemeProviderContext.Provider {...props} value={value}>
      {children}
    </ThemeProviderContext.Provider>
  );
}

export const useTheme = () => {
  const context = useContext(ThemeProviderContext);

  if (context === undefined)
    throw new Error('useTheme must be used within a ThemeProvider');

  return context;
};

