// Performance monitoring utility
export interface PerformanceMetrics {
  appStartTime: number;
  gridInitTime?: number;
  profileLoadTime?: number;
  dataGenerationTime?: number;
  firstRenderTime?: number;
  fullyLoadedTime?: number;
  [key: string]: number | undefined;
}

class PerformanceMonitor {
  private metrics: PerformanceMetrics;
  private marks: Map<string, number> = new Map();

  constructor() {
    this.metrics = {
      appStartTime: performance.now()
    };
  }

  mark(name: string) {
    const time = performance.now();
    this.marks.set(name, time);
    // console.log(`[Performance] ${name}: ${time.toFixed(2)}ms`);
  }

  measure(name: string, startMark: string, endMark?: string) {
    const startTime = this.marks.get(startMark) || 0;
    const endTime = endMark ? (this.marks.get(endMark) || performance.now()) : performance.now();
    const duration = endTime - startTime;

    this.metrics[name] = duration;
    // console.log(`[Performance] ${name}: ${duration.toFixed(2)}ms`);

    return duration;
  }

  measureFromStart(name: string) {
    const duration = performance.now() - this.metrics.appStartTime;
    this.metrics[name] = duration;
    // console.log(`[Performance] ${name} from start: ${duration.toFixed(2)}ms`);
    return duration;
  }

  getMetrics(): PerformanceMetrics {
    return { ...this.metrics };
  }

  logSummary() {
    // console.group(' Performance Summary');
    // Object.entries(this.metrics).forEach(([key, value]) => {
    //   if (value !== undefined) {
    //     console.log(`${key}: ${value.toFixed(2)}ms`);
    //   }
    // });
    // console.groupEnd();
  }
}

export const perfMonitor = new PerformanceMonitor();

// Export to window for debugging
if (typeof window !== 'undefined') {
  (window as any).perfMonitor = perfMonitor;
}

// Performance testing utilities
import { perfMonitor } from './performance-monitor';

export interface PerformanceTestResult {
  testName: string;
  metrics: {
    [key: string]: number;
  };
  timestamp: number;
  userAgent: string;
}

class PerformanceTester {
  private results: PerformanceTestResult[] = [];

  async runLoadTest(): Promise<PerformanceTestResult> {
    const testName = 'Page Load Performance';
    const metrics = perfMonitor.getMetrics();

    // Calculate derived metrics
    const derivedMetrics = {
      ...metrics,
      totalLoadTime: metrics.fullyLoadedTime || 0,
      gridLoadTime: (metrics.gridInitTime || 0) - (metrics.appStartTime || 0),
      dataGenTime: metrics.dataGenerationTime || 0,
      profileLoadOverhead: metrics.profileLoadTime || 0,
      perceivedLoadTime: metrics.firstRenderTime || 0,
    };

    const result: PerformanceTestResult = {
      testName,
      metrics: derivedMetrics,
      timestamp: Date.now(),
      userAgent: navigator.userAgent,
    };

    this.results.push(result);
    return result;
  }

  async runProfileSwitchTest(profileId: string): Promise<PerformanceTestResult> {
    const startTime = performance.now();

    // This would need to be integrated with the actual profile switching logic
    // For now, we'll just measure the time it takes to switch profiles

    const endTime = performance.now();
    const switchTime = endTime - startTime;

    const result: PerformanceTestResult = {
      testName: 'Profile Switch Performance',
      metrics: {
        profileSwitchTime: switchTime,
        profileId: profileId.length, // Just for reference
      },
      timestamp: Date.now(),
      userAgent: navigator.userAgent,
    };

    this.results.push(result);
    return result;
  }

  getResults(): PerformanceTestResult[] {
    return [...this.results];
  }

  generateReport(): string {
    const report = ['# Performance Test Report', ''];

    this.results.forEach((result, index) => {
      report.push(`## Test ${index + 1}: ${result.testName}`);
      report.push(`Timestamp: ${new Date(result.timestamp).toISOString()}`);
      report.push('');
      report.push('### Metrics:');

      Object.entries(result.metrics).forEach(([key, value]) => {
        if (typeof value === 'number') {
          report.push(`- ${key}: ${value.toFixed(2)}ms`);
        }
      });

      report.push('');
    });

    // Summary statistics
    if (this.results.length > 0) {
      report.push('## Summary');
      report.push('');

      const loadTests = this.results.filter(r => r.testName === 'Page Load Performance');
      if (loadTests.length > 0) {
        const avgTotalLoad = loadTests.reduce((sum, test) => 
          sum + (test.metrics.totalLoadTime || 0), 0) / loadTests.length;
        const avgPerceivedLoad = loadTests.reduce((sum, test) => 
          sum + (test.metrics.perceivedLoadTime || 0), 0) / loadTests.length;

        report.push(`- Average Total Load Time: ${avgTotalLoad.toFixed(2)}ms`);
        report.push(`- Average Perceived Load Time: ${avgPerceivedLoad.toFixed(2)}ms`);
      }
    }

    return report.join('\n');
  }

  logReport(): void {
    console.group(' Performance Test Report');

    this.results.forEach((result, index) => {
      console.group(`Test ${index + 1}: ${result.testName}`);
      console.table(result.metrics);
      console.log('Timestamp:', new Date(result.timestamp).toISOString());
      console.groupEnd();
    });

    console.groupEnd();
  }
}

export const perfTester = new PerformanceTester();

// Export to window for easy access
if (typeof window !== 'undefined') {
  (window as any).perfTester = perfTester;

  // Add convenience methods
  (window as any).runPerfTest = async () => {
    const result = await perfTester.runLoadTest();
    perfTester.logReport();
    return result;
  };

  (window as any).getPerfReport = () => {
    return perfTester.generateReport();
  };
}

import { ColDef, ColumnState, SortModelItem, GridApi } from 'ag-grid-community';
import { GridProfile } from '@/stores/profile.store';
import { deserializeColumnCustomizations } from '@/stores/column-serializer';
import { createExcelFormatter } from '@/components/datatable/utils/formatters';

interface ProfileCache {
  processedColumnDefs: ColDef[];
  columnStateHash: string;
  filterModelHash: string;
  sortModelHash: string;
  timestamp: number;
}

interface ProfileDiff {
  columnDefsChanged: boolean;
  columnStateChanged: boolean;
  filterModelChanged: boolean;
  sortModelChanged: boolean;
  gridOptionsChanged: boolean;
  changes: {
    addedColumns: string[];
    removedColumns: string[];
    modifiedColumns: string[];
    columnStateChanges: Partial<ColumnState>[];
    filterChanges: Record<string, any>;
    sortChanges: SortModelItem[];
  };
}

class ProfileOptimizer {
  private cache = new Map<string, ProfileCache>();
  private currentProfileId: string | null = null;
  private pendingUpdates: Map<string, any> = new Map();
  private updateFrameId: number | null = null;

  // Pre-process profiles in background
  async preprocessProfile(profile: GridProfile) {
    if (this.cache.has(profile.id)) {
      const cached = this.cache.get(profile.id)!;
      // Cache is valid for 5 minutes
      if (Date.now() - cached.timestamp < 5 * 60 * 1000) {
        return;
      }
    }

    // Use requestIdleCallback for background processing
    if ('requestIdleCallback' in window) {
      requestIdleCallback(() => this.processProfile(profile), { timeout: 2000 });
    } else {
      setTimeout(() => this.processProfile(profile), 100);
    }
  }

  private processProfile(profile: GridProfile) {
    // perfMonitor.mark('profile-process-start');

    let processedColumnDefs: ColDef[] = [];

    // Process column definitions
    if (profile.gridState.columnCustomizations && profile.gridState.baseColumnDefs) {
      processedColumnDefs = deserializeColumnCustomizations(
        profile.gridState.columnCustomizations,
        profile.gridState.baseColumnDefs
      );
    } else if (profile.gridState.columnDefs) {
      processedColumnDefs = this.processLegacyColumnDefs(profile.gridState.columnDefs);
    }

    // Create hashes for quick comparison
    const columnStateHash = this.hashObject(profile.gridState.columnState || []);
    const filterModelHash = this.hashObject(profile.gridState.filterModel || {});
    const sortModelHash = this.hashObject(profile.gridState.sortModel || []);

    this.cache.set(profile.id, {
      processedColumnDefs,
      columnStateHash,
      filterModelHash,
      sortModelHash,
      timestamp: Date.now()
    });

          // perfMonitor.measure('profileProcessTime', 'profile-process-start');
  }

  private processLegacyColumnDefs(columnDefs: ColDef[]): ColDef[] {
    return columnDefs.map(col => {
      const processed = { ...col };

      // Clean invalid properties
      delete (processed as any).valueFormat;
      delete (processed as any)._hasFormatter;
      delete (processed as any).excelFormat;

      // Convert headerStyle objects back to functions
      if (processed.headerStyle && typeof processed.headerStyle === 'object') {
        const styleConfig = processed.headerStyle as any;

        if (styleConfig._isHeaderStyleConfig) {
          processed.headerStyle = ((params: { floatingFilter?: boolean }) => {
            if (params?.floatingFilter) {
              return styleConfig.floating || null;
            }
            return styleConfig.regular || null;
          }) as ColDef['headerStyle'];
        }
      }

      // Recreate valueFormatter from saved config
      if (processed.valueFormatter && typeof processed.valueFormatter === 'object') {
        const formatterConfig = processed.valueFormatter as any;

        if (formatterConfig._isFormatterConfig && formatterConfig.type === 'excel' && formatterConfig.formatString) {
          // Recreate the formatter function
          const formatter = createExcelFormatter(formatterConfig.formatString);
          processed.valueFormatter = formatter;
          // Value formatter will be used for export automatically
        } else {
          // Invalid formatter config, remove it
          delete processed.valueFormatter;
          // No export formatter needed
        }
      }

      return processed;
    });
  }

  // Calculate diff between current and target profile
  calculateDiff(currentProfile: GridProfile | null, targetProfile: GridProfile): ProfileDiff {
    const diff: ProfileDiff = {
      columnDefsChanged: false,
      columnStateChanged: false,
      filterModelChanged: false,
      sortModelChanged: false,
      gridOptionsChanged: false,
      changes: {
        addedColumns: [],
        removedColumns: [],
        modifiedColumns: [],
        columnStateChanges: [],
        filterChanges: {},
        sortChanges: []
      }
    };

    if (!currentProfile) {
      // Everything is new
      diff.columnDefsChanged = true;
      diff.columnStateChanged = true;
      diff.filterModelChanged = !!targetProfile.gridState.filterModel;
      diff.sortModelChanged = !!targetProfile.gridState.sortModel?.length;
      diff.gridOptionsChanged = !!targetProfile.gridState.gridOptions;
      return diff;
    }

    // Compare column definitions
    const currentCache = this.cache.get(currentProfile.id);
    const targetCache = this.cache.get(targetProfile.id);

    if (!currentCache || !targetCache || 
        currentCache.processedColumnDefs.length !== targetCache.processedColumnDefs.length) {
      diff.columnDefsChanged = true;
    } else {
      // Deep comparison of column definitions
      for (let i = 0; i < currentCache.processedColumnDefs.length; i++) {
        const current = currentCache.processedColumnDefs[i];
        const target = targetCache.processedColumnDefs[i];

        if (JSON.stringify(current) !== JSON.stringify(target)) {
          diff.columnDefsChanged = true;
          diff.changes.modifiedColumns.push(current.field || '');
        }
      }
    }

    // Compare states using hashes
    diff.columnStateChanged = currentCache?.columnStateHash !== targetCache?.columnStateHash;
    diff.filterModelChanged = currentCache?.filterModelHash !== targetCache?.filterModelHash;
    diff.sortModelChanged = currentCache?.sortModelHash !== targetCache?.sortModelHash;

    // Compare grid options
    diff.gridOptionsChanged = JSON.stringify(currentProfile.gridState.gridOptions) !== 
                             JSON.stringify(targetProfile.gridState.gridOptions);

    return diff;
  }

  // Apply profile with optimizations
  async applyProfile(
    gridApi: GridApi,
    profile: GridProfile,
    currentProfile: GridProfile | null,
    options: {
      showTransition?: boolean;
      onProgress?: (progress: number) => void;
    } = {}
  ) {
    const startTime = performance.now();
    // perfMonitor.mark('profile-switch-start');

    // Ensure profile is preprocessed
    if (!this.cache.has(profile.id)) {
      await this.processProfile(profile);
    }

    const cached = this.cache.get(profile.id);
    if (!cached) {
      console.error('[ProfileOptimizer] Failed to cache profile:', profile.id);
      return;
    }

    // Calculate diff
    const diff = this.calculateDiff(currentProfile, profile);

    // Show transition if needed
    if (options.showTransition) {
      this.showTransitionEffect(gridApi);
    }

    // Optimistic update - immediately update visual indicators
    this.currentProfileId = profile.id;

    // Batch all updates
    this.batchUpdates(() => {
      let progress = 0;
      const totalSteps = Object.keys(diff).filter(key => diff[key as keyof ProfileDiff]).length;

      // Collect all updates to apply in a single transaction
      const gridUpdates: any = {};
      let needsHeaderRefresh = false;
      let needsFilterRefresh = false;

      // Step 1: Prepare column definitions if changed
      if (diff.columnDefsChanged && cached.processedColumnDefs.length > 0) {
        // Get current defaultColDef to ensure it's preserved
        const defaultColDef = gridApi.getGridOption('defaultColDef') || {
          minWidth: 100,
          filter: true,
          floatingFilter: true,
          enableValue: true,
          enableRowGroup: true,
          enablePivot: true,
          resizable: true,
          sortable: true,
          useValueFormatterForExport: true,
        };

        gridUpdates.columnDefs = cached.processedColumnDefs;
        gridUpdates.defaultColDef = defaultColDef;

        // Check if we need header refresh
        needsHeaderRefresh = cached.processedColumnDefs.some(col => 
          col.floatingFilter !== false || col.headerStyle
        );

        progress++;
        options.onProgress?.(progress / totalSteps);
      }

      // Apply all grid updates in one call
      if (Object.keys(gridUpdates).length > 0) {
        gridApi.updateGridOptions(gridUpdates);
      }

      // Batch all remaining state updates into a single operation
      this.scheduleUpdate('profileState', () => {
        // Apply column state, sort, and filter in optimal order

        // 1. Column state (includes visibility, width, position)
        if (diff.columnStateChanged && profile.gridState.columnState) {
          gridApi.applyColumnState({
            state: profile.gridState.columnState,
            applyOrder: true
          });
          progress++;
          options.onProgress?.(progress / totalSteps);
        }

        // 2. Sort model (part of column state)
        if (diff.sortModelChanged && profile.gridState.sortModel?.length > 0) {
          const sortState = profile.gridState.sortModel.map(sort => ({
            colId: sort.colId,
            sort: sort.sort,
            sortIndex: sort.sortIndex
          }));
          gridApi.applyColumnState({ state: sortState });
          progress++;
          options.onProgress?.(progress / totalSteps);
        }

        // 3. Filter model (apply after column state)
        if (diff.filterModelChanged) {
          gridApi.setFilterModel(profile.gridState.filterModel || {});
          needsFilterRefresh = true;
          progress++;
          options.onProgress?.(progress / totalSteps);
        }

        // 4. Single refresh at the end if needed
        if (needsHeaderRefresh || needsFilterRefresh) {
          requestAnimationFrame(() => {
            if (needsHeaderRefresh) {
              gridApi.refreshHeader();
            }
            if (needsFilterRefresh) {
              // In AG-Grid v33, refreshFilters doesn't exist
              // Instead, we notify that filters have changed
              gridApi.onFilterChanged();
            }
          });
        }
      }, 1); // High priority

      // Apply grid options if changed
      if (diff.gridOptionsChanged && profile.gridState.gridOptions) {
        this.scheduleUpdate('gridOptions', () => {
          const options = profile.gridState.gridOptions!;
          if (options.rowHeight) {
            gridApi.resetRowHeights();
            gridApi.setGridOption('rowHeight', options.rowHeight);
          }
          if (options.headerHeight) {
            gridApi.setGridOption('headerHeight', options.headerHeight);
          }
        }, 4);
      }
    });

    // Log performance metrics
    const endTime = performance.now();
    const switchTime = endTime - startTime;

    console.log('[ProfileOptimizer] Profile switch completed:', {
      profileId: profile.id,
      profileName: profile.name,
      switchTime: `${switchTime.toFixed(1)}ms`,
      operations: {
        columnDefsChanged: diff.columnDefsChanged,
        columnStateChanged: diff.columnStateChanged,
        filterModelChanged: diff.filterModelChanged,
        sortModelChanged: diff.sortModelChanged,
        gridOptionsChanged: diff.gridOptionsChanged
      }
    });

    // perfMonitor.measure('profileSwitchTime', 'profile-switch-start');
  }

  private batchUpdates(callback: () => void) {
    // Cancel any pending frame
    if (this.updateFrameId !== null) {
      cancelAnimationFrame(this.updateFrameId);
    }

    callback();

    // Process updates in next animation frame
    this.updateFrameId = requestAnimationFrame(() => {
      this.processPendingUpdates();
    });
  }

  private scheduleUpdate(key: string, update: () => void, priority: number = 0) {
    this.pendingUpdates.set(key, { update, priority });
  }

  private processPendingUpdates() {
    // Sort updates by priority
    const updates = Array.from(this.pendingUpdates.entries())
      .sort((a, b) => a[1].priority - b[1].priority);

    // Execute updates
    updates.forEach(([key, { update }]) => {
      try {
        update();
      } catch (error) {
        console.error(`[ProfileOptimizer] Error applying ${key}:`, error);
      }
    });

    this.pendingUpdates.clear();
    this.updateFrameId = null;
  }

  private showTransitionEffect(_gridApi: GridApi) {
    // Get the grid wrapper element
    const gridElement = document.querySelector('.ag-root-wrapper') as HTMLElement;
    if (!gridElement) return;

    // Use CSS classes for better performance
    gridElement.classList.add('profile-switching');

    // Remove class after animation completes
    requestAnimationFrame(() => {
      requestAnimationFrame(() => {
        gridElement.classList.remove('profile-switching');
      });
    });
  }

  private hashObject(obj: any): string {
    return JSON.stringify(obj).split('').reduce((a, b) => {
      a = ((a << 5) - a) + b.charCodeAt(0);
      return a & a;
    }, 0).toString(36);
  }

  // Clear cache for a specific profile
  clearCache(profileId?: string) {
    if (profileId) {
      this.cache.delete(profileId);
    } else {
      this.cache.clear();
    }
  }

  // Preload all profiles
  async preloadAllProfiles(profiles: GridProfile[]) {
    // Process profiles in batches during idle time
    const batchSize = 2;
    for (let i = 0; i < profiles.length; i += batchSize) {
      const batch = profiles.slice(i, i + batchSize);

      if ('requestIdleCallback' in window) {
        await new Promise(resolve => {
          requestIdleCallback(() => {
            batch.forEach(profile => this.processProfile(profile));
            resolve(undefined);
          }, { timeout: 1000 });
        });
      } else {
        batch.forEach(profile => this.processProfile(profile));
        await new Promise(resolve => setTimeout(resolve, 50));
      }
    }
  }
}

export const profileOptimizer = new ProfileOptimizer();

// Export to window for debugging
if (typeof window !== 'undefined') {
  (window as any).profileOptimizer = profileOptimizer;
}

import { clsx, type ClassValue } from 'clsx';
import { twMerge } from 'tailwind-merge';

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

/**
 * Debounce function that delays invoking func until after wait milliseconds
 * have elapsed since the last time the debounced function was invoked.
 */
export function debounce<T extends (...args: any[]) => any>(
  func: T,
  wait: number
): (...args: Parameters<T>) => void {
  let timeoutId: NodeJS.Timeout | undefined;

  return function debounced(...args: Parameters<T>) {
    if (timeoutId) {
      clearTimeout(timeoutId);
    }

    timeoutId = setTimeout(() => {
      func(...args);
    }, wait);
  };
}

import { ColDef } from 'ag-grid-community';
import { createExcelFormatter, createCellStyleFunction } from '@/components/datatable/utils/formatters';
import { hasConditionalStyling } from '@/components/datatable/utils/style-utils';

// Minimal types for visual formatter data (to avoid circular imports)
interface SerializedFormattingRule {
  id: string;
  condition: {
    type: 'equals' | 'contains' | 'startsWith' | 'endsWith' | 'greaterThan' | 'lessThan' | 'greaterEqual' | 'lessEqual' | 'between' | 'isEmpty' | 'isNotEmpty';
    value: string;
    value2?: string;
  };
  display: {
    type: 'text' | 'original' | 'custom';
    text: string;
  };
  styling: {
    backgroundColor?: string;
    textColor?: string;
    fontSize?: number;
    fontWeight?: 'normal' | 'bold' | '100' | '200' | '300' | '400' | '500' | '600' | '700' | '800' | '900';
    fontStyle?: 'normal' | 'italic';
    textDecoration?: 'none' | 'underline' | 'line-through';
    textAlign?: 'left' | 'center' | 'right';
    border?: {
      width: number;
      style: 'solid' | 'dashed' | 'dotted' | 'double';
      color: string;
      sides: {
        top: boolean;
        right: boolean;
        bottom: boolean;
        left: boolean;
      };
    };
    padding?: {
      top: number;
      right: number;
      bottom: number;
      left: number;
    };
  };
  enabled: boolean;
}

interface SerializedDefaultFallback {
  display: {
    type: 'original' | 'text';
    text: string;
  };
  styling: {
    backgroundColor?: string;
    textColor?: string;
    fontSize?: number;
    fontWeight?: 'normal' | 'bold' | '100' | '200' | '300' | '400' | '500' | '600' | '700' | '800' | '900';
    fontStyle?: 'normal' | 'italic';
    textDecoration?: 'none' | 'underline' | 'line-through';
    textAlign?: 'left' | 'center' | 'right';
    border?: {
      width: number;
      style: 'solid' | 'dashed' | 'dotted' | 'double';
      color: string;
      sides: {
        top: boolean;
        right: boolean;
        bottom: boolean;
        left: boolean;
      };
    };
    padding?: {
      top: number;
      right: number;
      bottom: number;
      left: number;
    };
  };
}

// Type for serialized column customizations
export interface ColumnCustomization {
  // Identity
  field: string;

  // Basic properties (only if changed from defaults)
  headerName?: string;
  width?: number;
  minWidth?: number;
  maxWidth?: number;
  flex?: number;

  // Boolean flags (only if changed from defaults)
  sortable?: boolean;
  resizable?: boolean;
  editable?: boolean;
  filter?: boolean;
  floatingFilter?: boolean;
  hide?: boolean;
  lockPosition?: boolean;
  lockVisible?: boolean;
  lockPinned?: boolean;
  suppressHeaderMenuButton?: boolean;

  // Positioning
  pinned?: 'left' | 'right' | null;

  // Styles (serialized format)
  cellStyle?: {
    type: 'static' | 'function';
    value?: React.CSSProperties;
    formatString?: string; // For conditional formatting
    baseStyle?: React.CSSProperties;
  };
  headerStyle?: {
    type: 'static' | 'function';
    regular?: React.CSSProperties;
    floating?: React.CSSProperties | null;
  };

  // CSS Classes
  cellClass?: string | string[];
  headerClass?: string | string[];

  // Formatters (metadata only)
  valueFormatter?: {
    type: 'excel' | 'visual' | 'custom';
    formatString?: string;
    // Visual editor data (for full round-trip editing)
    rules?: SerializedFormattingRule[];
    defaultFallback?: SerializedDefaultFallback;
  };

  // Cell data type
  cellDataType?: 'text' | 'number' | 'date' | 'boolean';

  // Other commonly customized properties
  headerTooltip?: string;
  tooltipField?: string;
  headerCheckboxSelection?: boolean;
  checkboxSelection?: boolean;
  rowGroup?: boolean;
  pivot?: boolean;
  aggFunc?: string | null;

  // Alignment classes
  cellClassRules?: Record<string, any>;
}

// Default values for comparison
const COLUMN_DEFAULTS: Partial<ColDef> = {
  sortable: true,
  resizable: true,
  filter: true,
  floatingFilter: true,
  editable: false,
  hide: false,
  lockPosition: false,
  lockVisible: false,
  lockPinned: false,
  suppressHeaderMenuButton: false,
  flex: 1,
  minWidth: 100,
};

/**
 * Extract only the customized properties from a column definition
 */
function extractCustomizations(col: ColDef, baseCol?: ColDef): ColumnCustomization {
  const customization: ColumnCustomization = {
    field: col.field!,
  };

  // Compare against base column or defaults
  const compareBase = baseCol || COLUMN_DEFAULTS;

  // Basic properties
  if (col.headerName !== baseCol?.headerName && col.headerName !== col.field) {
    customization.headerName = col.headerName;
  }

  // Width properties
  if (col.width !== undefined && col.width !== compareBase.width) {
    customization.width = col.width;
  }
  if (col.minWidth !== undefined && col.minWidth !== compareBase.minWidth) {
    customization.minWidth = col.minWidth;
  }
  if (col.maxWidth !== undefined && col.maxWidth !== compareBase.maxWidth) {
    customization.maxWidth = col.maxWidth;
  }
  if (col.flex !== undefined && col.flex !== compareBase.flex) {
    customization.flex = col.flex;
  }

  // Boolean flags - only save if different from defaults
  if (col.sortable !== undefined && col.sortable !== compareBase.sortable) {
    customization.sortable = col.sortable;
  }
  if (col.resizable !== undefined && col.resizable !== compareBase.resizable) {
    customization.resizable = col.resizable;
  }
  if (col.editable !== undefined && col.editable !== compareBase.editable) {
    customization.editable = col.editable;
  }
  if (col.filter !== undefined && col.filter !== compareBase.filter) {
    customization.filter = col.filter;
  }
  if (col.floatingFilter !== undefined && col.floatingFilter !== compareBase.floatingFilter) {
    customization.floatingFilter = col.floatingFilter;
  }
  if (col.hide !== undefined && col.hide !== compareBase.hide) {
    customization.hide = col.hide;
  }
  if (col.lockPosition !== undefined && col.lockPosition !== compareBase.lockPosition) {
    customization.lockPosition = col.lockPosition;
  }
  if (col.lockVisible !== undefined && col.lockVisible !== compareBase.lockVisible) {
    customization.lockVisible = col.lockVisible;
  }
  if (col.lockPinned !== undefined && col.lockPinned !== compareBase.lockPinned) {
    customization.lockPinned = col.lockPinned;
  }
  if (col.suppressHeaderMenuButton !== undefined && col.suppressHeaderMenuButton !== compareBase.suppressHeaderMenuButton) {
    customization.suppressHeaderMenuButton = col.suppressHeaderMenuButton;
  }

  // Positioning
  if (col.pinned !== undefined && col.pinned !== null) {
    customization.pinned = col.pinned;
  }

  // Cell style
  if (col.cellStyle) {
    if (typeof col.cellStyle === 'function') {
      // Check if it has our metadata
      const metadata = (col.cellStyle as any).__formatString;
      const baseStyle = (col.cellStyle as any).__baseStyle;
      console.log('[ColumnSerializer] Saving cellStyle function:', {
        field: col.field,
        hasMetadata: !!metadata,
        formatString: metadata,
        hasBaseStyle: !!baseStyle,
        baseStyle
      });
      customization.cellStyle = {
        type: 'function',
        formatString: metadata,
        baseStyle: baseStyle
      };
    } else {
      customization.cellStyle = {
        type: 'static',
        value: col.cellStyle as React.CSSProperties
      };
    }
  }

  // Header style
  if (col.headerStyle) {
    if (typeof col.headerStyle === 'function') {
      // Try to extract the style config if it's our custom format
      const testRegular = (col.headerStyle as any)({ floatingFilter: false });
      const testFloating = (col.headerStyle as any)({ floatingFilter: true });

      customization.headerStyle = {
        type: 'function',
        regular: testRegular || undefined,
        floating: testFloating || undefined
      };
    } else if (typeof col.headerStyle === 'object') {
      const styleObj = col.headerStyle as any;
      if (styleObj._isHeaderStyleConfig) {
        customization.headerStyle = {
          type: 'function',
          regular: styleObj.regular,
          floating: styleObj.floating
        };
      } else {
        customization.headerStyle = {
          type: 'static',
          regular: col.headerStyle as React.CSSProperties
        };
      }
    }
  }

  // CSS Classes
  if (col.cellClass) {
    customization.cellClass = col.cellClass;
  }
  if (col.headerClass) {
    customization.headerClass = col.headerClass;
  }

  // Value formatter
  if (col.valueFormatter) {
    console.log('[ColumnSerializer] Processing valueFormatter for field:', col.field, {
      type: typeof col.valueFormatter,
      isFunction: typeof col.valueFormatter === 'function'
    });

    if (typeof col.valueFormatter === 'function') {
      const formatString = (col.valueFormatter as any).__formatString;
      const formatterType = (col.valueFormatter as any).__formatterType;
      const visualRules = (col.valueFormatter as any).__visualRules;
      const visualDefaultFallback = (col.valueFormatter as any).__visualDefaultFallback;

      console.log('[ColumnSerializer] Function formatter metadata:', {
        field: col.field,
        hasFormatString: !!formatString,
        formatString: formatString,
        formatterType: formatterType,
        hasVisualRules: !!visualRules,
        hasDefaultFallback: !!visualDefaultFallback
      });

      if (formatString) {
        customization.valueFormatter = {
          type: formatterType || 'excel',
          formatString,
          // Include visual editor data if available
          ...(visualRules && { rules: visualRules }),
          ...(visualDefaultFallback && { defaultFallback: visualDefaultFallback })
        };
        console.log('[ColumnSerializer]  Serialized formatter for field:', col.field);
      } else {
        console.warn('[ColumnSerializer]  Formatter function has no __formatString metadata for field:', col.field);
        // Try to serialize as custom formatter without format string
        customization.valueFormatter = {
          type: 'custom',
          formatString: `// Custom formatter function for ${col.field}\n// (Function body not serializable)`
        };
        console.log('[ColumnSerializer]  Serialized as custom formatter without metadata for field:', col.field);
      }
    } else if (typeof col.valueFormatter === 'object') {
      // Handle saved formatter config
      const config = col.valueFormatter as Record<string, unknown>;
      console.log('[ColumnSerializer] Object formatter config:', config);

      if (config._isFormatterConfig) {
        customization.valueFormatter = {
          type: (config.type as 'excel' | 'visual' | 'custom') || 'excel',
          formatString: config.formatString as string,
          ...(config.rules && { rules: config.rules as SerializedFormattingRule[] }),
          ...(config.defaultFallback && { defaultFallback: config.defaultFallback as SerializedDefaultFallback })
        };
        console.log('[ColumnSerializer]  Serialized object formatter for field:', col.field);
      } else {
        console.warn('[ColumnSerializer]  Object formatter is not a recognized config for field:', col.field);
      }
    } else {
      console.warn('[ColumnSerializer]  Unknown formatter type for field:', col.field, typeof col.valueFormatter);
    }
  }

  // Cell data type
  if (col.cellDataType && col.cellDataType !== baseCol?.cellDataType) {
    customization.cellDataType = col.cellDataType;
  }

  // Other properties
  if (col.headerTooltip) customization.headerTooltip = col.headerTooltip;
  if (col.tooltipField) customization.tooltipField = col.tooltipField;
  if (col.headerCheckboxSelection !== undefined) customization.headerCheckboxSelection = col.headerCheckboxSelection;
  if (col.checkboxSelection !== undefined) customization.checkboxSelection = col.checkboxSelection;
  if (col.rowGroup !== undefined) customization.rowGroup = col.rowGroup;
  if (col.pivot !== undefined) customization.pivot = col.pivot;
  if (col.aggFunc !== undefined) customization.aggFunc = col.aggFunc;
  if (col.cellClassRules) customization.cellClassRules = col.cellClassRules;

  return customization;
}

/**
 * Serialize column customizations - only saves properties that differ from defaults
 */
export function serializeColumnCustomizations(
  columns: ColDef[], 
  baseColumns?: ColDef[]
): Record<string, ColumnCustomization> {
  const customizations: Record<string, ColumnCustomization> = {};

  columns.forEach((col, _index) => {
    if (!col.field) return;

    const baseCol = baseColumns?.find(base => base.field === col.field);
    const extracted = extractCustomizations(col, baseCol);

    // Only include if there are actual customizations beyond the field
    if (Object.keys(extracted).length > 1) {
      customizations[col.field] = extracted;
    }
  });

  return customizations;
}


/**
 * Ensure a column has a cellStyle function if its valueFormatter has conditional styling
 */
function ensureCellStyleForConditionalFormatting(merged: ColDef, _custom: ColumnCustomization): void {
  // Check if valueFormatter has conditional styling
  if (merged.valueFormatter && typeof merged.valueFormatter === 'function') {
    const formatString = (merged.valueFormatter as any).__formatString;

    if (formatString && hasConditionalStyling(formatString)) {
      // Check if cellStyle already exists and is properly configured
      if (!merged.cellStyle || 
          (typeof merged.cellStyle === 'function' && 
           (merged.cellStyle as any).__formatString !== formatString)) {

        console.log('[ColumnSerializer] Creating cellStyle for conditional formatting:', {
          field: merged.field,
          formatString,
          hasExistingCellStyle: !!merged.cellStyle,
          existingCellStyleType: typeof merged.cellStyle
        });

        // Extract base style if it exists
        let baseStyle: React.CSSProperties = {};
        if (merged.cellStyle) {
          if (typeof merged.cellStyle === 'object') {
            baseStyle = merged.cellStyle;
          } else if (typeof merged.cellStyle === 'function' && (merged.cellStyle as any).__baseStyle) {
            baseStyle = (merged.cellStyle as any).__baseStyle;
          }
        }

        // Create cellStyle function
        const styleFunc = createCellStyleFunction(formatString, baseStyle);

        // Attach metadata for future serialization
        Object.defineProperty(styleFunc, '__formatString', { 
          value: formatString, 
          writable: false,
          enumerable: false,
          configurable: true
        });
        Object.defineProperty(styleFunc, '__baseStyle', { 
          value: baseStyle, 
          writable: false,
          enumerable: false,
          configurable: true
        });

        merged.cellStyle = styleFunc;
      }
    }
  }
}

/**
 * Apply customizations to base columns
 */
export function deserializeColumnCustomizations(
  customizations: Record<string, ColumnCustomization>,
  baseColumns: ColDef[]
): ColDef[] {
  // Handle case where baseColumns is empty
  if (!baseColumns || baseColumns.length === 0) {
    console.warn('[ColumnSerializer] No base columns provided for deserialization');
    return [];
  }

  return baseColumns.map(baseCol => {
    const field = baseCol.field;
    if (!field || !customizations[field]) {
      // Apply defaults even if no customizations
      return {
        ...baseCol,
        ...COLUMN_DEFAULTS
      };
    }

    const custom = customizations[field];
    // Start with base column merged with defaults
    const merged: ColDef = { 
      ...COLUMN_DEFAULTS,
      ...baseCol 
    };

    // Apply basic properties
    if (custom.headerName !== undefined) merged.headerName = custom.headerName;
    if (custom.width !== undefined) merged.width = custom.width;
    if (custom.minWidth !== undefined) merged.minWidth = custom.minWidth;
    if (custom.maxWidth !== undefined) merged.maxWidth = custom.maxWidth;
    if (custom.flex !== undefined) merged.flex = custom.flex;

    // Apply boolean flags
    if (custom.sortable !== undefined) merged.sortable = custom.sortable;
    if (custom.resizable !== undefined) merged.resizable = custom.resizable;
    if (custom.editable !== undefined) merged.editable = custom.editable;
    if (custom.filter !== undefined) merged.filter = custom.filter;
    if (custom.floatingFilter !== undefined) merged.floatingFilter = custom.floatingFilter;
    if (custom.hide !== undefined) merged.hide = custom.hide;
    if (custom.lockPosition !== undefined) merged.lockPosition = custom.lockPosition;
    if (custom.lockVisible !== undefined) merged.lockVisible = custom.lockVisible;
    if (custom.lockPinned !== undefined) merged.lockPinned = custom.lockPinned;
    if (custom.suppressHeaderMenuButton !== undefined) merged.suppressHeaderMenuButton = custom.suppressHeaderMenuButton;

    // Apply positioning
    if (custom.pinned !== undefined) merged.pinned = custom.pinned;

    // Apply cell style
    if (custom.cellStyle) {
      if (custom.cellStyle.type === 'static' && custom.cellStyle.value) {
        merged.cellStyle = custom.cellStyle.value;
      } else if (custom.cellStyle.type === 'function' && custom.cellStyle.formatString) {
        console.log('[ColumnSerializer] Loading cellStyle function:', {
          field,
          formatString: custom.cellStyle.formatString,
          hasBaseStyle: !!custom.cellStyle.baseStyle,
          baseStyle: custom.cellStyle.baseStyle
        });
        // Recreate the cell style function
        const styleFunc = createCellStyleFunction(custom.cellStyle.formatString, custom.cellStyle.baseStyle);
        // Attach metadata for future serialization
        Object.defineProperty(styleFunc, '__formatString', { 
          value: custom.cellStyle.formatString, 
          writable: false,
          enumerable: false,
          configurable: true
        });
        Object.defineProperty(styleFunc, '__baseStyle', { 
          value: custom.cellStyle.baseStyle, 
          writable: false,
          enumerable: false,
          configurable: true
        });
        merged.cellStyle = styleFunc;
      }
    }

    // Apply header style
    if (custom.headerStyle) {
      if (custom.headerStyle.type === 'static' && custom.headerStyle.regular) {
        merged.headerStyle = custom.headerStyle.regular;
      } else if (custom.headerStyle.type === 'function') {
        // Create the header style function
        merged.headerStyle = (params: { floatingFilter?: boolean }) => {
          if (params?.floatingFilter) {
            return custom.headerStyle?.floating || null;
          }
          return custom.headerStyle?.regular || null;
        };

        // Also store as object format for persistence
        (merged.headerStyle as any)._isHeaderStyleConfig = true;
        (merged.headerStyle as any).regular = custom.headerStyle.regular;
        (merged.headerStyle as any).floating = custom.headerStyle.floating;
      }
    }

    // Apply CSS classes
    if (custom.cellClass !== undefined) merged.cellClass = custom.cellClass;
    if (custom.headerClass !== undefined) merged.headerClass = custom.headerClass;

    // Apply value formatter
    if (custom.valueFormatter && custom.valueFormatter.formatString) {
      if (custom.valueFormatter.type === 'excel' || custom.valueFormatter.type === 'visual') {
        console.log('[ColumnSerializer] Recreating formatter for field:', field, {
          formatString: custom.valueFormatter.formatString,
          type: custom.valueFormatter.type,
          hasRules: !!custom.valueFormatter.rules,
          hasDefaultFallback: !!custom.valueFormatter.defaultFallback
        });
        const formatter = createExcelFormatter(custom.valueFormatter.formatString);

        // Attach visual editor metadata if available
        if (custom.valueFormatter.rules) {
          Object.defineProperty(formatter, '__visualRules', { 
            value: custom.valueFormatter.rules, 
            writable: false,
            enumerable: false,
            configurable: true
          });
        }

        if (custom.valueFormatter.defaultFallback) {
          Object.defineProperty(formatter, '__visualDefaultFallback', { 
            value: custom.valueFormatter.defaultFallback, 
            writable: false,
            enumerable: false,
            configurable: true
          });
        }

        merged.valueFormatter = formatter;
        // Value formatter will be used for export automatically
      }
    }

    // Apply cell data type
    if (custom.cellDataType !== undefined) merged.cellDataType = custom.cellDataType;

    // Apply other properties
    if (custom.headerTooltip !== undefined) merged.headerTooltip = custom.headerTooltip;
    if (custom.tooltipField !== undefined) merged.tooltipField = custom.tooltipField;
    if (custom.headerCheckboxSelection !== undefined) merged.headerCheckboxSelection = custom.headerCheckboxSelection;
    if (custom.checkboxSelection !== undefined) merged.checkboxSelection = custom.checkboxSelection;
    if (custom.rowGroup !== undefined) merged.rowGroup = custom.rowGroup;
    if (custom.pivot !== undefined) merged.pivot = custom.pivot;
    if (custom.aggFunc !== undefined) merged.aggFunc = custom.aggFunc;
    if (custom.cellClassRules !== undefined) merged.cellClassRules = custom.cellClassRules;

    // Ensure cellStyle is created if valueFormatter has conditional styling
    ensureCellStyleForConditionalFormatting(merged, custom);

    return merged;
  });
}

/**
 * Calculate the size of serialized customizations in bytes
 */
export function calculateCustomizationSize(customizations: Record<string, ColumnCustomization>): number {
  return JSON.stringify(customizations).length;
}

/**
 * Get a summary of what's customized
 */
export function getCustomizationSummary(customizations: Record<string, ColumnCustomization>): {
  totalColumns: number;
  customizedColumns: number;
  properties: Record<string, number>;
} {
  const propertyCount: Record<string, number> = {};

  Object.values(customizations).forEach(custom => {
    Object.keys(custom).forEach(key => {
      if (key !== 'field') {
        propertyCount[key] = (propertyCount[key] || 0) + 1;
      }
    });
  });

  return {
    totalColumns: Object.keys(customizations).length,
    customizedColumns: Object.keys(customizations).length,
    properties: propertyCount
  };
}

import { useProfileStore } from './profile.store';
import { logStorageAnalysis } from './storage-analyzer';

/**
 * Migrate all profiles to use lightweight column serialization
 * This will convert all existing profiles that still use full columnDefs
 * to the new lightweight format
 */
export function migrateAllProfilesToLightweight() {
  const store = useProfileStore.getState();
  const profiles = store.profiles;

  console.group(' Migrating profiles to lightweight format');

  let migratedCount = 0;

  profiles.forEach(profile => {
    // Check if profile has full columnDefs but no lightweight format
    if (profile.gridState.columnDefs && 
        profile.gridState.columnDefs.length > 0 && 
        !profile.gridState.columnCustomizations) {

      console.log(` Migrating profile: ${profile.name} (${profile.id})`);

      // Get the column definitions using the store method (handles all formats)
      const columnDefs = store.getColumnDefs(profile.id);

      if (columnDefs) {
        // Save using the lightweight format
        // We need to temporarily set this as the active profile to use saveColumnCustomizations
        const currentActiveId = store.activeProfileId;
        store.setActiveProfile(profile.id);

        // Save the columns in lightweight format
        store.saveColumnCustomizations(columnDefs, columnDefs);

        // Restore the previous active profile
        store.setActiveProfile(currentActiveId);

        migratedCount++;
        console.log(` Successfully migrated profile: ${profile.name}`);
      }
    } else if (profile.gridState.columnCustomizations) {
      console.log(`  Profile already using lightweight format: ${profile.name}`);
    } else {
      console.log(`  Profile has no column definitions: ${profile.name}`);
    }
  });

  console.log(`\n Migration complete: ${migratedCount} profiles migrated`);
  console.groupEnd();

  // Show storage analysis after migration
  console.log('\n');
  logStorageAnalysis();
}

/**
 * Force refresh all profiles to ensure they're using the latest format
 * This will reload column definitions and re-save them
 */
export function refreshAllProfiles() {
  const store = useProfileStore.getState();
  const profiles = store.profiles;
  const currentActiveId = store.activeProfileId;

  console.group(' Refreshing all profiles');

  profiles.forEach(profile => {
    const columnDefs = store.getColumnDefs(profile.id);
    if (columnDefs && columnDefs.length > 0) {
      console.log(` Refreshing profile: ${profile.name}`);

      // Temporarily switch to this profile
      store.setActiveProfile(profile.id);

      // Re-save with current format
      store.saveColumnCustomizations(columnDefs, profile.gridState.baseColumnDefs || columnDefs);

      console.log(` Refreshed: ${profile.name}`);
    }
  });

  // Restore original active profile
  store.setActiveProfile(currentActiveId);

  console.groupEnd();
}

// Export functions to window for easy console access
if (typeof window !== 'undefined') {
  (window as any).migrateProfiles = migrateAllProfilesToLightweight;
  (window as any).refreshProfiles = refreshAllProfiles;
}

import { GridProfile } from './profile.store';
import { calculateCustomizationSize, getCustomizationSummary } from './column-serializer';

export interface StorageAnalysis {
  totalProfiles: number;
  totalSizeKB: number;
  profileAnalysis: {
    profileId: string;
    profileName: string;
    legacySizeKB: number;
    lightweightSizeKB: number;
    savingsKB: number;
    savingsPercent: number;
    customizationSummary?: ReturnType<typeof getCustomizationSummary>;
  }[];
  overallSavings: {
    totalLegacySizeKB: number;
    totalLightweightSizeKB: number;
    totalSavingsKB: number;
    totalSavingsPercent: number;
  };
}

export function analyzeProfileStorage(): StorageAnalysis {
  const storageKey = 'grid-profile-storage';
  const stored = localStorage.getItem(storageKey);

  if (!stored) {
    return {
      totalProfiles: 0,
      totalSizeKB: 0,
      profileAnalysis: [],
      overallSavings: {
        totalLegacySizeKB: 0,
        totalLightweightSizeKB: 0,
        totalSavingsKB: 0,
        totalSavingsPercent: 0
      }
    };
  }

  const parsedData = JSON.parse(stored);
  const profiles: GridProfile[] = parsedData.state?.profiles || [];

  const profileAnalysis = profiles.map(profile => {
    // Calculate legacy size (full columnDefs)
    const legacySize = profile.gridState.columnDefs 
      ? JSON.stringify(profile.gridState.columnDefs).length / 1024
      : 0;

    // Calculate lightweight size
    const lightweightSize = profile.gridState.columnCustomizations
      ? calculateCustomizationSize(profile.gridState.columnCustomizations) / 1024
      : 0;

    // Get customization summary if using lightweight format
    const customizationSummary = profile.gridState.columnCustomizations
      ? getCustomizationSummary(profile.gridState.columnCustomizations)
      : undefined;

    const _actualSize = profile.gridState.columnCustomizations ? lightweightSize : legacySize;
    const potentialSavings = legacySize > 0 ? legacySize - lightweightSize : 0;
    const savingsPercent = legacySize > 0 ? (potentialSavings / legacySize) * 100 : 0;

    return {
      profileId: profile.id,
      profileName: profile.name,
      legacySizeKB: legacySize,
      lightweightSizeKB: lightweightSize,
      savingsKB: potentialSavings,
      savingsPercent,
      customizationSummary
    };
  });

  const totalLegacySizeKB = profileAnalysis.reduce((sum, p) => sum + p.legacySizeKB, 0);
  const totalLightweightSizeKB = profileAnalysis.reduce((sum, p) => sum + p.lightweightSizeKB, 0);
  const totalSavingsKB = totalLegacySizeKB - totalLightweightSizeKB;
  const totalSavingsPercent = totalLegacySizeKB > 0 ? (totalSavingsKB / totalLegacySizeKB) * 100 : 0;

  return {
    totalProfiles: profiles.length,
    totalSizeKB: stored.length / 1024,
    profileAnalysis,
    overallSavings: {
      totalLegacySizeKB,
      totalLightweightSizeKB,
      totalSavingsKB,
      totalSavingsPercent
    }
  };
}

export function logStorageAnalysis() {
  const analysis = analyzeProfileStorage();

  console.group(' Profile Storage Analysis');
  console.log(`Total profiles: ${analysis.totalProfiles}`);
  console.log(`Total storage size: ${analysis.totalSizeKB.toFixed(2)} KB`);

  console.group(' Profile Breakdown');
  analysis.profileAnalysis.forEach(profile => {
    console.group(` ${profile.profileName} (${profile.profileId})`);
    console.log(`Legacy size: ${profile.legacySizeKB.toFixed(2)} KB`);
    console.log(`Lightweight size: ${profile.lightweightSizeKB.toFixed(2)} KB`);
    console.log(`Savings: ${profile.savingsKB.toFixed(2)} KB (${profile.savingsPercent.toFixed(1)}%)`);

    if (profile.customizationSummary) {
      console.group(' Customizations');
      console.log(`Customized columns: ${profile.customizationSummary.customizedColumns}`);
      console.log('Property usage:', profile.customizationSummary.properties);
      console.groupEnd();
    }

    console.groupEnd();
  });
  console.groupEnd();

  console.group(' Overall Savings');
  console.log(`Total legacy size: ${analysis.overallSavings.totalLegacySizeKB.toFixed(2)} KB`);
  console.log(`Total lightweight size: ${analysis.overallSavings.totalLightweightSizeKB.toFixed(2)} KB`);
  console.log(`Total savings: ${analysis.overallSavings.totalSavingsKB.toFixed(2)} KB`);
  console.log(`Savings percentage: ${analysis.overallSavings.totalSavingsPercent.toFixed(1)}%`);
  console.groupEnd();

  console.groupEnd();
}

// Export function to window for easy console access
if (typeof window !== 'undefined') {
  (window as any).analyzeProfileStorage = logStorageAnalysis;
}
