import React, { memo, useMemo, useRef, useState } from 'react';
import { GridApi } from 'ag-grid-community';
import { DataTableProvider } from './DataTableContext';
import { DataTableGrid } from './DataTableGrid';
import { DataTableToolbar } from './DataTableToolbar';
import { ColumnCustomizationDialog } from './dialogs/columnSettings/ColumnCustomizationDialog';
import { FloatingRibbonUI } from './FloatingRibbonUI';
import { useColumnProcessor } from './hooks/useColumnProcessor';
import { useGridState } from './hooks/useGridState';
import { useProfileSync } from './hooks/useProfileSync';
import { useColumnOperations } from './hooks/useColumnOperations';
import { DataTableProps } from './types';
import { useProfileStore } from '@/components/datatable/stores/profile.store';
import { useTheme } from '@/components/datatable/ThemeProvider';
import './datatable.css';

/**
 * Container component that manages the state and logic for the DataTable.
 * This component coordinates all the hooks and provides context to child components.
 */
export const DataTableContainer = memo(({ columnDefs, dataRow }: DataTableProps) => {
  const gridApiRef = useRef<GridApi | null>(null);
  const [gridApi, setGridApi] = useState<GridApi | null>(null);
  const { saveColumnCustomizations } = useProfileStore();
  const { theme } = useTheme();

  // Initialize grid state
  const {
    currentColumnDefs,
    selectedFont,
    showColumnDialog,
    setCurrentColumnDefs,
    setSelectedFont,
    setShowColumnDialog,
  } = useGridState(columnDefs);

  const [ribbonConfig, setRibbonConfig] = useState<{
    targetColumn: string;
    position: { x: number; y: number };
  } | null>(null);

  // Process columns to ensure cellStyle functions for conditional formatting
  // This preserves the CRITICAL ensureCellStyleForColumns functionality
  const processedColumns = useColumnProcessor(currentColumnDefs);

  // Handle profile synchronization
  const { handleProfileChange } = useProfileSync(setCurrentColumnDefs, setSelectedFont);

  // Handle column operations
  const { handleApplyColumnChanges, getColumnDefsWithStyles } = useColumnOperations(
    gridApiRef,
    setCurrentColumnDefs
  );

  // Initialize default profile if needed
  React.useEffect(() => {
    const activeProfile = useProfileStore.getState().getActiveProfile();
    if (activeProfile && activeProfile.id === 'default-profile' && 
        !activeProfile.gridState.columnCustomizations && 
        (!activeProfile.gridState.columnDefs || activeProfile.gridState.columnDefs.length === 0)) {
      console.log('[DataTableContainer] Initializing default profile with base columnDefs');
      // Pass columnDefs as both current and base since this is the initial setup
      // The first parameter is current state, second is the original base columns
      saveColumnCustomizations(columnDefs, columnDefs);
    }
  }, [columnDefs, saveColumnCustomizations]);

  // Handle font changes
  const handleFontChange = React.useCallback((font: string) => {
    setSelectedFont(font);
    if (gridApiRef.current) {
      gridApiRef.current.refreshCells({ force: true });
    }
  }, [setSelectedFont]);

  // Refresh cells when theme changes to update conditional formatting colors
  React.useEffect(() => {
    if (gridApiRef.current || gridApi) {
      // Small delay to ensure DOM classes are updated
      setTimeout(() => {
        gridApiRef.current?.refreshCells({ force: true });
        gridApi?.refreshCells({ force: true });
      }, 50);
    }
  }, [theme, gridApi]);

  // Listen for format-column events from context menu
  React.useEffect(() => {
    const handleFormatColumn = (event: Event) => {
      const customEvent = event as CustomEvent;
      const { colId, x, y } = customEvent.detail;

      // Position ribbon near the clicked column but ensure it's visible
      const ribbonWidth = 450;
      const ribbonHeight = 400;
      const padding = 20;

      const adjustedX = Math.min(x, window.innerWidth - ribbonWidth - padding);
      const adjustedY = Math.min(y, window.innerHeight - ribbonHeight - padding);

      setRibbonConfig({
        targetColumn: colId,
        position: { x: Math.max(padding, adjustedX), y: Math.max(padding, adjustedY) }
      });
    };

    const handleOpenColumnSettings = (_event: Event) => {
      // const customEvent = event as CustomEvent;
      // const { colId } = customEvent.detail; // TODO: Use for pre-selecting column

      // TODO: Open column settings dialog with specific column pre-selected
      // For now, just open the general dialog
      setShowColumnDialog(true);
    };

    window.addEventListener('format-column', handleFormatColumn);
    window.addEventListener('open-column-settings', handleOpenColumnSettings);

    return () => {
      window.removeEventListener('format-column', handleFormatColumn);
      window.removeEventListener('open-column-settings', handleOpenColumnSettings);
    };
  }, []);

  // Get column state for dialog
  const getColumnState = React.useCallback(() => {
    if (!gridApiRef.current) return undefined;

    const columnState = gridApiRef.current.getColumnState();
    const allColumns = gridApiRef.current.getColumns();

    console.log('[DataTableContainer] Getting column state for dialog:', {
      columnStateLength: columnState?.length,
      allColumnsLength: allColumns?.length,
      visibleColumns: allColumns?.filter(col => col.isVisible()).length,
      hiddenColumns: allColumns?.filter(col => !col.isVisible()).length
    });

    // Create a complete state by merging column state with actual column visibility
    if (allColumns) {
      const completeState = allColumns.map(column => {
        const colId = column.getColId();
        const existingState = columnState?.find(cs => cs.colId === colId);

        return {
          colId: colId,
          hide: !column.isVisible(),
          ...existingState
        };
      });

      return completeState;
    }

    return columnState;
  }, []);

  // Create context value
  const contextValue = useMemo(() => ({
    processedColumns,
    selectedFont,
    handleFontChange,
    showColumnDialog,
    setShowColumnDialog,
    gridApiRef,
    getColumnDefsWithStyles,
    setGridApi,
  }), [
    processedColumns,
    selectedFont,
    handleFontChange,
    showColumnDialog,
    setShowColumnDialog,
    getColumnDefsWithStyles,
  ]);

  return (
    <DataTableProvider value={contextValue}>
      <div className="h-full w-full flex flex-col overflow-hidden">
        <DataTableToolbar
          onFontChange={handleFontChange}
          onSpacingChange={() => {}} // Empty function to satisfy prop requirements
          onOpenColumnSettings={() => setShowColumnDialog(true)}
          gridApi={gridApi}
          onProfileChange={handleProfileChange}
          getColumnDefsWithStyles={getColumnDefsWithStyles}
        />

        <DataTableGrid
          columnDefs={processedColumns}
          rowData={dataRow}
          gridApiRef={gridApiRef}
        />

        <ColumnCustomizationDialog
          open={showColumnDialog}
          onOpenChange={setShowColumnDialog}
          columnDefs={processedColumns}
          columnState={getColumnState()}
          onApply={handleApplyColumnChanges}
        />

        {/* Show FloatingRibbonUI when ribbon is invoked from context menu */}
        {ribbonConfig && (
          <FloatingRibbonUI 
            targetColumn={ribbonConfig.targetColumn}
            initialPosition={ribbonConfig.position}
            onClose={() => setRibbonConfig(null)}
          />
        )}
      </div>
    </DataTableProvider>
  );
});

DataTableContainer.displayName = 'DataTableContainer';

import React, { createContext } from 'react';
import { DataTableContextValue } from './types';

export const DataTableContext = createContext<DataTableContextValue | undefined>(undefined);

export const DataTableProvider: React.FC<{
  children: React.ReactNode;
  value: DataTableContextValue;
}> = ({ children, value }) => {
  return (
    <DataTableContext.Provider value={value}>
      {children}
    </DataTableContext.Provider>
  );
};

import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { Settings2, Download, FileSpreadsheet } from "lucide-react";
import { ProfileManager } from "./ProfileManager";
import { GridApi, ColDef as AgColDef, ProcessCellForExportParams } from "ag-grid-community";
import { GridProfile } from "@/components/datatable/stores/profile.store";
import { useToast } from "@/hooks/use-toast";

const monospaceFonts = [
  { value: 'JetBrains Mono', label: 'JetBrains Mono' },
  { value: 'Fira Code', label: 'Fira Code' },
  { value: 'Source Code Pro', label: 'Source Code Pro' },
  { value: 'IBM Plex Mono', label: 'IBM Plex Mono' },
  { value: 'Roboto Mono', label: 'Roboto Mono' },
  { value: 'Monaco', label: 'Monaco' },
  { value: 'Consolas', label: 'Consolas' },
  { value: 'Courier New', label: 'Courier New' },
  { value: 'monospace', label: 'System Monospace' },
];

interface DataTableToolbarProps {
  onFontChange: (font: string) => void;
  onSpacingChange: (spacing: string) => void;
  onOpenColumnSettings?: () => void;
  gridApi?: GridApi | null;
  onProfileChange?: (profile: GridProfile) => void;
  getColumnDefsWithStyles?: () => AgColDef[];
}

export function DataTableToolbar({ 
  onFontChange, 
  onOpenColumnSettings,
  gridApi,
  onProfileChange,
  getColumnDefsWithStyles 
}: DataTableToolbarProps) {
  const { toast } = useToast();

  const handleExportExcel = () => {
    if (!gridApi) {
      toast({
        title: "Export failed",
        description: "Grid is not ready for export",
        variant: "destructive"
      });
      return;
    }

    try {
      gridApi.exportDataAsExcel({
        fileName: `data-export-${new Date().toISOString().split('T')[0]}.xlsx`,
        author: 'AG-Grid Export',
        sheetName: 'Data',
        processCellCallback: (params: ProcessCellForExportParams) => {
          const colDef = params.column.getColDef();
          // Use valueFormatter for export if available
          if (colDef.valueFormatter && typeof colDef.valueFormatter === 'function') {
            return colDef.valueFormatter({
              value: params.value,
              data: params.node?.data,
              node: params.node as any,
              colDef: colDef,
              column: params.column,
              api: params.api,
              context: params.context
            } as any);
          }
          return params.value;
        }
      });

      toast({
        title: "Export successful",
        description: "Data exported to Excel file",
      });
    } catch {
      toast({
        title: "Export failed",
        description: "An error occurred while exporting data",
        variant: "destructive"
      });
    }
  };

  const handleExportCsv = () => {
    if (!gridApi) {
      toast({
        title: "Export failed",
        description: "Grid is not ready for export",
        variant: "destructive"
      });
      return;
    }

    try {
      gridApi.exportDataAsCsv({
        fileName: `data-export-${new Date().toISOString().split('T')[0]}.csv`,
        processCellCallback: (params: ProcessCellForExportParams) => {
          const colDef = params.column.getColDef();
          // Use valueFormatter for export if available
          if (colDef.valueFormatter && typeof colDef.valueFormatter === 'function') {
            return colDef.valueFormatter({
              value: params.value,
              data: params.node?.data,
              node: params.node as any,
              colDef: colDef,
              column: params.column,
              api: params.api,
              context: params.context
            } as any);
          }
          return params.value;
        }
      });

      toast({
        title: "Export successful",
        description: "Data exported to CSV file",
      });
    } catch {
      toast({
        title: "Export failed",
        description: "An error occurred while exporting data",
        variant: "destructive"
      });
    }
  };
  return (
    <div className="flex items-center justify-between p-4 border-b bg-muted/40">
      <div className="flex items-center gap-4">
        <ProfileManager 
          gridApi={gridApi || null} 
          onProfileChange={onProfileChange}
          getColumnDefsWithStyles={getColumnDefsWithStyles}
        />
        <div className="border-l pl-4 flex items-center gap-2">
          <label htmlFor="font-select" className="text-sm font-medium">
            Font:
          </label>
          <Select onValueChange={onFontChange} defaultValue="monospace">
            <SelectTrigger id="font-select" className="w-[180px] h-8">
              <SelectValue placeholder="Select font" />
            </SelectTrigger>
            <SelectContent>
              {monospaceFonts.map((font) => (
                <SelectItem key={font.value} value={font.value}>
                  <span style={{ fontFamily: font.value }}>{font.label}</span>
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="flex items-center gap-2">
        <Button 
          variant="outline" 
          size="sm" 
          onClick={handleExportExcel}
          className="h-8"
          disabled={!gridApi}
        >
          <FileSpreadsheet className="mr-2 h-4 w-4" />
          Excel
        </Button>
        <Button 
          variant="outline" 
          size="sm" 
          onClick={handleExportCsv}
          className="h-8"
          disabled={!gridApi}
        >
          <Download className="mr-2 h-4 w-4" />
          CSV
        </Button>
        {onOpenColumnSettings && (
          <Button 
            variant="outline" 
            size="sm" 
            onClick={onOpenColumnSettings}
            className="h-8"
          >
            <Settings2 className="mr-2 h-4 w-4" />
            Customize Columns
          </Button>
        )}
      </div>
    </div>
  );
}

import { useProfileStore } from '@/components/datatable/stores/profile.store';
import { Button } from '@/components/ui/button';

export function DebugProfile() {
  const { profiles, activeProfileId } = useProfileStore();
  const activeProfile = profiles.find(p => p.id === activeProfileId);

  const debugProfile = () => {
    console.log('=== PROFILE DEBUG ===');
    console.log('Active Profile:', activeProfile);
    console.log('Active Profile ID:', activeProfileId);

    if (activeProfile?.gridState.columnDefs) {
      console.log('Column Definitions Count:', activeProfile.gridState.columnDefs.length);

      // Show first 5 columns in detail
      const columnsWithCustomizations = activeProfile.gridState.columnDefs.filter(col => {
        const customProps = Object.keys(col).filter(key => 
          !['field', 'headerName', 'width', 'hide', 'colId', 'sort', 'sortIndex'].includes(key)
        );
        return customProps.length > 0;
      });

      console.log('Columns with customizations:', columnsWithCustomizations.length);

      columnsWithCustomizations.slice(0, 5).forEach((col, idx) => {
        console.log(`Customized Column ${idx}:`, {
          field: col.field,
          headerName: col.headerName,
          hasValueFormatter: !!col.valueFormatter,
          hasCellStyle: !!col.cellStyle,
          hasCellClass: !!col.cellClass,
          hasHeaderClass: !!col.headerClass,
          hasHeaderStyle: !!col.headerStyle,
          hasFilter: !!col.filter,
          cellClass: col.cellClass,
          headerClass: col.headerClass,
          cellStyle: col.cellStyle,
          headerStyle: col.headerStyle,
          cellStyleType: typeof col.cellStyle,
          headerStyleType: typeof col.headerStyle,
          customProperties: Object.keys(col).filter(key => 
            !['field', 'headerName', 'width', 'hide', 'colId', 'sort', 'sortIndex'].includes(key)
          )
        });
      });
    }

    // Check localStorage directly
    const storedData = localStorage.getItem('grid-profile-storage');
    if (storedData) {
      const parsed = JSON.parse(storedData);
      console.log('Raw localStorage data:', parsed);

      // Check specific profile
      const profile = parsed.state.profiles.find((p: any) => p.id === activeProfileId);
      if (profile?.gridState?.columnDefs) {
        const firstColWithStyle = profile.gridState.columnDefs.find((col: any) => col.cellStyle || col.headerStyle);
        if (firstColWithStyle) {
          console.log('First column with styles from localStorage:', {
            field: firstColWithStyle.field,
            cellStyle: firstColWithStyle.cellStyle,
            headerStyle: firstColWithStyle.headerStyle,
            cellClass: firstColWithStyle.cellClass,
            headerClass: firstColWithStyle.headerClass
          });

          // Show the actual headerStyle content
          if (firstColWithStyle.headerStyle) {
            console.log('HeaderStyle details:', firstColWithStyle.headerStyle);
          }
        }
        console.log('First column full data:', profile.gridState.columnDefs[0]);
      }
    }
  };

  return (
    <div className="fixed bottom-4 right-4 z-50">
      <Button onClick={debugProfile} variant="outline" size="sm">
        Debug Profile
      </Button>
    </div>
  );
}

import { lazy, useEffect } from 'react';
import { Skeleton } from '@/components/ui/skeleton';

// Lazy load AG-Grid modules
const loadAgGridModules = () => {
  // perfMonitor.mark('ag-grid-load-start');

  return Promise.all([
    import('ag-grid-community'),
    import('ag-grid-enterprise'),
    import('ag-grid-react')
  ]).then(([community, enterprise, react]) => {
    // Register modules
    community.ModuleRegistry.registerModules([enterprise.AllEnterpriseModule]);

    // perfMonitor.mark('ag-grid-load-end');
    // perfMonitor.measure('agGridLoadTime', 'ag-grid-load-start', 'ag-grid-load-end');

    return {
      ...community,
      ...enterprise,
      AgGridReact: react.AgGridReact
    };
  });
};

