export function FormatTab({ uiMode = 'simple' }: FormatTabProps) {
  const {
    selectedColumns,
    columnDefinitions,
    pendingChanges,
    updateBulkProperties,
    updateSingleProperty,
    updateBulkProperty,
  } = useColumnCustomizationStore();

  const [showFormatterEditor, setShowFormatterEditor] = useState(false);
  const [selectedTemplate, setSelectedTemplate] = useState<string | null>(null);
  const [customFormat, setCustomFormat] = useState('');
  const [activeTab, setActiveTab] = useState('templates');

  const isDisabled = selectedColumns.size === 0;
  const isMultipleSelection = selectedColumns.size > 1;

  // Get current format for single selection
  const currentFormat = useMemo(() => {
    if (selectedColumns.size !== 1) return null;

    const colId = Array.from(selectedColumns)[0];
    const colDef = columnDefinitions.get(colId);
    const pendingChange = pendingChanges.get(colId);
    const formatter = pendingChange?.valueFormatter || colDef?.valueFormatter;

    if (formatter && typeof formatter === 'function') {
      return (formatter as any).__formatString || null;
    }
    return null;
  }, [selectedColumns, columnDefinitions, pendingChanges]);

  // Apply format template
  const applyFormat = useCallback(async (formatString: string) => {
    if (!formatString || selectedColumns.size === 0) return;

    try {
      const formatter = createExcelFormatter(formatString);
      const newFormatClasses = getExcelStyleClass(formatString);

      // Check if format string has conditional styling and create cellStyle if needed
      console.log('[applyFormat] Checking if cellStyle needed for format:', formatString);
      let cellStyle = undefined;
      if (hasConditionalStyling(formatString)) {
        console.log('[applyFormat] Creating cellStyle for conditional formatting');

        // Get existing base styles from ALL selected columns to preserve styling tab settings
        // We need to check each column individually since they might have different styles
        const cellStyleUpdates: Record<string, any> = {};

        selectedColumns.forEach(colId => {
          const colDef = columnDefinitions.get(colId);
          const pendingChange = pendingChanges.get(colId);
          const existingCellStyle = pendingChange?.cellStyle || colDef?.cellStyle;

          let baseStyle: React.CSSProperties | undefined;
          if (existingCellStyle) {
            if (typeof existingCellStyle === 'object') {
              // Direct style object from styling tab
              baseStyle = existingCellStyle;
            } else if (typeof existingCellStyle === 'function') {
              // Function style - check for base style metadata
              const metadata = (existingCellStyle as any).__baseStyle;
              if (metadata) {
                baseStyle = metadata;
              } else {
                // Try calling the function to see if it returns static styles
                // This handles the case where styling tab created a function that returns static styles
                const testStyle = existingCellStyle({ value: null });
                if (testStyle && typeof testStyle === 'object') {
                  baseStyle = testStyle;
                }
              }
            }
          }

          console.log(`[applyFormat] Column ${colId} base style:`, baseStyle);
          cellStyleUpdates[colId] = createCellStyleFunction(formatString, baseStyle);
        });

        // Check if all columns have the same base style
        const uniqueStyles = new Set(Object.values(cellStyleUpdates).map(fn => {
          const baseStyle = (fn as any).__baseStyle;
          return baseStyle ? JSON.stringify(baseStyle) : 'undefined';
        }));

        if (uniqueStyles.size === 1) {
          // All columns have the same base style, use a single cellStyle function
          cellStyle = Object.values(cellStyleUpdates)[0];
        } else {
          // Different base styles, we'll need to apply them individually
          // Store for later individual application
          properties.cellStyleUpdates = cellStyleUpdates;
        }
      }

      // Preserve existing alignment classes
      const properties: any = {
        valueFormatter: formatter,
        useValueFormatterForExport: true
      };

      if (cellStyle) {
        properties.cellStyle = cellStyle;
      }

      // Handle cellClass updates to preserve alignment
      const cellClassUpdates: Record<string, string> = {};
      selectedColumns.forEach(colId => {
        const colDef = columnDefinitions.get(colId);
        const pendingChange = pendingChanges.get(colId);
        const existingCellClass = pendingChange?.cellClass || colDef?.cellClass || '';

        // Extract alignment classes from existing cellClass (both custom and Tailwind)
        const existingClasses = existingCellClass.split(' ').filter(Boolean);
        const alignmentClasses = existingClasses.filter((cls: string) => 
          // Custom alignment classes
          cls.startsWith('cell-align-') || cls.startsWith('cell-valign-') ||
          // Tailwind alignment classes
          cls === 'text-left' || cls === 'text-center' || cls === 'text-right' ||
          cls === 'justify-start' || cls === 'justify-center' || cls === 'justify-end' ||
          cls === 'items-start' || cls === 'items-center' || cls === 'items-end'
        );

        // Combine new format classes with preserved alignment classes
        const allClasses = [...new Set([...newFormatClasses.split(' '), ...alignmentClasses])];
        cellClassUpdates[colId] = allClasses.filter(Boolean).join(' ');
      });

      // Apply cellClass
      const uniqueCellClasses = new Set(Object.values(cellClassUpdates));
      if (uniqueCellClasses.size === 1) {
        properties.cellClass = Array.from(uniqueCellClasses)[0];
      }

      // Apply properties
      updateBulkProperties(properties);

      // Apply individual cellClass updates if needed
      if (uniqueCellClasses.size > 1) {
        selectedColumns.forEach(colId => {
          updateSingleProperty(colId, 'cellClass', cellClassUpdates[colId]);
        });
      }

      // Apply individual cellStyle updates if needed
      if (properties.cellStyleUpdates) {
        selectedColumns.forEach(colId => {
          updateSingleProperty(colId, 'cellStyle', properties.cellStyleUpdates[colId]);
        });
        delete properties.cellStyleUpdates; // Clean up
      }

      setSelectedTemplate(formatString);
    } catch (error) {
      console.error('Error applying format:', error);
    }
  }, [selectedColumns, updateBulkProperties, updateSingleProperty, columnDefinitions, pendingChanges]);

  // Clear format
  const clearFormat = useCallback(() => {
    if (selectedColumns.size === 0) return;

    // Preserve alignment classes when clearing format
    const cellClassUpdates: Record<string, string | undefined> = {};
    selectedColumns.forEach(colId => {
      const colDef = columnDefinitions.get(colId);
      const pendingChange = pendingChanges.get(colId);
      const existingCellClass = pendingChange?.cellClass || colDef?.cellClass || '';

      // Extract only alignment classes to preserve (both custom and Tailwind)
      const alignmentClasses = existingCellClass.split(' ').filter((cls: string) => 
        // Custom alignment classes
        cls.startsWith('cell-align-') || cls.startsWith('cell-valign-') ||
        // Tailwind alignment classes
        cls === 'text-left' || cls === 'text-center' || cls === 'text-right' ||
        cls === 'justify-start' || cls === 'justify-center' || cls === 'justify-end' ||
        cls === 'items-start' || cls === 'items-center' || cls === 'items-end'
      );

      cellClassUpdates[colId] = alignmentClasses.length > 0 ? alignmentClasses.join(' ') : undefined;
    });

    const properties: any = {
      valueFormatter: undefined,
      cellStyle: undefined
    };

    // Handle cellClass updates
    const uniqueCellClasses = new Set(Object.values(cellClassUpdates));
    if (uniqueCellClasses.size === 1) {
      properties.cellClass = Array.from(uniqueCellClasses)[0];
    }

    updateBulkProperties(properties);

    // Apply individual cellClass updates if needed
    if (uniqueCellClasses.size > 1) {
      selectedColumns.forEach(colId => {
        updateSingleProperty(colId, 'cellClass', cellClassUpdates[colId]);
      });
    }

    setSelectedTemplate(null);
    setCustomFormat('');
  }, [selectedColumns, updateBulkProperties, updateSingleProperty, columnDefinitions, pendingChanges]);

  // Handle custom format input
  const handleCustomFormatApply = useCallback(() => {
    if (customFormat.trim()) {
      applyFormat(customFormat.trim());
    }
  }, [customFormat, applyFormat]);

  // Check if current format matches a template
  const isTemplateSelected = useCallback((format: string) => {
    return currentFormat === format || selectedTemplate === format;
  }, [currentFormat, selectedTemplate]);

  if (isDisabled) {
    return (
      <div className="p-6 text-center text-muted-foreground">
        <Info className="h-12 w-12 mx-auto mb-3 opacity-50" />
        <p>Select one or more columns to apply formatting</p>
      </div>
    );
  }

  return (
    <div className="p-4 space-y-4">
      {/* Header with guidance */}
      <div className="space-y-2">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-medium">Format Cells</h3>
          {currentFormat && (
            <Button
              variant="ghost"
              size="sm"
              onClick={clearFormat}
              className="h-8 text-xs"
            >
              <X className="h-3 w-3 mr-1" />
              Clear Format
            </Button>
          )}
        </div>
        <p className="text-sm text-muted-foreground">
          Choose how to display values in the selected {isMultipleSelection ? 'columns' : 'column'}
        </p>
      </div>

      <Separator />

      {/* Current format display */}
      {currentFormat && !isMultipleSelection && (
        <Alert className="py-2">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription className="text-sm">
            Current format: <code className="ml-1 px-2 py-0.5 bg-muted rounded text-xs">{currentFormat}</code>
          </AlertDescription>
        </Alert>
      )}

      {/* Format options */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="templates">Templates</TabsTrigger>
          <TabsTrigger value="custom">Custom Format</TabsTrigger>
          <TabsTrigger value="advanced">Advanced</TabsTrigger>
        </TabsList>

        {/* Templates Tab */}
        <TabsContent value="templates" className="space-y-4">
          <ScrollArea className="h-[400px] pr-4">
            <div className="space-y-6">
              {Object.entries(FORMAT_TEMPLATES).map(([key, category]) => {
                const Icon = category.icon;
                return (
                  <div key={key} className="space-y-3">
                    <div className="flex items-center gap-2">
                      <Icon className="h-4 w-4 text-muted-foreground" />
                      <h4 className="font-medium text-sm">{category.title}</h4>
                    </div>
                    <div className="grid gap-2">
                      {category.formats.map((format) => (
                        <Card
                          key={format.format}
                          className={`cursor-pointer transition-colors ${
                            isTemplateSelected(format.format) 
                              ? 'border-primary bg-primary/5' 
                              : 'hover:border-muted-foreground/50'
                          }`}
                          onClick={() => applyFormat(format.format)}
                        >
                          <CardContent className="p-3">
                            <div className="flex items-center justify-between">
                              <div className="space-y-1">
                                <p className="text-sm font-medium">{format.label}</p>
                                <p className="text-xs text-muted-foreground font-mono">
                                  {format.format}
                                </p>
                              </div>
                              <div className="text-right">
                                <p className="text-sm font-mono text-muted-foreground">
                                  {format.preview}
                                </p>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  </div>
                );
              })}
            </div>
          </ScrollArea>
        </TabsContent>

        {/* Custom Format Tab */}
        <TabsContent value="custom" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="text-base">Custom Format String</CardTitle>
              <CardDescription>
                Enter an Excel-style format string
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="custom-format">Format String</Label>
                <div className="flex gap-2">
                  <Input
                    id="custom-format"
                    value={customFormat}
                    onChange={(e) => setCustomFormat(e.target.value)}
                    placeholder='e.g., #,##0.00 or "USD" #,##0.00'
                    className="font-mono text-sm"
                  />
                  <Button 
                    onClick={handleCustomFormatApply}
                    disabled={!customFormat.trim()}
                    size="sm"
                  >
                    Apply
                  </Button>
                </div>
              </div>

              {/* Format string examples */}
              <div className="space-y-2">
                <p className="text-sm font-medium text-muted-foreground">Examples:</p>
                <div className="space-y-1 text-xs text-muted-foreground">
                  <div className="flex justify-between font-mono">
                    <span>#,##0.00</span>
                    <span> 1,234.56</span>
                  </div>
                  <div className="flex justify-between font-mono">
                    <span>0.00%</span>
                    <span> 12.34%</span>
                  </div>
                  <div className="flex justify-between font-mono">
                    <span>"USD" #,##0.00</span>
                    <span> USD 1,234.56</span>
                  </div>
                  <div className="flex justify-between font-mono">
                    <span>MM/DD/YYYY</span>
                    <span> 12/31/2023</span>
                  </div>
                </div>
              </div>

              {/* Help tooltip */}
              <Alert>
                <HelpCircle className="h-4 w-4" />
                <AlertDescription className="text-xs">
                  <strong>Format symbols:</strong>
                  <br />
                  # = Optional digit, 0 = Required digit, , = Thousand separator
                  <br />
                  . = Decimal point, % = Percentage, $ = Currency
                  <br />
                  "text" = Literal text, _ = Space, * = Fill character
                </AlertDescription>
              </Alert>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Advanced Tab */}
        <TabsContent value="advanced" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="text-base">Advanced Formatting</CardTitle>
              <CardDescription>
                Create conditional formatting with rules and styles
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Button
                onClick={() => setShowFormatterEditor(true)}
                variant="outline"
                className="w-full"
              >
                <Sparkles className="h-4 w-4 mr-2" />
                Open Advanced Editor
              </Button>
            </CardContent>
          </Card>

          <Alert>
            <Info className="h-4 w-4" />
            <AlertDescription className="text-sm">
              The advanced editor allows you to create complex conditional formatting rules with:
              <ul className="mt-2 ml-4 list-disc text-xs space-y-1">
                <li>Multiple conditions (if value {'>'} X, if value {'<'} Y, etc.)</li>
                <li>Custom colors and styles for each condition</li>
                <li>Text transformations and custom display formats</li>
                <li>Default fallback formatting</li>
              </ul>
            </AlertDescription>
          </Alert>
        </TabsContent>
      </Tabs>

      {/* Advanced formatter dialog */}
      {showFormatterEditor && (
        <ValueFormatterEditor
          open={showFormatterEditor}
          onOpenChange={setShowFormatterEditor}
          initialFormatter={(() => {
            if (selectedColumns.size !== 1) return undefined;
            const colId = Array.from(selectedColumns)[0];
            const colDef = columnDefinitions.get(colId);
            const pendingChange = pendingChanges.get(colId);
            return pendingChange?.valueFormatter || colDef?.valueFormatter;
          })()}
          onSave={async (formatter, cellStyle) => {
            console.log('[FormatTab] Received from ValueFormatterEditor:', {
              hasFormatter: !!formatter,
              formatterType: typeof formatter,
              formatString: (formatter as any)?.__formatString,
              hasCellStyle: !!cellStyle,
              cellStyleType: typeof cellStyle
            });

            const properties: any = {
              valueFormatter: formatter,
              useValueFormatterForExport: true
            };

            if (cellStyle) {
              // We need to check each selected column for existing base styles
              const cellStyleUpdates: Record<string, any> = {};
              const formatString = (formatter as any).__formatString;

              selectedColumns.forEach(colId => {
                const colDef = columnDefinitions.get(colId);
                const pendingChange = pendingChanges.get(colId);
                const existingCellStyle = pendingChange?.cellStyle || colDef?.cellStyle;

                let baseStyle: React.CSSProperties | undefined;
                if (existingCellStyle) {
                  if (typeof existingCellStyle === 'object') {
                    // Direct style object from styling tab
                    baseStyle = existingCellStyle;
                  } else if (typeof existingCellStyle === 'function') {
                    // Function style - check for base style metadata
                    const metadata = (existingCellStyle as any).__baseStyle;
                    if (metadata) {
                      baseStyle = metadata;
                    } else {
                      // Try calling the function to see if it returns static styles
                      const testStyle = existingCellStyle({ value: null });
                      if (testStyle && typeof testStyle === 'object') {
                        baseStyle = testStyle;
                      }
                    }
                  }
                }

                console.log(`[FormatTab] Column ${colId} existing base style:`, baseStyle);

                // Create a merged cellStyle function that preserves base styles
                if (baseStyle && Object.keys(baseStyle).length > 0) {
                  const mergedCellStyle = (params: { value: unknown }) => {
                    const conditionalStyles = cellStyle(params) || {};
                    // Always merge base and conditional styles, with conditional taking precedence
                    const merged = { ...baseStyle, ...conditionalStyles };
                    return Object.keys(merged).length > 0 ? merged : undefined;
                  };

                  // Attach metadata
                  if (formatString) {
                    Object.defineProperty(mergedCellStyle, '__formatString', { 
                      value: formatString,
                      writable: false,
                      enumerable: false,
                      configurable: true
                    });
                  }
                  Object.defineProperty(mergedCellStyle, '__baseStyle', { 
                    value: baseStyle,
                    writable: false,
                    enumerable: false,
                    configurable: true
                  });

                  cellStyleUpdates[colId] = mergedCellStyle;
                } else {
                  // No base styles, use the cellStyle as-is
                  cellStyleUpdates[colId] = cellStyle;
                }
              });

              // Check if all columns have the same cellStyle function
              const uniqueStyles = new Set(Object.values(cellStyleUpdates).map(fn => {
                const baseStyle = (fn as any).__baseStyle;
                const formatString = (fn as any).__formatString;
                return JSON.stringify({ baseStyle, formatString });
              }));

              if (uniqueStyles.size === 1) {
                // All columns have the same style function, use bulk update
                properties.cellStyle = Object.values(cellStyleUpdates)[0];
              } else {
                // Different styles, need individual updates
                properties.cellStyleUpdates = cellStyleUpdates;
              }
            }

            console.log('[FormatTab] Updating bulk properties:', properties);
            updateBulkProperties(properties);

            // Apply individual cellStyle updates if needed
            if (properties.cellStyleUpdates) {
              selectedColumns.forEach(colId => {
                updateSingleProperty(colId, 'cellStyle', properties.cellStyleUpdates[colId]);
              });
              delete properties.cellStyleUpdates; // Clean up
            }

            setShowFormatterEditor(false);
          }}
          dataType={(() => {
            if (selectedColumns.size === 0) return 'text';
            const colId = Array.from(selectedColumns)[0];
            const colDef = columnDefinitions.get(colId);
            return colDef?.cellDataType as string || 'text';
          })()}
        />
      )}
    </div>
  );
}

import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { ScrollArea } from '@/components/ui/scroll-area';
import { ThreeStateCheckbox } from '../components/ThreeStateCheckbox';
import { AlignmentIconPicker } from '../components/AlignmentIconPicker';
import { Button } from '@/components/ui/button';
import { Palette, Eraser } from 'lucide-react';
import { StyleEditor } from '../editors/StyleEditor';
import { useColumnCustomizationStore } from '../store/columnCustomization.store';
import { createCellStyleFunction } from '@/components/datatable/utils/formatters';

interface StylingTabProps {
  uiMode?: 'simple' | 'advanced';
}

export const StylingTab: React.FC<StylingTabProps> = ({ uiMode: _uiMode = 'simple' }) => {
  const {
    selectedColumns,
    columnDefinitions,
    pendingChanges,
    updateBulkProperty
  } = useColumnCustomizationStore();

  const [showCellStyleEditor, setShowCellStyleEditor] = useState(false);
  const [showHeaderStyleEditor, setShowHeaderStyleEditor] = useState(false);

  const getMixedValue = (property: string) => {
    const values = new Set();
    const allValues: unknown[] = [];

    selectedColumns.forEach(colId => {
      const colDef = columnDefinitions.get(colId);
      const pendingChange = pendingChanges.get(colId);

      // Check pending changes first, then fall back to column definition
      let value;
      if (pendingChange && property in pendingChange) {
        value = pendingChange[property as keyof typeof pendingChange];
      } else if (colDef) {
        value = colDef[property as keyof typeof colDef];
      }

      values.add(value);
      allValues.push(value);
    });

    if (values.size === 0) return { value: undefined, isMixed: false };
    if (values.size === 1) return { value: Array.from(values)[0], isMixed: false };
    return { value: undefined, isMixed: true, values: allValues };
  };

  const isDisabled = selectedColumns.size === 0;
  const isMultipleSelection = selectedColumns.size > 1;

  const handleCellStyleSave = (style: React.CSSProperties) => {
    // Check if we have conditional formatting that needs to be preserved
    let hasConditionalFormatting = false;
    let formatString = '';

    selectedColumns.forEach(colId => {
      const colDef = columnDefinitions.get(colId);
      const pendingChange = pendingChanges.get(colId);

      // Check pending changes first, then column definition
      const valueFormatter = pendingChange?.valueFormatter || colDef?.valueFormatter;
      const cellStyle = pendingChange?.cellStyle || colDef?.cellStyle;

      // First check if valueFormatter has format string metadata
      if (valueFormatter && typeof valueFormatter === 'function') {
        const metadata = (valueFormatter as any).__formatString;
        if (metadata && metadata.includes('[') && metadata.includes(']')) {
          // Check if format string contains style directives OR color specifications
          const hasStyleDirectives = metadata.match(/\[(BG:|Background:|Border:|B:|Size:|FontSize:|Align:|TextAlign:|Padding:|P:|Weight:|FontWeight:|Bold|Italic|Underline|Center|Left|Right|#[0-9A-Fa-f]{3,6}|Red|Green|Blue|Yellow|Orange|Purple|Gray|Grey|Black|White|Magenta|Cyan)/i);
          if (hasStyleDirectives) {
            hasConditionalFormatting = true;
            formatString = metadata;
          }
        }
      }

      // Also check if existing cellStyle has format string metadata
      if (!hasConditionalFormatting && cellStyle && typeof cellStyle === 'function') {
        const metadata = (cellStyle as any).__formatString;
        if (metadata && metadata.includes('[') && metadata.includes(']')) {
          hasConditionalFormatting = true;
          formatString = metadata;
        }
      }
    });

    if (hasConditionalFormatting) {
      // We have conditional formatting from valueFormatter
      // Create a cellStyle function that merges base styles with conditional styles
      // Conditional styles take precedence (similar to the reference implementation)
      // Create a custom cellStyle function that always merges base and conditional styles
      // This is different from createCellStyleFunction which returns EITHER conditional OR base styles
      const cellStyleFn = (params: { value: unknown }) => {
        // Always start with base styles
        const baseStyles = style && Object.keys(style).length > 0 ? { ...style } : {};

        // Get conditional styles using createCellStyleFunction with empty base
        // This ensures we only get the conditional styles when conditions match
        const conditionalStyleFn = createCellStyleFunction(formatString, {});
        const conditionalStyles = conditionalStyleFn(params) || {};

        // Always merge base and conditional styles, with conditional taking precedence
        // This matches the reference implementation pattern
        const mergedStyles = { ...baseStyles, ...conditionalStyles };

        // Return merged styles if we have any, otherwise undefined
        return Object.keys(mergedStyles).length > 0 ? mergedStyles : undefined;
      };

      // Attach metadata for future serialization
      Object.defineProperty(cellStyleFn, '__formatString', { 
        value: formatString, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      Object.defineProperty(cellStyleFn, '__baseStyle', { 
        value: style, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      updateBulkProperty('cellStyle', cellStyleFn);
    } else {
      // No conditional formatting, just save the style directly
      updateBulkProperty('cellStyle', style);
    }
  };

  const handleHeaderStyleSave = (style: React.CSSProperties) => {
    console.log('[StylingTab] Saving headerStyle:', {
      style,
      selectedColumnsCount: selectedColumns.size,
      selectedColumns: Array.from(selectedColumns)
    });

    // Create a function that applies styles only to regular headers, not floating filters
    const headerStyleFn = (params: { floatingFilter?: boolean }) => {
      // Don't apply styles to floating filter row
      if (params?.floatingFilter) {
        return null;
      }
      return style;
    };

    updateBulkProperty('headerStyle', headerStyleFn);
  };

  // Clear all styles
  const _clearAllStyles = () => {
    updateBulkProperty('cellStyle', undefined);
    updateBulkProperty('headerStyle', undefined);
    updateBulkProperty('cellClass', undefined);
    updateBulkProperty('headerClass', undefined);
  };

  // Clear only cell styles
  const clearCellStyles = () => {
    // Check if we have conditional formatting from valueFormatter that needs to be preserved
    let hasConditionalFormatting = false;
    let formatString = '';

    selectedColumns.forEach(colId => {
      const colDef = columnDefinitions.get(colId);
      const pendingChange = pendingChanges.get(colId);

      // Check pending changes first, then column definition
      const valueFormatter = pendingChange?.valueFormatter || colDef?.valueFormatter;

      // Check if valueFormatter has format string metadata (from conditional formatting)
      if (valueFormatter && typeof valueFormatter === 'function') {
        const metadata = (valueFormatter as any).__formatString;
        if (metadata && metadata.includes('[') && metadata.includes(']')) {
          // Check if format string contains style directives OR color specifications
          const hasStyleDirectives = metadata.match(/\[(BG:|Background:|Border:|B:|Size:|FontSize:|Align:|TextAlign:|Padding:|P:|Weight:|FontWeight:|Bold|Italic|Underline|Center|Left|Right|#[0-9A-Fa-f]{3,6}|Red|Green|Blue|Yellow|Orange|Purple|Gray|Grey|Black|White|Magenta|Cyan)/i);
          if (hasStyleDirectives) {
            hasConditionalFormatting = true;
            formatString = metadata;
          }
        }
      }
    });

    if (hasConditionalFormatting) {
      // Keep conditional formatting but clear base styles
      // This is the same as handleCellStyleSave but with empty base styles
      const cellStyleFn = (params: { value: unknown }) => {
        // No base styles (cleared)
        const _baseStyles = {};

        // Get conditional styles using createCellStyleFunction with empty base
        const conditionalStyleFn = createCellStyleFunction(formatString, {});
        const conditionalStyles = conditionalStyleFn(params) || {};

        // Return only conditional styles since base styles are cleared
        return Object.keys(conditionalStyles).length > 0 ? conditionalStyles : undefined;
      };

      // Attach metadata for future serialization
      Object.defineProperty(cellStyleFn, '__formatString', { 
        value: formatString, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      Object.defineProperty(cellStyleFn, '__baseStyle', { 
        value: {}, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      updateBulkProperty('cellStyle', cellStyleFn);
    } else {
      updateBulkProperty('cellStyle', undefined);
    }

    // Clear non-format related classes
    const currentCellClass = getMixedValue('cellClass');
    if (currentCellClass.value) {
      const classes = (currentCellClass.value as string).split(' ').filter(c => 
        c.startsWith('ag-numeric-cell') || c.startsWith('ag-currency-cell') || 
        c.startsWith('ag-percentage-cell') || c.startsWith('ag-date-cell')
      );
      updateBulkProperty('cellClass', classes.length > 0 ? classes.join(' ') : undefined);
    }
  };

  // Clear only header styles
  const clearHeaderStyles = () => {
    updateBulkProperty('headerStyle', undefined);
    updateBulkProperty('headerClass', undefined);
  };

  // Clear only alignment classes (keep other styles)
  const _clearAlignment = () => {
    const currentHeaderClass = getMixedValue('headerClass');
    const currentCellClass = getMixedValue('cellClass');

    // Remove alignment classes from headerClass
    if (currentHeaderClass.value) {
      const headerClasses = (currentHeaderClass.value as string).split(' ').filter(c =>
        !c.startsWith('header-align-') && !c.startsWith('header-valign-')
      );
      updateBulkProperty('headerClass', headerClasses.length > 0 ? headerClasses.join(' ') : undefined);
    }

    // Remove alignment classes from cellClass
    if (currentCellClass.value) {
      const cellClasses = (currentCellClass.value as string).split(' ').filter(c =>
        !c.startsWith('cell-align-') && !c.startsWith('cell-valign-')
      );
      updateBulkProperty('cellClass', cellClasses.length > 0 ? cellClasses.join(' ') : undefined);
    }
  };

  // Get current cell style (if consistent across selected columns)
  const currentCellStyle = getMixedValue('cellStyle');
  const currentHeaderStyle = getMixedValue('headerStyle');

  // Extract style object from headerStyle if it's a function
  const getHeaderStyleObject = () => {
    if (currentHeaderStyle.value && typeof currentHeaderStyle.value === 'function') {
      // Call the function with non-floating filter context to get the style object
      return currentHeaderStyle.value({ floatingFilter: false });
    }
    return currentHeaderStyle.value;
  };

  // Extract style object from cellStyle if it's a function
  const getCellStyleObject = () => {
    if (currentCellStyle.value && typeof currentCellStyle.value === 'function') {
      // Check if this function has base style metadata
      const baseStyle = (currentCellStyle.value as any).__baseStyle;
      if (baseStyle) {
        return baseStyle;
      }
      // Don't pass function-based styles to the editor without base style
      return {};
    }
    return currentCellStyle.value;
  };

  // Handle header alignment changes using headerClass (separate from headerStyle)
  const handleHeaderAlignmentChange = (alignment: string, type: 'horizontal' | 'vertical') => {
    const currentHeaderClass = getMixedValue('headerClass');
    // Ensure we always work with strings, not booleans
    const currentClassesRaw = currentHeaderClass.value;
    const currentClasses = (typeof currentClassesRaw === 'string' ? currentClassesRaw : '').trim();
    const classArray = currentClasses ? currentClasses.split(' ').filter(c => c) : [];

    // Remove existing alignment classes (both custom and Tailwind)
    const filteredClasses = classArray.filter(c => {
      if (type === 'horizontal') {
        return !c.startsWith('header-align-') && 
               c !== 'text-left' && c !== 'text-center' && c !== 'text-right' &&
               c !== 'justify-start' && c !== 'justify-center' && c !== 'justify-end';
      } else {
        return !c.startsWith('header-valign-') &&
               c !== 'items-start' && c !== 'items-center' && c !== 'items-end';
      }
    });

    // Add new Tailwind alignment classes if not default
    if (alignment !== 'default') {
      if (type === 'horizontal') {
        // For headers, we need both text alignment and flex justification
        const alignmentMap: Record<string, string[]> = {
          'left': ['text-left', 'justify-start'],
          'center': ['text-center', 'justify-center'],
          'right': ['text-right', 'justify-end']
        };
        if (alignmentMap[alignment]) {
          filteredClasses.push(...alignmentMap[alignment]);
        }
      } else {
        // Vertical alignment
        const alignmentMap: Record<string, string> = {
          'top': 'items-start',
          'middle': 'items-center',
          'bottom': 'items-end'
        };
        if (alignmentMap[alignment]) {
          filteredClasses.push(alignmentMap[alignment]);
        }
      }
    }

    const newHeaderClass = filteredClasses.join(' ').trim();
    updateBulkProperty('headerClass', newHeaderClass || undefined);
  };

  // Handle cell alignment changes using cellClass
  const handleCellAlignmentChange = (alignment: string, type: 'horizontal' | 'vertical') => {
    const currentCellClass = getMixedValue('cellClass');
    // Ensure we always work with strings, not booleans
    const currentClassesRaw = currentCellClass.value;
    const currentClasses = (typeof currentClassesRaw === 'string' ? currentClassesRaw : '').trim();
    const classArray = currentClasses ? currentClasses.split(' ').filter(c => c) : [];

    // Remove existing alignment classes (both custom and Tailwind)
    const filteredClasses = classArray.filter(c => {
      if (type === 'horizontal') {
        return !c.startsWith('cell-align-') && 
               c !== 'text-left' && c !== 'text-center' && c !== 'text-right' &&
               c !== 'justify-start' && c !== 'justify-center' && c !== 'justify-end';
      } else {
        return !c.startsWith('cell-valign-') &&
               c !== 'items-start' && c !== 'items-center' && c !== 'items-end';
      }
    });

    // Add new Tailwind alignment classes if not default
    if (alignment !== 'default') {
      if (type === 'horizontal') {
        // For cells, we need both text alignment and flex justification
        const alignmentMap: Record<string, string[]> = {
          'left': ['text-left', 'justify-start'],
          'center': ['text-center', 'justify-center'],
          'right': ['text-right', 'justify-end']
        };
        if (alignmentMap[alignment]) {
          filteredClasses.push(...alignmentMap[alignment]);
        }
      } else {
        // Vertical alignment
        const alignmentMap: Record<string, string> = {
          'top': 'items-start',
          'middle': 'items-center',
          'bottom': 'items-end'
        };
        if (alignmentMap[alignment]) {
          filteredClasses.push(alignmentMap[alignment]);
        }
      }
    }

    const newCellClass = filteredClasses.join(' ').trim();
    updateBulkProperty('cellClass', newCellClass || undefined);
  };

  // Extract current header alignment from headerClass
  const getCurrentHeaderAlignment = (type: 'horizontal' | 'vertical') => {
    const headerClassValue = getMixedValue('headerClass');
    const headerClassRaw = headerClassValue.value;
    const headerClass = (typeof headerClassRaw === 'string' ? headerClassRaw : '').trim();

    if (!headerClass) return 'default';

    if (type === 'horizontal') {
      // Check for Tailwind classes first
      if (headerClass.includes('text-left') || headerClass.includes('justify-start')) return 'left';
      if (headerClass.includes('text-center') || headerClass.includes('justify-center')) return 'center';
      if (headerClass.includes('text-right') || headerClass.includes('justify-end')) return 'right';

      // Fall back to custom classes
      const prefix = 'header-align-';
      const regex = new RegExp(`${prefix}(\\w+)`);
      const match = headerClass.match(regex);
      return match ? match[1] : 'default';
    } else {
      // Check for Tailwind classes
      if (headerClass.includes('items-start')) return 'top';
      if (headerClass.includes('items-center')) return 'middle';
      if (headerClass.includes('items-end')) return 'bottom';

      // Fall back to custom classes
      const prefix = 'header-valign-';
      const regex = new RegExp(`${prefix}(\\w+)`);
      const match = headerClass.match(regex);
      return match ? match[1] : 'default';
    }
  };

  // Extract current cell alignment from cellClass
  const getCurrentCellAlignment = (type: 'horizontal' | 'vertical') => {
    const cellClassValue = getMixedValue('cellClass');
    const cellClassRaw = cellClassValue.value;
    const cellClass = (typeof cellClassRaw === 'string' ? cellClassRaw : '').trim();

    if (!cellClass) return 'default';

    if (type === 'horizontal') {
      // Check for Tailwind classes first
      if (cellClass.includes('text-left') || cellClass.includes('justify-start')) return 'left';
      if (cellClass.includes('text-center') || cellClass.includes('justify-center')) return 'center';
      if (cellClass.includes('text-right') || cellClass.includes('justify-end')) return 'right';

      // Fall back to custom classes
      const prefix = 'cell-align-';
      const regex = new RegExp(`${prefix}(\\w+)`);
      const match = cellClass.match(regex);
      return match ? match[1] : 'default';
    } else {
      // Check for Tailwind classes
      if (cellClass.includes('items-start')) return 'top';
      if (cellClass.includes('items-center')) return 'middle';
      if (cellClass.includes('items-end')) return 'bottom';

      // Fall back to custom classes
      const prefix = 'cell-valign-';
      const regex = new RegExp(`${prefix}(\\w+)`);
      const match = cellClass.match(regex);
      return match ? match[1] : 'default';
    }
  };

  // Check if header alignment is mixed
  const isHeaderAlignmentMixed = (_type: 'horizontal' | 'vertical') => {
    const headerClassValue = getMixedValue('headerClass');
    return headerClassValue.isMixed;
  };

  // Check if cell alignment is mixed
  const isCellAlignmentMixed = (_type: 'horizontal' | 'vertical') => {
    const cellClassValue = getMixedValue('cellClass');
    return cellClassValue.isMixed;
  };

  return (
    <ScrollArea className="h-full">
      <div className="px-6 py-4 space-y-6">
        {/* Two-column layout for better space utilization */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Left Column */}
          <div className="space-y-6">
            {/* Header Alignment */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Header Alignment</CardTitle>
                <CardDescription className="text-sm">
                  Set text alignment for column headers
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <AlignmentIconPicker
                  label="Horizontal"
                  type="horizontal"
                  value={getCurrentHeaderAlignment('horizontal')}
                  onChange={(value) => handleHeaderAlignmentChange(value, 'horizontal')}
                  disabled={isDisabled || (isMultipleSelection && isHeaderAlignmentMixed('horizontal'))}
                  isMixed={isMultipleSelection && isHeaderAlignmentMixed('horizontal')}
                />

                <AlignmentIconPicker
                  label="Vertical"
                  type="vertical"
                  value={getCurrentHeaderAlignment('vertical')}
                  onChange={(value) => handleHeaderAlignmentChange(value, 'vertical')}
                  disabled={isDisabled || (isMultipleSelection && isHeaderAlignmentMixed('vertical'))}
                  isMixed={isMultipleSelection && isHeaderAlignmentMixed('vertical')}
                />
              </CardContent>
            </Card>

            {/* Style Editors */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Style Editors</CardTitle>
                <CardDescription className="text-sm">
                  Customize visual appearance
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-3">
                <Button
                  variant="outline"
                  size="sm"
                  className="w-full justify-start gap-2 h-9"
                  disabled={isDisabled}
                  onClick={() => setShowHeaderStyleEditor(true)}
                >
                  <Palette className="h-4 w-4" />
                  Edit Header Style
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  className="w-full justify-start gap-2 h-9"
                  disabled={isDisabled}
                  onClick={() => setShowCellStyleEditor(true)}
                >
                  <Palette className="h-4 w-4" />
                  Edit Cell Style
                </Button>
              </CardContent>
            </Card>

            {/* Text Display Options */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Text Display</CardTitle>
                <CardDescription className="text-sm">
                  Control how text is displayed in cells
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-3">
                <ThreeStateCheckbox
                  label="Wrap Text"
                  property="wrapText"
                  mixedValue={getMixedValue('wrapText')}
                  onChange={(value) => updateBulkProperty('wrapText', value)}
                  disabled={isDisabled || (isMultipleSelection && getMixedValue('wrapText').isMixed)}
                  description="Wrap long text within cells"
                />

                <ThreeStateCheckbox
                  label="Auto Height"
                  property="autoHeight"
                  mixedValue={getMixedValue('autoHeight')}
                  onChange={(value) => updateBulkProperty('autoHeight', value)}
                  disabled={isDisabled || (isMultipleSelection && getMixedValue('autoHeight').isMixed)}
                  description="Automatically adjust row height to fit content"
                />
              </CardContent>
            </Card>
          </div>

          {/* Right Column */}
          <div className="space-y-6">
            {/* Cell Alignment */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Cell Alignment</CardTitle>
                <CardDescription className="text-sm">
                  Set text alignment for cell content
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <AlignmentIconPicker
                  label="Horizontal"
                  type="horizontal"
                  value={getCurrentCellAlignment('horizontal')}
                  onChange={(value) => handleCellAlignmentChange(value, 'horizontal')}
                  disabled={isDisabled || (isMultipleSelection && isCellAlignmentMixed('horizontal'))}
                  isMixed={isMultipleSelection && isCellAlignmentMixed('horizontal')}
                />

                <AlignmentIconPicker
                  label="Vertical"
                  type="vertical"
                  value={getCurrentCellAlignment('vertical')}
                  onChange={(value) => handleCellAlignmentChange(value, 'vertical')}
                  disabled={isDisabled || (isMultipleSelection && isCellAlignmentMixed('vertical'))}
                  isMixed={isMultipleSelection && isCellAlignmentMixed('vertical')}
                />
              </CardContent>
            </Card>

            {/* Clear Styles */}
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-base">Reset Styles</CardTitle>
                <CardDescription className="text-sm">
                  Remove custom styling and formatting
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-3">
                <Button
                  variant="outline"
                  size="sm"
                  className="w-full justify-start gap-2 h-9"
                  disabled={isDisabled}
                  onClick={clearCellStyles}
                >
                  <Eraser className="h-4 w-4" />
                  Clear Cell Styles
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  className="w-full justify-start gap-2 h-9"
