import { useCallback, useRef, useMemo } from 'react';
import { ColDef, GridApi } from 'ag-grid-community';
import { useActiveProfile } from '@/components/datatable/stores/profile.store';
import { profileOptimizer } from '@/components/datatable/lib/profileOptimizer';
import { debounce } from '@/lib/utils';
import { ColumnDef } from '../types';
import { STATE_RESTORATION_DELAY_MS, COLUMN_UPDATE_DEBOUNCE_MS } from '../utils/constants';

/**
 * Custom hook for handling column operations and updates
 */
export function useColumnOperations(
  gridApiRef: React.MutableRefObject<GridApi | null>,
  setCurrentColumnDefs: (columns: ColumnDef[]) => void
) {
  const activeProfile = useActiveProfile();
  const columnDefsWithStylesRef = useRef<ColumnDef[]>([]);

  // Debounced column update to prevent rapid re-renders
  const applyColumnChangesDebounced = useMemo(
    () => debounce((columns: ColDef[]) => {
      if (gridApiRef.current && activeProfile) {
        console.log('[useColumnOperations] Applying column definitions to grid');

        // Simply update the column definitions
        gridApiRef.current.setGridOption('columnDefs', columns);

        // Apply the column state from the active profile
        if (activeProfile.gridState.columnState && activeProfile.gridState.columnState.length > 0) {
          console.log('[useColumnOperations] Applying column state from active profile:', {
            profileName: activeProfile.name,
            columnStateCount: activeProfile.gridState.columnState.length
          });

          gridApiRef.current.applyColumnState({
            state: activeProfile.gridState.columnState,
            applyOrder: true
          });
        }

        // Apply sort model from profile
        if (activeProfile.gridState.sortModel && activeProfile.gridState.sortModel.length > 0) {
          const sortState = activeProfile.gridState.sortModel.map(sort => ({
            colId: sort.colId,
            sort: sort.sort,
            sortIndex: sort.sortIndex
          }));
          gridApiRef.current.applyColumnState({ state: sortState });
        }

        // Apply filter model from profile
        if (activeProfile.gridState.filterModel) {
          gridApiRef.current.setFilterModel(activeProfile.gridState.filterModel);
        }

        // Refresh the grid to show the changes
        gridApiRef.current.refreshHeader();
        gridApiRef.current.refreshCells({ 
          force: true,
          suppressFlash: true 
        });
      }
    }, COLUMN_UPDATE_DEBOUNCE_MS),
    [activeProfile]
  );

  const handleApplyColumnChanges = useCallback((updatedColumns: ColDef[]) => {
    console.log('[useColumnOperations] handleApplyColumnChanges:', {
      updatedColumnsCount: updatedColumns.length,
      hasGridApi: !!gridApiRef.current,
      hasCustomizations: updatedColumns.some(col => 
        col.cellStyle || col.valueFormatter || col.cellClass
      ),
      firstColumn: updatedColumns[0]?.field
    });

    // Store the columns with styles for later retrieval
    columnDefsWithStylesRef.current = updatedColumns as ColumnDef[];

    // Update the currentColumnDefs state
    setCurrentColumnDefs(updatedColumns as ColumnDef[]);

    // Apply the changes to the grid
    if (gridApiRef.current) {
      // Since maintainColumnOrder is true, this should preserve column state
      applyColumnChangesDebounced(updatedColumns);
    }

    // Log what was updated for debugging
    const updatedFields = updatedColumns.filter(col => 
      col.cellStyle || col.valueFormatter || col.cellClass
    ).map(col => col.field);

    if (updatedFields.length > 0) {
      console.log('[useColumnOperations] Column customizations applied:', {
        updatedFields,
        totalColumns: updatedColumns.length
      });
    }

    // Update React state
    setCurrentColumnDefs(updatedColumns.map((col) => {
      if (col.field && col.headerName) {
        return col as ColumnDef;
      }

      return {
        ...col,
        field: col.field || '',
        headerName: col.headerName || col.field || ''
      };
    }));

    // Clear optimizer cache for active profile since columns changed
    if (activeProfile) {
      profileOptimizer.clearCache(activeProfile.id);
    }

    // Column changes from dialog are only saved when Save Profile button is clicked
  }, [activeProfile, setCurrentColumnDefs, applyColumnChangesDebounced]);

  // Get column definitions with styles
  const getColumnDefsWithStyles = useCallback(() => {
    return columnDefsWithStylesRef.current;
  }, []);

  return {
    handleApplyColumnChanges,
    getColumnDefsWithStyles,
  };
}

import { useMemo } from 'react';
import { createCellStyleFunction, hasConditionalStyling } from '../utils/styleUtils';
import { ColumnDef } from '../types';

/**
 * Custom hook that processes columns to ensure cellStyle functions are created
 * for columns with conditional formatting in their valueFormatters.
 * 
 * This preserves the CRITICAL functionality from ensureCellStyleForColumns
 * that merges base styles with conditional styles.
 */
export function useColumnProcessor(columns: ColumnDef[]): ColumnDef[] {
  return useMemo(() => {
    let hasChanges = false;

    const processed = columns.map(col => {
      // Check if valueFormatter has conditional styling that needs cellStyle
      if (!col.valueFormatter || typeof col.valueFormatter !== 'function') {
        return col;
      }

      const formatString = (col.valueFormatter as any).__formatString;

      if (!formatString || !hasConditionalStyling(formatString)) {
        return col;
      }

      // Check if cellStyle needs to be created or updated
      const existingCellStyle = col.cellStyle as any;
      const needsCellStyle = !col.cellStyle || 
                            (typeof col.cellStyle === 'function' && 
                             existingCellStyle.__formatString !== formatString);

      if (!needsCellStyle) {
        return col;
      }

      hasChanges = true;

      console.log('[useColumnProcessor] Creating cellStyle for conditional formatting:', {
        field: col.field,
        formatString,
        hasExistingCellStyle: !!col.cellStyle
      });

      // Extract base style if it exists
      let baseStyle: React.CSSProperties = {};
      if (col.cellStyle) {
        if (typeof col.cellStyle === 'object') {
          baseStyle = col.cellStyle as React.CSSProperties;
        } else if (typeof col.cellStyle === 'function' && existingCellStyle.__baseStyle) {
          baseStyle = existingCellStyle.__baseStyle;
        }
      }

      // Create cellStyle function with proper base style merging
      const cellStyleFn = createCellStyleFunction(formatString, baseStyle);

      // Attach metadata for future serialization
      Object.defineProperty(cellStyleFn, '__formatString', { 
        value: formatString, 
        writable: false,
        enumerable: false,
        configurable: true
      });
      Object.defineProperty(cellStyleFn, '__baseStyle', { 
        value: baseStyle, 
        writable: false,
        enumerable: false,
        configurable: true
      });

      return { ...col, cellStyle: cellStyleFn };
    });

    // Only return new array if changes were made
    return hasChanges ? processed : columns;
  }, [columns]);
}

import { useCallback, useMemo } from 'react';
import { GridApi, GridReadyEvent } from 'ag-grid-community';
import { useActiveProfile } from '@/components/datatable/stores/profile.store';
import { useDataTableContext } from './useDataTableContext';

/**
 * Custom hook for memoized grid callbacks to prevent unnecessary re-renders
 */
export function useGridCallbacks(
  gridApiRef: React.MutableRefObject<GridApi | null>,
  setSelectedFont: (font: string) => void
) {
  const activeProfile = useActiveProfile();
  const { setGridApi } = useDataTableContext();

  // Context menu items - now includes Format Column option
  const getContextMenuItems = useCallback((params: any): any => {
    const defaultItems = [
      "autoSizeAll",
      "resetColumns",
      "separator",
      "copy",
      "copyWithHeaders",
      "paste",
      "separator",
      "export",
    ];

    // Only add Format Column for column headers, not cell context menus
    if (params.column) {
      return [
        {
          name: 'Format Column',
          action: () => {
            // Dispatch custom event with column info and click position
            const event = new CustomEvent('format-column', {
              detail: {
                colId: params.column.getColId(),
                colDef: params.column.getColDef(),
                x: params.event?.clientX || 0,
                y: params.event?.clientY || 0
              }
            });
            window.dispatchEvent(event);
          },
          icon: '<span class="ag-icon ag-icon-columns" style="font-size: 14px;"></span>'
        },
        "separator",
        ...defaultItems
      ];
    }

    return defaultItems;
  }, []);

  // Grid ready handler
  const onGridReady = useCallback(async (params: GridReadyEvent) => {
    gridApiRef.current = params.api;

    // Update the state version of gridApi for components that need it
    if (setGridApi) {
      setGridApi(params.api);
    }

    console.log('[useGridCallbacks] onGridReady:', {
      hasActiveProfile: !!activeProfile,
      activeProfileId: activeProfile?.id,
      activeProfileName: activeProfile?.name,
      hasGridState: !!activeProfile?.gridState
    });

    // Load active profile on grid ready - SIMPLIFIED APPROACH
    if (activeProfile && activeProfile.gridState) {
      console.log('[useGridCallbacks] Applying active profile on grid ready:', {
        profileId: activeProfile.id,
        profileName: activeProfile.name,
        hasColumnState: !!activeProfile.gridState.columnState,
        columnStateLength: activeProfile.gridState.columnState?.length
      });

      try {
        // Apply states in a deterministic order with proper timing

        // 1. Apply column state (visibility, width, position, sort)
        if (activeProfile.gridState.columnState && activeProfile.gridState.columnState.length > 0) {
          console.log('[useGridCallbacks] Applying column state');
          params.api.applyColumnState({
            state: activeProfile.gridState.columnState,
            applyOrder: true
          });
        }

        // 2. Apply filter model
        if (activeProfile.gridState.filterModel) {
          console.log('[useGridCallbacks] Applying filter model');
          params.api.setFilterModel(activeProfile.gridState.filterModel);
        }

        // 3. Apply grid options
        if (activeProfile.gridState.gridOptions) {
          console.log('[useGridCallbacks] Applying grid options');
          const options = activeProfile.gridState.gridOptions;
          if (options.rowHeight) {
            params.api.resetRowHeights();
            params.api.setGridOption('rowHeight', options.rowHeight);
          }
          if (options.headerHeight) {
            params.api.setGridOption('headerHeight', options.headerHeight);
          }
        }

        // 4. Apply font
        if (activeProfile.gridState.font) {
          console.log('[useGridCallbacks] Applying font:', activeProfile.gridState.font);
          setSelectedFont(activeProfile.gridState.font);
        }

        console.log('[useGridCallbacks] Profile applied successfully');
      } catch (error) {
        console.error('[useGridCallbacks] Error applying profile:', error);
      }
    } else {
      console.log('[useGridCallbacks] No active profile or gridState to apply');
    }
  }, [activeProfile, setSelectedFont, setGridApi]);

  // Column event handlers - no auto-save, only saved when Save Profile button is clicked
  const onColumnMoved = useCallback(() => {
    // State changes are only saved when Save Profile button is clicked
  }, []);

  const onColumnResized = useCallback(() => {
    // State changes are only saved when Save Profile button is clicked
  }, []);

  const onColumnVisible = useCallback(() => {
    // State changes are only saved when Save Profile button is clicked
  }, []);

  const onToolPanelVisibleChanged = useCallback(() => {
    // State changes are only saved when Save Profile button is clicked
  }, []);

  const onSortChanged = useCallback(() => {
    // State changes are only saved when Save Profile button is clicked
  }, []);

  const onFilterChanged = useCallback(() => {
    // State changes are only saved when Save Profile button is clicked
  }, []);

  // Excel export styles
  const excelStyles = useMemo(() => [
    {
      id: 'header',
      font: { bold: true },
      alignment: { horizontal: 'Center' as const, vertical: 'Center' as const }
    },
    {
      id: 'ag-numeric-cell',
      alignment: { horizontal: 'Right' as const }
    },
    {
      id: 'ag-currency-cell',
      numberFormat: { format: '$#,##0.00' },
      alignment: { horizontal: 'Right' as const }
    },
    {
      id: 'ag-percentage-cell',
      numberFormat: { format: '0.00%' },
      alignment: { horizontal: 'Right' as const }
    }
  ], []);

  // Default Excel export params
  const defaultExcelExportParams = useMemo(() => ({
    // Apply column formatting to Excel export
    processCellCallback: (params: any) => {
      const colDef = params.column.getColDef();
      // Use valueFormatter for export if available
      if (colDef.valueFormatter) {
        return typeof colDef.valueFormatter === 'function'
          ? colDef.valueFormatter(params)
          : params.value;
      }
      return params.value;
    },
    // Include column headers with formatting
    processHeaderCallback: (params: any) => {
      return params.column.getColDef().headerName || params.column.getColId();
    }
  }), []);

  return {
    // Event handlers
    getContextMenuItems,
    onGridReady,
    onColumnMoved,
    onColumnResized,
    onColumnVisible,
    onToolPanelVisibleChanged,
    onSortChanged,
    onFilterChanged,

    // Export configuration
    excelStyles,
    defaultExcelExportParams,
  };
}

import { useState, useCallback } from 'react';
import { useProfileStore, useActiveProfile } from '@/components/datatable/stores/profile.store';
import { ColumnDef } from '../types';

/**
 * Custom hook for managing DataTable grid state
 */
export function useGridState(initialColumnDefs: ColumnDef[]) {
  const activeProfile = useActiveProfile();
  const { getColumnDefs } = useProfileStore();

  // Initialize font from active profile
  const [selectedFont, setSelectedFont] = useState(() => {
    return activeProfile?.gridState?.font || 'monospace';
  });

  // Dialog visibility
  const [showColumnDialog, setShowColumnDialog] = useState(false);

  // Initialize column definitions from profile or use defaults
  const [currentColumnDefs, setCurrentColumnDefs] = useState<ColumnDef[]>(() => {
    // Try to get column definitions from profile (will use lightweight format if available)
    const savedColumnDefs = getColumnDefs();

    if (savedColumnDefs && savedColumnDefs.length > 0) {
      console.log('[useGridState] Initializing with saved columnDefs from profile:', {
        profileName: activeProfile?.name,
        profileId: activeProfile?.id,
        columnCount: savedColumnDefs.length,
        hasLightweightFormat: !!(activeProfile?.gridState?.columnCustomizations),
        hasBaseColumnDefs: !!(activeProfile?.gridState?.baseColumnDefs),
        sampleColumn: savedColumnDefs[0],
        columnsWithFormatters: savedColumnDefs.filter((col: any) => col.valueFormatter).length
      });

      return savedColumnDefs as ColumnDef[];
    }

    console.log('[useGridState] Initializing with default columnDefs');
    return initialColumnDefs;
  });

  // Font change handler
  const handleFontChange = useCallback((font: string) => {
    setSelectedFont(font);
    // Font changes are only saved when Save Profile button is clicked
  }, []);

  // Update column definitions
  const updateColumnDefs = useCallback((columns: ColumnDef[]) => {
    setCurrentColumnDefs(columns);
  }, []);

  return {
    // State
    currentColumnDefs,
    selectedFont,
    showColumnDialog,

    // Actions
    setCurrentColumnDefs: updateColumnDefs,
    setSelectedFont: handleFontChange,
    setShowColumnDialog,
  };
}

// Performance monitoring utility
export interface PerformanceMetrics {
  appStartTime: number;
  gridInitTime?: number;
  profileLoadTime?: number;
  dataGenerationTime?: number;
  firstRenderTime?: number;
  fullyLoadedTime?: number;
  [key: string]: number | undefined;
}

class PerformanceMonitor {
  private metrics: PerformanceMetrics;
  private marks: Map<string, number> = new Map();

  constructor() {
    this.metrics = {
      appStartTime: performance.now()
    };
  }

  mark(name: string) {
    const time = performance.now();
    this.marks.set(name, time);
    // console.log(`[Performance] ${name}: ${time.toFixed(2)}ms`);
  }

  measure(name: string, startMark: string, endMark?: string) {
    const startTime = this.marks.get(startMark) || 0;
    const endTime = endMark ? (this.marks.get(endMark) || performance.now()) : performance.now();
    const duration = endTime - startTime;

    this.metrics[name] = duration;
    // console.log(`[Performance] ${name}: ${duration.toFixed(2)}ms`);

    return duration;
  }

  measureFromStart(name: string) {
    const duration = performance.now() - this.metrics.appStartTime;
    this.metrics[name] = duration;
    // console.log(`[Performance] ${name} from start: ${duration.toFixed(2)}ms`);
    return duration;
  }

  getMetrics(): PerformanceMetrics {
    return { ...this.metrics };
  }

  logSummary() {
    // console.group(' Performance Summary');
    // Object.entries(this.metrics).forEach(([key, value]) => {
    //   if (value !== undefined) {
    //     console.log(`${key}: ${value.toFixed(2)}ms`);
    //   }
    // });
    // console.groupEnd();
  }
}

export const perfMonitor = new PerformanceMonitor();

// Export to window for debugging
if (typeof window !== 'undefined') {
  (window as any).perfMonitor = perfMonitor;
}

// Performance testing utilities
import { perfMonitor } from './performanceMonitor';

export interface PerformanceTestResult {
  testName: string;
  metrics: {
    [key: string]: number;
  };
  timestamp: number;
  userAgent: string;
}

class PerformanceTester {
  private results: PerformanceTestResult[] = [];

  async runLoadTest(): Promise<PerformanceTestResult> {
    const testName = 'Page Load Performance';
    const metrics = perfMonitor.getMetrics();

    // Calculate derived metrics
    const derivedMetrics = {
      ...metrics,
      totalLoadTime: metrics.fullyLoadedTime || 0,
      gridLoadTime: (metrics.gridInitTime || 0) - (metrics.appStartTime || 0),
      dataGenTime: metrics.dataGenerationTime || 0,
      profileLoadOverhead: metrics.profileLoadTime || 0,
      perceivedLoadTime: metrics.firstRenderTime || 0,
    };

    const result: PerformanceTestResult = {
      testName,
      metrics: derivedMetrics,
      timestamp: Date.now(),
      userAgent: navigator.userAgent,
    };

    this.results.push(result);
    return result;
  }

  async runProfileSwitchTest(profileId: string): Promise<PerformanceTestResult> {
    const startTime = performance.now();

    // This would need to be integrated with the actual profile switching logic
    // For now, we'll just measure the time it takes to switch profiles

    const endTime = performance.now();
    const switchTime = endTime - startTime;

    const result: PerformanceTestResult = {
      testName: 'Profile Switch Performance',
      metrics: {
        profileSwitchTime: switchTime,
        profileId: profileId.length, // Just for reference
      },
      timestamp: Date.now(),
      userAgent: navigator.userAgent,
    };

    this.results.push(result);
    return result;
  }

  getResults(): PerformanceTestResult[] {
    return [...this.results];
  }

  generateReport(): string {
    const report = ['# Performance Test Report', ''];

    this.results.forEach((result, index) => {
      report.push(`## Test ${index + 1}: ${result.testName}`);
      report.push(`Timestamp: ${new Date(result.timestamp).toISOString()}`);
      report.push('');
      report.push('### Metrics:');

      Object.entries(result.metrics).forEach(([key, value]) => {
        if (typeof value === 'number') {
          report.push(`- ${key}: ${value.toFixed(2)}ms`);
        }
      });

      report.push('');
    });

    // Summary statistics
    if (this.results.length > 0) {
      report.push('## Summary');
      report.push('');

      const loadTests = this.results.filter(r => r.testName === 'Page Load Performance');
      if (loadTests.length > 0) {
        const avgTotalLoad = loadTests.reduce((sum, test) => 
          sum + (test.metrics.totalLoadTime || 0), 0) / loadTests.length;
        const avgPerceivedLoad = loadTests.reduce((sum, test) => 
          sum + (test.metrics.perceivedLoadTime || 0), 0) / loadTests.length;

        report.push(`- Average Total Load Time: ${avgTotalLoad.toFixed(2)}ms`);
        report.push(`- Average Perceived Load Time: ${avgPerceivedLoad.toFixed(2)}ms`);
      }
    }

    return report.join('\n');
  }

  logReport(): void {
    console.group(' Performance Test Report');

    this.results.forEach((result, index) => {
      console.group(`Test ${index + 1}: ${result.testName}`);
      console.table(result.metrics);
      console.log('Timestamp:', new Date(result.timestamp).toISOString());
      console.groupEnd();
    });

    console.groupEnd();
  }
}

export const perfTester = new PerformanceTester();

// Export to window for easy access
if (typeof window !== 'undefined') {
  (window as any).perfTester = perfTester;

  // Add convenience methods
  (window as any).runPerfTest = async () => {
    const result = await perfTester.runLoadTest();
    perfTester.logReport();
    return result;
  };

  (window as any).getPerfReport = () => {
    return perfTester.generateReport();
  };
}

import { ColDef, ColumnState, SortModelItem, GridApi } from 'ag-grid-community';
import { GridProfile } from '@/components/datatable/stores/profile.store';
import { deserializeColumnCustomizations } from '@/components/datatable/stores/columnSerializer';
import { createExcelFormatter } from '@/components/datatable/utils/formatters';

interface ProfileCache {
  processedColumnDefs: ColDef[];
  columnStateHash: string;
  filterModelHash: string;
  sortModelHash: string;
  timestamp: number;
}

interface ProfileDiff {
  columnDefsChanged: boolean;
  columnStateChanged: boolean;
  filterModelChanged: boolean;
  sortModelChanged: boolean;
  gridOptionsChanged: boolean;
  changes: {
    addedColumns: string[];
    removedColumns: string[];
    modifiedColumns: string[];
    columnStateChanges: Partial<ColumnState>[];
    filterChanges: Record<string, any>;
    sortChanges: SortModelItem[];
  };
}

class ProfileOptimizer {
  private cache = new Map<string, ProfileCache>();
  private currentProfileId: string | null = null;
  private pendingUpdates: Map<string, any> = new Map();
  private updateFrameId: number | null = null;

  // Pre-process profiles in background
  async preprocessProfile(profile: GridProfile) {
    if (this.cache.has(profile.id)) {
      const cached = this.cache.get(profile.id)!;
      // Cache is valid for 5 minutes
      if (Date.now() - cached.timestamp < 5 * 60 * 1000) {
        return;
      }
    }

    // Use requestIdleCallback for background processing
    if ('requestIdleCallback' in window) {
      requestIdleCallback(() => this.processProfile(profile), { timeout: 2000 });
    } else {
      setTimeout(() => this.processProfile(profile), 100);
    }
  }

  private processProfile(profile: GridProfile) {
    // perfMonitor.mark('profile-process-start');

    let processedColumnDefs: ColDef[] = [];

    // Process column definitions
    if (profile.gridState.columnCustomizations && profile.gridState.baseColumnDefs && profile.gridState.baseColumnDefs.length > 0) {
      processedColumnDefs = deserializeColumnCustomizations(
        profile.gridState.columnCustomizations,
        profile.gridState.baseColumnDefs
      );
    } else if (profile.gridState.columnDefs) {
      processedColumnDefs = this.processLegacyColumnDefs(profile.gridState.columnDefs);
    }

    // Create hashes for quick comparison
    const columnStateHash = this.hashObject(profile.gridState.columnState || []);
    const filterModelHash = this.hashObject(profile.gridState.filterModel || {});
    const sortModelHash = this.hashObject(profile.gridState.sortModel || []);

    this.cache.set(profile.id, {
      processedColumnDefs,
      columnStateHash,
      filterModelHash,
      sortModelHash,
      timestamp: Date.now()
    });

          // perfMonitor.measure('profileProcessTime', 'profile-process-start');
  }

  private processLegacyColumnDefs(columnDefs: ColDef[]): ColDef[] {
    return columnDefs.map(col => {
      const processed = { ...col };

      // Clean invalid properties
      delete (processed as any).valueFormat;
      delete (processed as any)._hasFormatter;
      delete (processed as any).excelFormat;

      // Remove hide property - visibility should be controlled by column state
      delete (processed as any).hide;

      // Convert headerStyle objects back to functions
      if (processed.headerStyle && typeof processed.headerStyle === 'object') {
        const styleConfig = processed.headerStyle as any;

        if (styleConfig._isHeaderStyleConfig) {
          processed.headerStyle = ((params: { floatingFilter?: boolean }) => {
            if (params?.floatingFilter) {
              return styleConfig.floating || null;
            }
            return styleConfig.regular || null;
          }) as ColDef['headerStyle'];
        }
      }

      // Recreate valueFormatter from saved config
      if (processed.valueFormatter && typeof processed.valueFormatter === 'object') {
        const formatterConfig = processed.valueFormatter as any;

        if (formatterConfig._isFormatterConfig && formatterConfig.type === 'excel' && formatterConfig.formatString) {
          // Recreate the formatter function
          const formatter = createExcelFormatter(formatterConfig.formatString);
          processed.valueFormatter = formatter;
          // Value formatter will be used for export automatically
        } else {
          // Invalid formatter config, remove it
          delete processed.valueFormatter;
          // No export formatter needed
        }
      }

      return processed;
    });
  }

  // Calculate diff between current and target profile
  calculateDiff(currentProfile: GridProfile | null, targetProfile: GridProfile): ProfileDiff {
    const diff: ProfileDiff = {
      columnDefsChanged: false,
      columnStateChanged: false,
      filterModelChanged: false,
      sortModelChanged: false,
      gridOptionsChanged: false,
      changes: {
        addedColumns: [],
        removedColumns: [],
        modifiedColumns: [],
        columnStateChanges: [],
        filterChanges: {},
        sortChanges: []
      }
    };

    if (!currentProfile) {
      // Everything is new
      diff.columnDefsChanged = true;
      diff.columnStateChanged = true;
      diff.filterModelChanged = !!targetProfile.gridState.filterModel;
      diff.sortModelChanged = !!targetProfile.gridState.sortModel?.length;
      diff.gridOptionsChanged = !!targetProfile.gridState.gridOptions;
      return diff;
    }

    // Compare column definitions
    const currentCache = this.cache.get(currentProfile.id);
    const targetCache = this.cache.get(targetProfile.id);

    if (!currentCache || !targetCache || 
        currentCache.processedColumnDefs.length !== targetCache.processedColumnDefs.length) {
      diff.columnDefsChanged = true;
    } else {
      // Deep comparison of column definitions
      for (let i = 0; i < currentCache.processedColumnDefs.length; i++) {
        const current = currentCache.processedColumnDefs[i];
        const target = targetCache.processedColumnDefs[i];

        if (JSON.stringify(current) !== JSON.stringify(target)) {
          diff.columnDefsChanged = true;
          diff.changes.modifiedColumns.push(current.field || '');
        }
      }
    }

    // Compare states using hashes
    diff.columnStateChanged = currentCache?.columnStateHash !== targetCache?.columnStateHash;
    diff.filterModelChanged = currentCache?.filterModelHash !== targetCache?.filterModelHash;
    diff.sortModelChanged = currentCache?.sortModelHash !== targetCache?.sortModelHash;

    // Compare grid options
    diff.gridOptionsChanged = JSON.stringify(currentProfile.gridState.gridOptions) !== 
                             JSON.stringify(targetProfile.gridState.gridOptions);

    return diff;
  }

  // Apply profile with optimizations
  async applyProfile(
    gridApi: GridApi,
    profile: GridProfile,
    currentProfile: GridProfile | null,
    options: {
      showTransition?: boolean;
      onProgress?: (progress: number) => void;
    } = {}
  ) {
    const startTime = performance.now();
    // perfMonitor.mark('profile-switch-start');

    // Ensure profile is preprocessed
    if (!this.cache.has(profile.id)) {
      await this.processProfile(profile);
    }

    const cached = this.cache.get(profile.id);
    if (!cached) {
      console.error('[ProfileOptimizer] Failed to cache profile:', profile.id);
      return;
    }

    // Calculate diff
    const diff = this.calculateDiff(currentProfile, profile);

    // Show transition if needed
    if (options.showTransition) {
      this.showTransitionEffect(gridApi);
    }

    // Optimistic update - immediately update visual indicators
    this.currentProfileId = profile.id;

    // Cancel any pending updates from previous profile switches
    if (this.updateFrameId !== null) {
      cancelAnimationFrame(this.updateFrameId);
      this.updateFrameId = null;
    }
    this.pendingUpdates.clear();

    // Apply updates synchronously for consistency
    let progress = 0;
    const totalSteps = Object.keys(diff).filter(key => diff[key as keyof ProfileDiff]).length;

    try {
      // Step 1: Clear existing state to prevent bleeding
      if (diff.columnDefsChanged || diff.columnStateChanged) {
        // Force a clean state by resetting filters and sorts
        gridApi.setFilterModel({});
        // Clear sort using applyColumnState with empty sort state
        const allColumns = gridApi.getColumns();
        if (allColumns) {
          const clearSortState = allColumns.map(col => ({
            colId: col.getColId(),
            sort: null,
            sortIndex: null
          }));
          gridApi.applyColumnState({ state: clearSortState });
        }
      }

      // Collect all updates to apply in a single transaction
      const gridUpdates: any = {};
      let needsHeaderRefresh = false;
      let needsFilterRefresh = false;

      // Step 1: Prepare column definitions if changed OR if this is the initial load
      const isInitialLoad = currentProfile === null;
      if ((diff.columnDefsChanged || isInitialLoad) && cached.processedColumnDefs.length > 0) {
        // IMPORTANT: When updating column definitions, AG-Grid resets column state
        // So we need to capture the target column state and ensure it's applied after
        // the column definitions are updated

        // Get current defaultColDef to ensure it's preserved
        const defaultColDef = gridApi.getGridOption('defaultColDef') || {
          minWidth: 100,
          filter: true,
          floatingFilter: true,
          enableValue: true,
          enableRowGroup: true,
          enablePivot: true,
          resizable: true,
          sortable: true,
          useValueFormatterForExport: true,
        };

        // Remove any hide properties from column definitions
        // Column visibility should only be controlled by column state
        // Also ensure we start with clean column definitions to prevent style bleeding
        const cleanedColumnDefs = cached.processedColumnDefs.map(col => {
          // Create a fresh copy to avoid any reference issues
          const cleaned = { ...col };
          delete (cleaned as any).hide;

          // Ensure styles are properly set (not carrying over from previous profile)
          if (cleaned.cellStyle && typeof cleaned.cellStyle === 'function') {
            // Re-create the function to ensure it's not a stale reference
            const originalFunc = cleaned.cellStyle;
            cleaned.cellStyle = originalFunc;
          }

          if (cleaned.headerStyle && typeof cleaned.headerStyle === 'function') {
            // Re-create the function to ensure it's not a stale reference
            const originalFunc = cleaned.headerStyle;
            cleaned.headerStyle = originalFunc;
          }

          return cleaned;
        });

        gridUpdates.columnDefs = cleanedColumnDefs;
        gridUpdates.defaultColDef = defaultColDef;

        // Check if we need header refresh
        needsHeaderRefresh = cached.processedColumnDefs.some(col => 
          col.floatingFilter !== false || col.headerStyle
        );

        progress++;
        options.onProgress?.(progress / totalSteps);
      }

      // Apply all grid updates in one call
      if (Object.keys(gridUpdates).length > 0) {
        console.log('[ProfileOptimizer] Applying grid updates:', {
          hasColumnDefs: !!gridUpdates.columnDefs,
          columnDefsCount: gridUpdates.columnDefs?.length,
          hasDefaultColDef: !!gridUpdates.defaultColDef
        });
        gridApi.updateGridOptions(gridUpdates);

        // IMPORTANT: If we updated column definitions, we need to apply column state immediately
        // AG-Grid resets column state when columnDefs change, so we can't defer this
        needsHeaderRefresh = true;

        // Give AG-Grid a moment to process the column definition changes
        await new Promise(resolve => setTimeout(resolve, 0));
      }

      // Apply column state, sort, and filter immediately (not deferred)
      // This ensures proper state application after column definition changes

      // 1. Column state (includes visibility, width, position)
      // IMPORTANT: Always apply column state if we updated column definitions
      // because AG-Grid resets state when columnDefs change
      if ((diff.columnStateChanged || diff.columnDefsChanged || isInitialLoad) && profile.gridState.columnState) {
        console.log('[ProfileOptimizer] Applying column state:', {
          stateCount: profile.gridState.columnState.length,
          hiddenColumns: profile.gridState.columnState.filter((col: any) => col.hide).length,
          visibleColumns: profile.gridState.columnState.filter((col: any) => !col.hide).length,
          sample: profile.gridState.columnState.slice(0, 3)
        });

        // Log the state we're about to apply
        console.log('[ProfileOptimizer] Column state to apply:', 
          profile.gridState.columnState.map((s: any) => ({
            colId: s.colId,
            hide: s.hide,
            width: s.width,
            pinned: s.pinned
          }))
        );

        const result = gridApi.applyColumnState({
          state: profile.gridState.columnState,
          applyOrder: true
        });

        console.log('[ProfileOptimizer] applyColumnState result:', result);

        // Verify the state was applied
        const appliedState = gridApi.getColumnState();
        const allColumns = gridApi.getColumns();
        console.log('[ProfileOptimizer] Column state after apply:', {
          stateCount: appliedState.length,
          hiddenColumns: appliedState.filter(col => col.hide).length,
          visibleColumns: appliedState.filter(col => !col.hide).length,
          appliedSuccessfully: result === true,
          actualVisibleColumns: allColumns?.filter(c => c.isVisible()).length,
          actualColumns: allColumns?.map(c => ({
            colId: c.getColId(),
            visible: c.isVisible()
          }))
        });

        progress++;
        options.onProgress?.(progress / totalSteps);
      }

      // 2. Sort model (part of column state)
      if (diff.sortModelChanged && profile.gridState.sortModel?.length > 0) {
        const sortState = profile.gridState.sortModel.map(sort => ({
          colId: sort.colId,
          sort: sort.sort,
          sortIndex: sort.sortIndex
        }));
        gridApi.applyColumnState({ state: sortState });
        progress++;
        options.onProgress?.(progress / totalSteps);
      }

      // 3. Filter model (apply after column state)
      if (diff.filterModelChanged) {
        gridApi.setFilterModel(profile.gridState.filterModel || {});
        needsFilterRefresh = true;
        progress++;
        options.onProgress?.(progress / totalSteps);
      }

      // 4. Single refresh at the end if needed
      if (needsHeaderRefresh || needsFilterRefresh) {
        requestAnimationFrame(() => {
          if (needsHeaderRefresh) {
            gridApi.refreshHeader();
          }
          if (needsFilterRefresh) {
            // In AG-Grid v33, refreshFilters doesn't exist
            // Instead, we notify that filters have changed
            gridApi.onFilterChanged();
          }
        });
      }

      // Apply grid options if changed
      if (diff.gridOptionsChanged && profile.gridState.gridOptions) {
        const options = profile.gridState.gridOptions!;
        if (options.rowHeight) {
          gridApi.resetRowHeights();
          gridApi.setGridOption('rowHeight', options.rowHeight);
        }
        if (options.headerHeight) {
          gridApi.setGridOption('headerHeight', options.headerHeight);
        }
        progress++;
        options.onProgress?.(progress / totalSteps);
      }
    } catch (error) {
      console.error('[ProfileOptimizer] Error switching profile:', error);
      // Re-throw to let caller handle
      throw error;
    }

    // Log performance metrics
    const endTime = performance.now();
    const switchTime = endTime - startTime;

    console.log('[ProfileOptimizer] Profile switch completed:', {
      profileId: profile.id,
      profileName: profile.name,
      switchTime: `${switchTime.toFixed(1)}ms`,
      operations: {
        columnDefsChanged: diff.columnDefsChanged,
        columnStateChanged: diff.columnStateChanged,
        filterModelChanged: diff.filterModelChanged,
        sortModelChanged: diff.sortModelChanged,
        gridOptionsChanged: diff.gridOptionsChanged
      }
    });

    // perfMonitor.measure('profileSwitchTime', 'profile-switch-start');
  }

  private batchUpdates(callback: () => void) {
    // Cancel any pending frame
    if (this.updateFrameId !== null) {
      cancelAnimationFrame(this.updateFrameId);
    }

    callback();

    // Process updates in next animation frame
    this.updateFrameId = requestAnimationFrame(() => {
      this.processPendingUpdates();
    });
  }

  private scheduleUpdate(key: string, update: () => void, priority: number = 0) {
    this.pendingUpdates.set(key, { update, priority });
  }

  private processPendingUpdates() {
    // Sort updates by priority
    const updates = Array.from(this.pendingUpdates.entries())
      .sort((a, b) => a[1].priority - b[1].priority);

    // Execute updates
    updates.forEach(([key, { update }]) => {
      try {
        update();
      } catch (error) {
        console.error(`[ProfileOptimizer] Error applying ${key}:`, error);
      }
    });

    this.pendingUpdates.clear();
    this.updateFrameId = null;
  }

  private showTransitionEffect(_gridApi: GridApi) {
    // Get the grid wrapper element
    const gridElement = document.querySelector('.ag-root-wrapper') as HTMLElement;
    if (!gridElement) return;

    // Use CSS classes for better performance
    gridElement.classList.add('profile-switching');

    // Remove class after animation completes
    requestAnimationFrame(() => {
      requestAnimationFrame(() => {
        gridElement.classList.remove('profile-switching');
      });
    });
  }

  private hashObject(obj: any): string {
    return JSON.stringify(obj).split('').reduce((a, b) => {
      a = ((a << 5) - a) + b.charCodeAt(0);
      return a & a;
    }, 0).toString(36);
  }

  // Clear cache for a specific profile
  clearCache(profileId?: string) {
    if (profileId) {
      this.cache.delete(profileId);
    } else {
      this.cache.clear();
    }
  }

  // Preload all profiles
  async preloadAllProfiles(profiles: GridProfile[]) {
    // Process profiles in batches during idle time
    const batchSize = 2;
    for (let i = 0; i < profiles.length; i += batchSize) {
      const batch = profiles.slice(i, i + batchSize);

      if ('requestIdleCallback' in window) {
        await new Promise(resolve => {
          requestIdleCallback(() => {
            batch.forEach(profile => this.processProfile(profile));
            resolve(undefined);
          }, { timeout: 1000 });
        });
      } else {
        batch.forEach(profile => this.processProfile(profile));
        await new Promise(resolve => setTimeout(resolve, 50));
      }
    }
  }
}

export const profileOptimizer = new ProfileOptimizer();

// Export to window for debugging
