    fontStyle?: 'normal' | 'italic';
    textDecoration?: 'none' | 'underline' | 'line-through';
    textAlign?: 'left' | 'center' | 'right';
    border?: {
      width: number;
      style: 'solid' | 'dashed' | 'dotted' | 'double';
      color: string;
      sides: {
        top: boolean;
        right: boolean;
        bottom: boolean;
        left: boolean;
      };
    };
    padding?: {
      top: number;
      right: number;
      bottom: number;
      left: number;
    };
  };
}

// Type for serialized column customizations
export interface ColumnCustomization {
  // Identity
  field: string;

  // Basic properties (only if changed from defaults)
  headerName?: string;
  width?: number;
  minWidth?: number;
  maxWidth?: number;
  flex?: number;

  // Boolean flags (only if changed from defaults)
  sortable?: boolean;
  resizable?: boolean;
  editable?: boolean;
  filter?: boolean;
  floatingFilter?: boolean;
  hide?: boolean;
  lockPosition?: boolean;
  lockVisible?: boolean;
  lockPinned?: boolean;
  suppressHeaderMenuButton?: boolean;

  // Positioning
  pinned?: 'left' | 'right' | null;

  // Styles (serialized format)
  cellStyle?: {
    type: 'static' | 'function';
    value?: React.CSSProperties;
    formatString?: string; // For conditional formatting
    baseStyle?: React.CSSProperties;
  };
  headerStyle?: {
    type: 'static' | 'function';
    regular?: React.CSSProperties;
    floating?: React.CSSProperties | null;
  };

  // CSS Classes
  cellClass?: string | string[];
  headerClass?: string | string[];

  // Formatters (metadata only)
  valueFormatter?: {
    type: 'excel' | 'visual' | 'custom';
    formatString?: string;
    // Visual editor data (for full round-trip editing)
    rules?: SerializedFormattingRule[];
    defaultFallback?: SerializedDefaultFallback;
  };

  // Cell data type
  cellDataType?: 'text' | 'number' | 'date' | 'boolean';

  // Other commonly customized properties
  headerTooltip?: string;
  tooltipField?: string;
  headerCheckboxSelection?: boolean;
  checkboxSelection?: boolean;
  rowGroup?: boolean;
  pivot?: boolean;
  aggFunc?: string | null;

  // Alignment classes
  cellClassRules?: Record<string, any>;
}

// Default values for comparison
const COLUMN_DEFAULTS: Partial<ColDef> = {
  sortable: true,
  resizable: true,
  filter: true,
  floatingFilter: true,
  editable: false,
  hide: false,
  lockPosition: false,
  lockVisible: false,
  lockPinned: false,
  suppressHeaderMenuButton: false,
  minWidth: 100,
};

/**
 * Extract only the customized properties from a column definition
 */
function extractCustomizations(col: ColDef, baseCol?: ColDef): ColumnCustomization {
  const customization: ColumnCustomization = {
    field: col.field!,
  };

  // Compare against base column or defaults
  const compareBase = baseCol || COLUMN_DEFAULTS;

  // Basic properties
  if (col.headerName !== baseCol?.headerName && col.headerName !== col.field) {
    customization.headerName = col.headerName;
  }

  // Width properties
  if (col.width !== undefined && col.width !== compareBase.width) {
    customization.width = col.width;
  }
  if (col.minWidth !== undefined && col.minWidth !== compareBase.minWidth) {
    customization.minWidth = col.minWidth;
  }
  if (col.maxWidth !== undefined && col.maxWidth !== compareBase.maxWidth) {
    customization.maxWidth = col.maxWidth;
  }
  if (col.flex !== undefined && col.flex !== compareBase.flex) {
    customization.flex = col.flex;
  }

  // Boolean flags - only save if different from defaults
  if (col.sortable !== undefined && col.sortable !== compareBase.sortable) {
    customization.sortable = col.sortable;
  }
  if (col.resizable !== undefined && col.resizable !== compareBase.resizable) {
    customization.resizable = col.resizable;
  }
  if (col.editable !== undefined && col.editable !== compareBase.editable) {
    customization.editable = col.editable;
  }
  if (col.filter !== undefined && col.filter !== compareBase.filter) {
    customization.filter = col.filter;
  }
  if (col.floatingFilter !== undefined && col.floatingFilter !== compareBase.floatingFilter) {
    customization.floatingFilter = col.floatingFilter;
  }
  if (col.hide !== undefined && col.hide !== compareBase.hide) {
    customization.hide = col.hide;
  }
  if (col.lockPosition !== undefined && col.lockPosition !== compareBase.lockPosition) {
    customization.lockPosition = col.lockPosition;
  }
  if (col.lockVisible !== undefined && col.lockVisible !== compareBase.lockVisible) {
    customization.lockVisible = col.lockVisible;
  }
  if (col.lockPinned !== undefined && col.lockPinned !== compareBase.lockPinned) {
    customization.lockPinned = col.lockPinned;
  }
  if (col.suppressHeaderMenuButton !== undefined && col.suppressHeaderMenuButton !== compareBase.suppressHeaderMenuButton) {
    customization.suppressHeaderMenuButton = col.suppressHeaderMenuButton;
  }

  // Positioning
  if (col.pinned !== undefined && col.pinned !== null) {
    customization.pinned = col.pinned;
  }

  // Cell style
  if (col.cellStyle) {
    if (typeof col.cellStyle === 'function') {
      // Check if it has our metadata
      const metadata = (col.cellStyle as any).__formatString;
      const baseStyle = (col.cellStyle as any).__baseStyle;
      console.log('[ColumnSerializer] Saving cellStyle function:', {
        field: col.field,
        hasMetadata: !!metadata,
        formatString: metadata,
        hasBaseStyle: !!baseStyle,
        baseStyle
      });
      customization.cellStyle = {
        type: 'function',
        formatString: metadata,
        baseStyle: baseStyle
      };
    } else {
      customization.cellStyle = {
        type: 'static',
        value: col.cellStyle as React.CSSProperties
      };
    }
  }

  // Header style
  if (col.headerStyle) {
    if (typeof col.headerStyle === 'function') {
      // Try to extract the style config if it's our custom format
      const testRegular = (col.headerStyle as any)({ floatingFilter: false });
      const testFloating = (col.headerStyle as any)({ floatingFilter: true });

      customization.headerStyle = {
        type: 'function',
        regular: testRegular || undefined,
        floating: testFloating || undefined
      };
    } else if (typeof col.headerStyle === 'object') {
      const styleObj = col.headerStyle as any;
      if (styleObj._isHeaderStyleConfig) {
        customization.headerStyle = {
          type: 'function',
          regular: styleObj.regular,
          floating: styleObj.floating
        };
      } else {
        customization.headerStyle = {
          type: 'static',
          regular: col.headerStyle as React.CSSProperties
        };
      }
    }
  }

  // CSS Classes
  if (col.cellClass) {
    customization.cellClass = col.cellClass;
  }
  if (col.headerClass) {
    customization.headerClass = col.headerClass;
  }

  // Value formatter
  if (col.valueFormatter) {
    console.log('[ColumnSerializer] Processing valueFormatter for field:', col.field, {
      type: typeof col.valueFormatter,
      isFunction: typeof col.valueFormatter === 'function'
    });

    if (typeof col.valueFormatter === 'function') {
      const formatString = (col.valueFormatter as any).__formatString;
      const formatterType = (col.valueFormatter as any).__formatterType;
      const visualRules = (col.valueFormatter as any).__visualRules;
      const visualDefaultFallback = (col.valueFormatter as any).__visualDefaultFallback;

      console.log('[ColumnSerializer] Function formatter metadata:', {
        field: col.field,
        hasFormatString: !!formatString,
        formatString: formatString,
        formatterType: formatterType,
        hasVisualRules: !!visualRules,
        hasDefaultFallback: !!visualDefaultFallback
      });

      if (formatString) {
        customization.valueFormatter = {
          type: formatterType || 'excel',
          formatString,
          // Include visual editor data if available
          ...(visualRules && { rules: visualRules }),
          ...(visualDefaultFallback && { defaultFallback: visualDefaultFallback })
        };
        console.log('[ColumnSerializer]  Serialized formatter for field:', col.field);
      } else {
        console.warn('[ColumnSerializer]  Formatter function has no __formatString metadata for field:', col.field);
        // Try to serialize as custom formatter without format string
        customization.valueFormatter = {
          type: 'custom',
          formatString: `// Custom formatter function for ${col.field}\n// (Function body not serializable)`
        };
        console.log('[ColumnSerializer]  Serialized as custom formatter without metadata for field:', col.field);
      }
    } else if (typeof col.valueFormatter === 'object') {
      // Handle saved formatter config
      const config = col.valueFormatter as Record<string, unknown>;
      console.log('[ColumnSerializer] Object formatter config:', config);

      if (config._isFormatterConfig) {
        customization.valueFormatter = {
          type: (config.type as 'excel' | 'visual' | 'custom') || 'excel',
          formatString: config.formatString as string,
          ...(config.rules && { rules: config.rules as SerializedFormattingRule[] }),
          ...(config.defaultFallback && { defaultFallback: config.defaultFallback as SerializedDefaultFallback })
        };
        console.log('[ColumnSerializer]  Serialized object formatter for field:', col.field);
      } else {
        console.warn('[ColumnSerializer]  Object formatter is not a recognized config for field:', col.field);
      }
    } else {
      console.warn('[ColumnSerializer]  Unknown formatter type for field:', col.field, typeof col.valueFormatter);
    }
  }

  // Cell data type
  if (col.cellDataType && col.cellDataType !== baseCol?.cellDataType) {
    customization.cellDataType = col.cellDataType;
  }

  // Other properties
  if (col.headerTooltip) customization.headerTooltip = col.headerTooltip;
  if (col.tooltipField) customization.tooltipField = col.tooltipField;
  if (col.headerCheckboxSelection !== undefined) customization.headerCheckboxSelection = col.headerCheckboxSelection;
  if (col.checkboxSelection !== undefined) customization.checkboxSelection = col.checkboxSelection;
  if (col.rowGroup !== undefined) customization.rowGroup = col.rowGroup;
  if (col.pivot !== undefined) customization.pivot = col.pivot;
  if (col.aggFunc !== undefined) customization.aggFunc = col.aggFunc;
  if (col.cellClassRules) customization.cellClassRules = col.cellClassRules;

  return customization;
}

/**
 * Serialize column customizations - only saves properties that differ from defaults
 */
export function serializeColumnCustomizations(
  columns: ColDef[], 
  baseColumns?: ColDef[]
): Record<string, ColumnCustomization> {
  const customizations: Record<string, ColumnCustomization> = {};

  columns.forEach((col, _index) => {
    if (!col.field) return;

    const baseCol = baseColumns?.find(base => base.field === col.field);
    const extracted = extractCustomizations(col, baseCol);

    // Only include if there are actual customizations beyond the field
    if (Object.keys(extracted).length > 1) {
      customizations[col.field] = extracted;
    }
  });

  return customizations;
}


/**
 * Ensure a column has a cellStyle function if its valueFormatter has conditional styling
 */
function ensureCellStyleForConditionalFormatting(merged: ColDef, _custom: ColumnCustomization): void {
  console.log('[ensureCellStyleForConditionalFormatting] Checking column:', {
    field: merged.field,
    hasValueFormatter: !!merged.valueFormatter,
    valueFormatterType: typeof merged.valueFormatter
  });

  // Check if valueFormatter has conditional styling
  if (merged.valueFormatter && typeof merged.valueFormatter === 'function') {
    const formatString = (merged.valueFormatter as any).__formatString;

    console.log('[ensureCellStyleForConditionalFormatting] valueFormatter details:', {
      field: merged.field,
      hasFormatString: !!formatString,
      formatString: formatString,
      hasConditionalStyling: formatString ? hasConditionalStyling(formatString) : false
    });

    if (formatString && hasConditionalStyling(formatString)) {
      // Check if cellStyle already exists and is properly configured
      if (!merged.cellStyle || 
          (typeof merged.cellStyle === 'function' && 
           (merged.cellStyle as any).__formatString !== formatString)) {

        console.log('[ensureCellStyleForConditionalFormatting] Creating cellStyle:', {
          field: merged.field,
          formatString,
          hasExistingCellStyle: !!merged.cellStyle,
          existingCellStyleType: typeof merged.cellStyle
        });

        // Extract base style if it exists
        let baseStyle: React.CSSProperties = {};
        if (merged.cellStyle) {
          if (typeof merged.cellStyle === 'object') {
            baseStyle = merged.cellStyle;
          } else if (typeof merged.cellStyle === 'function' && (merged.cellStyle as any).__baseStyle) {
            baseStyle = (merged.cellStyle as any).__baseStyle;
          }
        }

        // Create cellStyle function
        const styleFunc = createCellStyleFunction(formatString, baseStyle);

        // Attach metadata for future serialization
        Object.defineProperty(styleFunc, '__formatString', { 
          value: formatString, 
          writable: false,
          enumerable: false,
          configurable: true
        });
        Object.defineProperty(styleFunc, '__baseStyle', { 
          value: baseStyle, 
          writable: false,
          enumerable: false,
          configurable: true
        });

        merged.cellStyle = styleFunc;
      }
    }
  }
}

/**
 * Apply customizations to base columns
 */
export function deserializeColumnCustomizations(
  customizations: Record<string, ColumnCustomization>,
  baseColumns: ColDef[]
): ColDef[] {
  // Handle case where baseColumns is empty
  if (!baseColumns || baseColumns.length === 0) {
    console.warn('[ColumnSerializer] No base columns provided for deserialization');
    return [];
  }

  return baseColumns.map(baseCol => {
    const field = baseCol.field;
    if (!field || !customizations[field]) {
      // Apply defaults even if no customizations
      return {
        ...baseCol,
        ...COLUMN_DEFAULTS
      };
    }

    const custom = customizations[field];
    // Start with base column merged with defaults
    const merged: ColDef = { 
      ...COLUMN_DEFAULTS,
      ...baseCol 
    };

    // Apply basic properties
    if (custom.headerName !== undefined) merged.headerName = custom.headerName;
    if (custom.width !== undefined) merged.width = custom.width;
    if (custom.minWidth !== undefined) merged.minWidth = custom.minWidth;
    if (custom.maxWidth !== undefined) merged.maxWidth = custom.maxWidth;
    if (custom.flex !== undefined) merged.flex = custom.flex;

    // Apply boolean flags
    if (custom.sortable !== undefined) merged.sortable = custom.sortable;
    if (custom.resizable !== undefined) merged.resizable = custom.resizable;
    if (custom.editable !== undefined) merged.editable = custom.editable;
    if (custom.filter !== undefined) merged.filter = custom.filter;
    if (custom.floatingFilter !== undefined) merged.floatingFilter = custom.floatingFilter;
    // Don't apply hide property from customizations - column visibility should be controlled by column state only
    // if (custom.hide !== undefined) merged.hide = custom.hide;
    if (custom.lockPosition !== undefined) merged.lockPosition = custom.lockPosition;
    if (custom.lockVisible !== undefined) merged.lockVisible = custom.lockVisible;
    if (custom.lockPinned !== undefined) merged.lockPinned = custom.lockPinned;
    if (custom.suppressHeaderMenuButton !== undefined) merged.suppressHeaderMenuButton = custom.suppressHeaderMenuButton;

    // Apply positioning
    if (custom.pinned !== undefined) merged.pinned = custom.pinned;

    // Apply cell style
    if (custom.cellStyle) {
      if (custom.cellStyle.type === 'static' && custom.cellStyle.value) {
        merged.cellStyle = custom.cellStyle.value;
      } else if (custom.cellStyle.type === 'function' && custom.cellStyle.formatString) {
        console.log('[ColumnSerializer] Loading cellStyle function:', {
          field,
          formatString: custom.cellStyle.formatString,
          hasBaseStyle: !!custom.cellStyle.baseStyle,
          baseStyle: custom.cellStyle.baseStyle
        });
        // Recreate the cell style function
        const styleFunc = createCellStyleFunction(custom.cellStyle.formatString, custom.cellStyle.baseStyle);
        // Attach metadata for future serialization
        Object.defineProperty(styleFunc, '__formatString', { 
          value: custom.cellStyle.formatString, 
          writable: false,
          enumerable: false,
          configurable: true
        });
        Object.defineProperty(styleFunc, '__baseStyle', { 
          value: custom.cellStyle.baseStyle, 
          writable: false,
          enumerable: false,
          configurable: true
        });
        merged.cellStyle = styleFunc;
      }
    }

    // Apply header style
    if (custom.headerStyle) {
      if (custom.headerStyle.type === 'static' && custom.headerStyle.regular) {
        merged.headerStyle = custom.headerStyle.regular;
      } else if (custom.headerStyle.type === 'function') {
        // Create the header style function
        merged.headerStyle = (params: { floatingFilter?: boolean }) => {
          if (params?.floatingFilter) {
            return custom.headerStyle?.floating || null;
          }
          return custom.headerStyle?.regular || null;
        };

        // Also store as object format for persistence
        (merged.headerStyle as any)._isHeaderStyleConfig = true;
        (merged.headerStyle as any).regular = custom.headerStyle.regular;
        (merged.headerStyle as any).floating = custom.headerStyle.floating;
      }
    }

    // Apply CSS classes
    if (custom.cellClass !== undefined) merged.cellClass = custom.cellClass;
    if (custom.headerClass !== undefined) merged.headerClass = custom.headerClass;

    // Apply value formatter
    if (custom.valueFormatter && custom.valueFormatter.formatString) {
      if (custom.valueFormatter.type === 'excel' || custom.valueFormatter.type === 'visual') {
        console.log('[ColumnSerializer] Recreating formatter for field:', field, {
          formatString: custom.valueFormatter.formatString,
          type: custom.valueFormatter.type,
          hasRules: !!custom.valueFormatter.rules,
          hasDefaultFallback: !!custom.valueFormatter.defaultFallback
        });
        const formatter = createExcelFormatter(custom.valueFormatter.formatString);

        // Attach visual editor metadata if available
        if (custom.valueFormatter.rules) {
          Object.defineProperty(formatter, '__visualRules', { 
            value: custom.valueFormatter.rules, 
            writable: false,
            enumerable: false,
            configurable: true
          });
        }

        if (custom.valueFormatter.defaultFallback) {
          Object.defineProperty(formatter, '__visualDefaultFallback', { 
            value: custom.valueFormatter.defaultFallback, 
            writable: false,
            enumerable: false,
            configurable: true
          });
        }

        merged.valueFormatter = formatter;
        // Value formatter will be used for export automatically
      }
    }

    // Apply cell data type
    if (custom.cellDataType !== undefined) merged.cellDataType = custom.cellDataType;

    // Apply other properties
    if (custom.headerTooltip !== undefined) merged.headerTooltip = custom.headerTooltip;
    if (custom.tooltipField !== undefined) merged.tooltipField = custom.tooltipField;
    if (custom.headerCheckboxSelection !== undefined) merged.headerCheckboxSelection = custom.headerCheckboxSelection;
    if (custom.checkboxSelection !== undefined) merged.checkboxSelection = custom.checkboxSelection;
    if (custom.rowGroup !== undefined) merged.rowGroup = custom.rowGroup;
    if (custom.pivot !== undefined) merged.pivot = custom.pivot;
    if (custom.aggFunc !== undefined) merged.aggFunc = custom.aggFunc;
    if (custom.cellClassRules !== undefined) merged.cellClassRules = custom.cellClassRules;

    // Ensure cellStyle is created if valueFormatter has conditional styling
    ensureCellStyleForConditionalFormatting(merged, custom);

    return merged;
  });
}

/**
 * Calculate the size of serialized customizations in bytes
 */
export function calculateCustomizationSize(customizations: Record<string, ColumnCustomization>): number {
  return JSON.stringify(customizations).length;
}

/**
 * Get a summary of what's customized
 */
export function getCustomizationSummary(customizations: Record<string, ColumnCustomization>): {
  totalColumns: number;
  customizedColumns: number;
  properties: Record<string, number>;
} {
  const propertyCount: Record<string, number> = {};

  Object.values(customizations).forEach(custom => {
    Object.keys(custom).forEach(key => {
      if (key !== 'field') {
        propertyCount[key] = (propertyCount[key] || 0) + 1;
      }
    });
  });

  return {
    totalColumns: Object.keys(customizations).length,
    customizedColumns: Object.keys(customizations).length,
    properties: propertyCount
  };
}

import { useProfileStore } from './profile.store';
import { logStorageAnalysis } from './storageAnalyzer';

/**
 * Migrate all profiles to use lightweight column serialization
 * This will convert all existing profiles that still use full columnDefs
 * to the new lightweight format
 */
export function migrateAllProfilesToLightweight() {
  const store = useProfileStore.getState();
  const profiles = store.profiles;

  console.group(' Migrating profiles to lightweight format');

  let migratedCount = 0;

  profiles.forEach(profile => {
    // Check if profile has full columnDefs but no lightweight format
    if (profile.gridState.columnDefs && 
        profile.gridState.columnDefs.length > 0 && 
        !profile.gridState.columnCustomizations) {

      console.log(` Migrating profile: ${profile.name} (${profile.id})`);

      // Get the column definitions using the store method (handles all formats)
      const columnDefs = store.getColumnDefs(profile.id);

      if (columnDefs) {
        // Save using the lightweight format
        // We need to temporarily set this as the active profile to use saveColumnCustomizations
        const currentActiveId = store.activeProfileId;
        store.setActiveProfile(profile.id);

        // Save the columns in lightweight format
        store.saveColumnCustomizations(columnDefs, columnDefs);

        // Restore the previous active profile
        store.setActiveProfile(currentActiveId);

        migratedCount++;
        console.log(` Successfully migrated profile: ${profile.name}`);
      }
    } else if (profile.gridState.columnCustomizations) {
      console.log(`  Profile already using lightweight format: ${profile.name}`);
    } else {
      console.log(`  Profile has no column definitions: ${profile.name}`);
    }
  });

  console.log(`\n Migration complete: ${migratedCount} profiles migrated`);
  console.groupEnd();

  // Show storage analysis after migration
  console.log('\n');
  logStorageAnalysis();
}

/**
 * Force refresh all profiles to ensure they're using the latest format
 * This will reload column definitions and re-save them
 */
export function refreshAllProfiles() {
  const store = useProfileStore.getState();
  const profiles = store.profiles;
  const currentActiveId = store.activeProfileId;

  console.group(' Refreshing all profiles');

  profiles.forEach(profile => {
    const columnDefs = store.getColumnDefs(profile.id);
    if (columnDefs && columnDefs.length > 0) {
      console.log(` Refreshing profile: ${profile.name}`);

      // Temporarily switch to this profile
      store.setActiveProfile(profile.id);

      // Re-save with current format
      store.saveColumnCustomizations(columnDefs, profile.gridState.baseColumnDefs || columnDefs);

      console.log(` Refreshed: ${profile.name}`);
    }
  });

  // Restore original active profile
  store.setActiveProfile(currentActiveId);

  console.groupEnd();
}

// Export functions to window for easy console access
if (typeof window !== 'undefined') {
  (window as any).migrateProfiles = migrateAllProfilesToLightweight;
  (window as any).refreshProfiles = refreshAllProfiles;
}

import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import { ColDef, ColumnState, FilterModel, SortModelItem } from 'ag-grid-community';
import { ColumnCustomization, serializeColumnCustomizations, deserializeColumnCustomizations } from './columnSerializer';

interface PersistedState {
  profiles?: GridProfile[];
  activeProfileId?: string;
  lastExportedAt?: number;
  autoSave?: boolean;
}

// Profile data structure
export interface GridProfile {
  id: string;
  name: string;
  createdAt: number;
  updatedAt: number;
  isDefault?: boolean;
  description?: string;
  gridState: {
    // Column definitions with all customizations (legacy - for backward compatibility)
    columnDefs?: ColDef[];
    // Lightweight column customizations (new format)
    columnCustomizations?: Record<string, ColumnCustomization>;
    // Base column definitions snapshot (for reference)
    baseColumnDefs?: ColDef[];
    // Column state (order, visibility, width, etc.)
    columnState: ColumnState[];
    // Filter model
    filterModel: FilterModel;
    // Sort model
    sortModel: SortModelItem[];
    // Templates
    templates?: Record<string, unknown>[];
    // Column customization templates
    columnTemplates?: Record<string, unknown>[];
    // UI preferences
    font?: string;
    // Other grid options
    gridOptions?: {
      rowHeight?: number;
      headerHeight?: number;
      floatingFiltersHeight?: number;
      groupHeaderHeight?: number;
      pivotHeaderHeight?: number;
      pivotGroupHeaderHeight?: number;
      animateRows?: boolean;
      pagination?: boolean;
      paginationPageSize?: number;
    };
  };
}

interface ProfileStore {
  // Profiles
  profiles: GridProfile[];
  activeProfileId: string;

  // Migration state
  migrationPending: boolean;
  migrationInProgress: boolean;

  // Actions
  createProfile: (name: string, description?: string) => GridProfile;
  updateProfile: (profileId: string, updates: Partial<GridProfile>) => void;
  deleteProfile: (profileId: string) => void;
  renameProfile: (profileId: string, newName: string) => void;
  setActiveProfile: (profileId: string) => void;
  getActiveProfile: () => GridProfile | undefined;
  duplicateProfile: (profileId: string, newName: string) => GridProfile;
  importProfile: (profileData: GridProfile) => void;
  exportProfile: (profileId: string) => GridProfile | undefined;
  saveCurrentState: (gridState: Partial<GridProfile['gridState']>) => void;

  // Lightweight column serialization
  saveColumnCustomizations: (columnDefs: ColDef[], baseColumnDefs?: ColDef[]) => void;
  getColumnDefs: (profileId?: string) => ColDef[] | undefined;

  // Auto-save
  autoSave: boolean;
  setAutoSave: (enabled: boolean) => void;

  // Migration
  performDeferredMigration: () => Promise<void>;
}

// Default profile ID
const DEFAULT_PROFILE_ID = 'default-profile';

// Create default profile
const createDefaultProfile = (): GridProfile => ({
  id: DEFAULT_PROFILE_ID,
  name: 'Default',
  createdAt: Date.now(),
  updatedAt: Date.now(),
  isDefault: true,
  gridState: {
    columnCustomizations: {},
    columnState: [],
    filterModel: {},
    sortModel: []
  }
});

// Deferred migration function
async function performMigration(state: PersistedState): Promise<PersistedState> {
        // perfMonitor.mark('migration-start');

  const newState = { ...state };

  // Migrate from version 1 to 2 (clean invalid properties)
  if (state.profiles && Array.isArray(state.profiles)) {
    newState.profiles = state.profiles.map((profile: GridProfile) => {
      const newProfile = { ...profile };

      if (profile.gridState && profile.gridState.columnDefs) {
        // Clean invalid properties from each column definition
        newProfile.gridState.columnDefs = profile.gridState.columnDefs.map((col: ColDef) => {
          const cleaned = { ...col } as any;
          // Remove invalid properties that AG-Grid doesn't recognize
          delete cleaned.valueFormat;
          delete cleaned._hasFormatter;
          delete cleaned.excelFormat;
          return cleaned;
        });
      }

      // Convert headerStyle objects to new format
      if (newProfile.gridState && newProfile.gridState.columnDefs) {
        newProfile.gridState.columnDefs = newProfile.gridState.columnDefs.map((col: ColDef) => {
          if (col.headerStyle && typeof col.headerStyle === 'object' && !(col.headerStyle as any)._isHeaderStyleConfig) {
            // Convert old format to new format
            col.headerStyle = {
              _isHeaderStyleConfig: true,
              regular: col.headerStyle,
              floating: null
            } as any;
          }
          return col;
        });
      }

      // Convert to lightweight format if needed
      if (newProfile.gridState && newProfile.gridState.columnDefs && 
          newProfile.gridState.columnDefs.length > 0 && 
          !newProfile.gridState.columnCustomizations) {

        const baseColumns = newProfile.gridState.columnDefs.map((col: ColDef) => ({
          field: col.field,
          headerName: col.field,
          cellDataType: col.cellDataType
        }));

        const customizations = serializeColumnCustomizations(newProfile.gridState.columnDefs, baseColumns);

        console.log('[ProfileStore Migration] Converting profile to lightweight format:', {
          profileId: newProfile.id,
          profileName: newProfile.name,
          originalSize: JSON.stringify(newProfile.gridState.columnDefs).length,
          newSize: JSON.stringify(customizations).length,
          reduction: `${Math.round((1 - JSON.stringify(customizations).length / JSON.stringify(newProfile.gridState.columnDefs).length) * 100)}%`
        });

        // Update to new format
        newProfile.gridState.columnCustomizations = customizations;
        newProfile.gridState.baseColumnDefs = baseColumns;
        // Remove legacy columnDefs after migration
        delete newProfile.gridState.columnDefs;
      }

      return newProfile;
    });
  }

      // perfMonitor.mark('migration-end');
    // perfMonitor.measure('migrationTime', 'migration-start', 'migration-end');

  return newState;
}

export const useProfileStore = create<ProfileStore>()(
  persist(
    (set, get) => ({
      // Initial state
      profiles: [createDefaultProfile()],
      activeProfileId: DEFAULT_PROFILE_ID,
      autoSave: true,
      migrationPending: false,
      migrationInProgress: false,

      // Create new profile
      createProfile: (name, description) => {
        const state = get();

        // Get the default profile to clone its grid state
        const defaultProfile = state.profiles.find(p => p.id === DEFAULT_PROFILE_ID);

        const newProfile: GridProfile = {
          id: `profile-${Date.now()}`,
          name,
          description,
          createdAt: Date.now(),
          updatedAt: Date.now(),
          gridState: defaultProfile?.gridState ? {
            // Clone the default profile's grid state using new lightweight format
            columnCustomizations: { ...(defaultProfile.gridState.columnCustomizations || {}) },
            baseColumnDefs: [...(defaultProfile.gridState.baseColumnDefs || [])],
            columnState: [...(defaultProfile.gridState.columnState || [])],
            filterModel: {},  // Start with no filters
            sortModel: []     // Start with no sorting
          } : {
            // Fallback to empty state if default profile not found
            columnCustomizations: {},
            baseColumnDefs: [],
            columnState: [],
            filterModel: {},
            sortModel: []
          }
        };

        console.log('[ProfileStore] Creating new profile with default state:', {
          profileName: name,
          defaultProfileFound: !!defaultProfile,
          customizationsCount: Object.keys(newProfile.gridState.columnCustomizations || {}).length
        });

        set(state => ({
          profiles: [...state.profiles, newProfile]
        }));

        return newProfile;
      },

      // Update existing profile
      updateProfile: (profileId, updates) => {
        console.log('[ProfileStore] updateProfile called:', {
          profileId,
          updates,
          hasGridState: !!updates.gridState,
          columnDefsCount: updates.gridState?.columnDefs?.length
        });

        set(state => {
          const updatedProfiles = state.profiles.map(profile => {
            if (profile.id === profileId) {
              const updatedProfile = {
                ...profile,
                ...updates,
                updatedAt: Date.now(),
                gridState: updates.gridState
                  ? {
                      ...profile.gridState,
                      ...updates.gridState
                    }
                  : profile.gridState
              };
              console.log('[ProfileStore] Profile updated:', {
                profileId,
                profileName: updatedProfile.name,
                columnDefsCount: updatedProfile.gridState?.columnDefs?.length,
                hasColumnState: !!updatedProfile.gridState?.columnState
              });
              return updatedProfile;
            }
            return profile;
          });

          return { profiles: updatedProfiles };
        });
      },

      // Delete profile (cannot delete default or active profile)
      deleteProfile: (profileId) => {
        const { activeProfileId, profiles } = get();

        // Cannot delete default profile
        if (profileId === DEFAULT_PROFILE_ID) {
          console.warn('Cannot delete default profile');
          return;
        }

        const profileToDelete = profiles.find(p => p.id === profileId);
        if (!profileToDelete) {
          console.warn(`Profile ${profileId} not found`);
          return;
        }

        console.log('[ProfileStore] Deleting profile:', {
          profileId,
          profileName: profileToDelete.name,
          isActiveProfile: profileId === activeProfileId,
          willSwitchToDefault: profileId === activeProfileId
        });

        // If deleting active profile, switch to default
        if (profileId === activeProfileId) {
          console.log('[ProfileStore] Switching to default profile after deletion');
          set({ activeProfileId: DEFAULT_PROFILE_ID });
        }

        // Remove the profile from the list
        set(state => ({
          profiles: state.profiles.filter(p => p.id !== profileId)
        }));

        console.log('[ProfileStore] Profile deleted successfully:', {
          deletedProfile: profileToDelete.name,
          newActiveProfileId: profileId === activeProfileId ? DEFAULT_PROFILE_ID : activeProfileId,
          remainingProfiles: get().profiles.length
        });
      },

      // Rename profile
      renameProfile: (profileId, newName) => {
        set(state => ({
          profiles: state.profiles.map(profile => 
            profile.id === profileId
              ? { ...profile, name: newName, updatedAt: Date.now() }
              : profile
          )
        }));
      },

      // Set active profile
      setActiveProfile: (profileId) => {
        const { profiles } = get();
        const profileExists = profiles.some(p => p.id === profileId);

        console.log('[ProfileStore] setActiveProfile called:', {
          profileId,
          profileExists,
          availableProfiles: profiles.map(p => ({ id: p.id, name: p.name }))
        });

        if (profileExists) {
          set({ activeProfileId: profileId });
          console.log('[ProfileStore] Active profile set to:', profileId);
        } else {
          console.warn(`[ProfileStore] Profile ${profileId} not found`);
        }
      },

      // Get active profile
      getActiveProfile: () => {
        const { profiles, activeProfileId } = get();
        const activeProfile = profiles.find(p => p.id === activeProfileId);

        console.log('[ProfileStore] getActiveProfile:', {
          activeProfileId,
          found: !!activeProfile,
          profileName: activeProfile?.name,
          hasGridState: !!activeProfile?.gridState,
          columnDefsCount: activeProfile?.gridState?.columnDefs?.length
        });

        return activeProfile;
      },

      // Duplicate profile
      duplicateProfile: (profileId, newName) => {
        const { profiles } = get();
        const sourceProfile = profiles.find(p => p.id === profileId);

        if (!sourceProfile) {
          throw new Error(`Profile ${profileId} not found`);
        }

        const duplicatedProfile: GridProfile = {
          ...JSON.parse(JSON.stringify(sourceProfile)), // Deep clone
          id: `profile-${Date.now()}`,
          name: newName,
          createdAt: Date.now(),
          updatedAt: Date.now(),
          isDefault: false,
        };

        set(state => ({
          profiles: [...state.profiles, duplicatedProfile]
        }));

        return duplicatedProfile;
      },

      // Import profile
      importProfile: (profileData) => {
        const newProfile: GridProfile = {
          ...profileData,
          id: `profile-${Date.now()}`, // Generate new ID to avoid conflicts
          createdAt: Date.now(),
          updatedAt: Date.now(),
          isDefault: false,
        };

        set(state => ({
          profiles: [...state.profiles, newProfile]
        }));
      },

      // Export profile
      exportProfile: (profileId) => {
        const { profiles } = get();
        return profiles.find(p => p.id === profileId);
      },

      // Save current state to active profile
      saveCurrentState: (gridState) => {
        const { activeProfileId, profiles } = get();
        const activeProfile = profiles.find(p => p.id === activeProfileId);

        console.log('[ProfileStore] saveCurrentState called:', {
          activeProfileId,
          hasActiveProfile: !!activeProfile,
          gridStateKeys: Object.keys(gridState),
          columnDefsCount: gridState.columnDefs?.length,
          columnStateCount: gridState.columnState?.length
        });

        if (!activeProfile) {
          console.warn('[ProfileStore] No active profile found for saving');
          return;
        }

        // If columnDefs are provided, convert to lightweight format
        if (gridState.columnDefs) {
          const baseColumns = activeProfile.gridState.baseColumnDefs || [];
          const customizations = serializeColumnCustomizations(gridState.columnDefs, baseColumns);

          console.log('[ProfileStore] Converting columnDefs to lightweight format:', {
            columnDefsCount: gridState.columnDefs.length,
            customizationsCount: Object.keys(customizations).length,
            sizeReduction: `${Math.round((1 - JSON.stringify(customizations).length / JSON.stringify(gridState.columnDefs).length) * 100)}%`
          });

          get().updateProfile(activeProfileId, {
            gridState: {
              ...activeProfile.gridState,
              ...gridState,
              columnCustomizations: customizations,
              // Keep base columns for reference (only update if not set)
              baseColumnDefs: activeProfile.gridState.baseColumnDefs || gridState.columnDefs,
              // Remove full columnDefs to save space
              columnDefs: undefined
            }
          });
        } else {
          get().updateProfile(activeProfileId, {
            gridState: {
              ...activeProfile.gridState,
              ...gridState
            }
          });
        }
      },

      // Save column customizations in lightweight format
      saveColumnCustomizations: (columnDefs, baseColumnDefs) => {
        const { activeProfileId } = get();
        const activeProfile = get().getActiveProfile();

        if (!activeProfile) {
          console.warn('[ProfileStore] No active profile found for saving column customizations');
          return;
        }

        // Ensure we have base columns - use provided ones or fall back to current columns
        const actualBaseColumns = baseColumnDefs || activeProfile.gridState.baseColumnDefs || columnDefs;

        const customizations = serializeColumnCustomizations(
          columnDefs, 
          actualBaseColumns
        );

        console.log('[ProfileStore] Saving column customizations:', {
          profileId: activeProfileId,
          customizationsCount: Object.keys(customizations).length,
          baseColumnsCount: actualBaseColumns.length,
          size: `${(JSON.stringify(customizations).length / 1024).toFixed(2)}KB`
        });

        get().updateProfile(activeProfileId, {
          gridState: {
            ...activeProfile.gridState,
            columnCustomizations: customizations,
            baseColumnDefs: actualBaseColumns,
            // Remove legacy full columnDefs
            columnDefs: undefined
          }
        });
      },

      // Get column definitions (reconstruct from lightweight format if needed)
      getColumnDefs: (profileId) => {
        const { profiles, activeProfileId } = get();
        const id = profileId || activeProfileId;
        const profile = profiles.find(p => p.id === id);

        console.log('[ProfileStore] getColumnDefs called:', {
          profileId,
          activeProfileId,
          found: !!profile,
          profileName: profile?.name,
          hasCustomizations: !!(profile?.gridState?.columnCustomizations),
          hasBaseColumns: !!(profile?.gridState?.baseColumnDefs)
        });

        if (!profile) return undefined;

        // If we have the new lightweight format with valid base columns
        if (profile.gridState.columnCustomizations && profile.gridState.baseColumnDefs && profile.gridState.baseColumnDefs.length > 0) {
          console.log('[ProfileStore] Reconstructing columnDefs from lightweight format', {
            customizationsCount: Object.keys(profile.gridState.columnCustomizations).length,
            baseColumnsCount: profile.gridState.baseColumnDefs.length
          });
          const reconstructed = deserializeColumnCustomizations(
            profile.gridState.columnCustomizations,
            profile.gridState.baseColumnDefs
          );
          console.log('[ProfileStore] Column definitions reconstructed:', {
            totalColumns: reconstructed.length,
            columnsWithFormatters: reconstructed.filter(col => col.valueFormatter).length
          });
          return reconstructed;
        }

        // Fall back to legacy format if available
        if (profile.gridState.columnDefs) {
          console.log('[ProfileStore] Using legacy columnDefs format');
          return profile.gridState.columnDefs;
        }

        // Return empty array if no column definitions available
        return [];
      },

      // Auto-save settings
      setAutoSave: (enabled) => {
        set({ autoSave: enabled });
      },

      // Perform deferred migration
      performDeferredMigration: async () => {
        const state = get();
        if (state.migrationInProgress) return;

        set({ migrationInProgress: true });

        try {
          // Get the current persisted state
          const storageKey = 'grid-profile-storage';
          const stored = localStorage.getItem(storageKey);

          if (stored) {
            const parsedData = JSON.parse(stored);
            const currentVersion = parsedData.version || 1;

            if (currentVersion < 4) {
              console.log('[ProfileStore] Performing deferred migration from version', currentVersion);

              // Perform migration
              const migratedState = await performMigration(parsedData.state);

              // Update the store
              set({
                profiles: migratedState.profiles || [createDefaultProfile()],
                activeProfileId: migratedState.activeProfileId || DEFAULT_PROFILE_ID,
                autoSave: migratedState.autoSave !== undefined ? migratedState.autoSave : true,
                migrationPending: false,
                migrationInProgress: false
              });

              // Update localStorage with new version
              const newData = {
                ...parsedData,
                version: 4,
                state: migratedState
              };
              localStorage.setItem(storageKey, JSON.stringify(newData));

              console.log('[ProfileStore] Migration completed successfully');
            }
          }
        } catch (error) {
          console.error('[ProfileStore] Migration failed:', error);
        } finally {
          set({ migrationInProgress: false, migrationPending: false });
        }
      }
    }),
    {
      name: 'grid-profile-storage',
      version: 4,
      // Skip migration during initialization - will be done on demand
      migrate: (persistedState: unknown, version: number) => {
        const typedState = persistedState as PersistedState;
        if (version < 4) {
          // Mark that migration is needed but don't do it now
          console.log('[ProfileStore] Migration needed, will defer until after initialization');
          return {
            ...typedState,
            migrationPending: true
          };
        }
        return typedState;
      },
      // Only persist essential data
      partialize: (state) => ({
        profiles: state.profiles,
        activeProfileId: state.activeProfileId,
        autoSave: state.autoSave
      })
    }
  )
);

// Convenience hooks
export const useProfiles = () => useProfileStore(state => state.profiles);
export const useActiveProfile = () => {
  // perfMonitor.mark('profile-load-start');
  const profile = useProfileStore(state => 
    state.profiles.find(p => p.id === state.activeProfileId)
  );
  // perfMonitor.mark('profile-load-end');
