  return profile;
};

// Perform migration on idle
if (typeof window !== 'undefined') {
  const store = useProfileStore.getState();

  if ('requestIdleCallback' in window) {
    requestIdleCallback(() => {
      if (store.migrationPending) {
        store.performDeferredMigration();
      }
    }, { timeout: 5000 });
  } else {
    setTimeout(() => {
      if (store.migrationPending) {
        store.performDeferredMigration();
      }
    }, 2000);
  }
}

import { GridProfile } from './profile.store';
import { calculateCustomizationSize, getCustomizationSummary } from './columnSerializer';

export interface StorageAnalysis {
  totalProfiles: number;
  totalSizeKB: number;
  profileAnalysis: {
    profileId: string;
    profileName: string;
    legacySizeKB: number;
    lightweightSizeKB: number;
    savingsKB: number;
    savingsPercent: number;
    customizationSummary?: ReturnType<typeof getCustomizationSummary>;
  }[];
  overallSavings: {
    totalLegacySizeKB: number;
    totalLightweightSizeKB: number;
    totalSavingsKB: number;
    totalSavingsPercent: number;
  };
}

export function analyzeProfileStorage(): StorageAnalysis {
  const storageKey = 'grid-profile-storage';
  const stored = localStorage.getItem(storageKey);

  if (!stored) {
    return {
      totalProfiles: 0,
      totalSizeKB: 0,
      profileAnalysis: [],
      overallSavings: {
        totalLegacySizeKB: 0,
        totalLightweightSizeKB: 0,
        totalSavingsKB: 0,
        totalSavingsPercent: 0
      }
    };
  }

  const parsedData = JSON.parse(stored);
  const profiles: GridProfile[] = parsedData.state?.profiles || [];

  const profileAnalysis = profiles.map(profile => {
    // Calculate legacy size (full columnDefs)
    const legacySize = profile.gridState.columnDefs 
      ? JSON.stringify(profile.gridState.columnDefs).length / 1024
      : 0;

    // Calculate lightweight size
    const lightweightSize = profile.gridState.columnCustomizations
      ? calculateCustomizationSize(profile.gridState.columnCustomizations) / 1024
      : 0;

    // Get customization summary if using lightweight format
    const customizationSummary = profile.gridState.columnCustomizations
      ? getCustomizationSummary(profile.gridState.columnCustomizations)
      : undefined;

    const potentialSavings = legacySize > 0 ? legacySize - lightweightSize : 0;
    const savingsPercent = legacySize > 0 ? (potentialSavings / legacySize) * 100 : 0;

    return {
      profileId: profile.id,
      profileName: profile.name,
      legacySizeKB: legacySize,
      lightweightSizeKB: lightweightSize,
      savingsKB: potentialSavings,
      savingsPercent,
      customizationSummary
    };
  });

  const totalLegacySizeKB = profileAnalysis.reduce((sum, p) => sum + p.legacySizeKB, 0);
  const totalLightweightSizeKB = profileAnalysis.reduce((sum, p) => sum + p.lightweightSizeKB, 0);
  const totalSavingsKB = totalLegacySizeKB - totalLightweightSizeKB;
  const totalSavingsPercent = totalLegacySizeKB > 0 ? (totalSavingsKB / totalLegacySizeKB) * 100 : 0;

  return {
    totalProfiles: profiles.length,
    totalSizeKB: stored.length / 1024,
    profileAnalysis,
    overallSavings: {
      totalLegacySizeKB,
      totalLightweightSizeKB,
      totalSavingsKB,
      totalSavingsPercent
    }
  };
}

export function logStorageAnalysis() {
  const analysis = analyzeProfileStorage();

  console.group(' Profile Storage Analysis');
  console.log(`Total profiles: ${analysis.totalProfiles}`);
  console.log(`Total storage size: ${analysis.totalSizeKB.toFixed(2)} KB`);

  console.group(' Profile Breakdown');
  analysis.profileAnalysis.forEach(profile => {
    console.group(` ${profile.profileName} (${profile.profileId})`);
    console.log(`Legacy size: ${profile.legacySizeKB.toFixed(2)} KB`);
    console.log(`Lightweight size: ${profile.lightweightSizeKB.toFixed(2)} KB`);
    console.log(`Savings: ${profile.savingsKB.toFixed(2)} KB (${profile.savingsPercent.toFixed(1)}%)`);

    if (profile.customizationSummary) {
      console.group(' Customizations');
      console.log(`Customized columns: ${profile.customizationSummary.customizedColumns}`);
      console.log('Property usage:', profile.customizationSummary.properties);
      console.groupEnd();
    }

    console.groupEnd();
  });
  console.groupEnd();

  console.group(' Overall Savings');
  console.log(`Total legacy size: ${analysis.overallSavings.totalLegacySizeKB.toFixed(2)} KB`);
  console.log(`Total lightweight size: ${analysis.overallSavings.totalLightweightSizeKB.toFixed(2)} KB`);
  console.log(`Total savings: ${analysis.overallSavings.totalSavingsKB.toFixed(2)} KB`);
  console.log(`Savings percentage: ${analysis.overallSavings.totalSavingsPercent.toFixed(1)}%`);
  console.groupEnd();

  console.groupEnd();
}

// Export function to window for easy console access
if (typeof window !== 'undefined') {
  (window as any).analyzeProfileStorage = logStorageAnalysis;
}

// Utility to serialize and deserialize style functions for localStorage

import { ColDef } from 'ag-grid-community';

export interface SerializedStyle {
  type: 'static' | 'function';
  value?: React.CSSProperties;
  formatString?: string;
  baseStyle?: React.CSSProperties;
}

export interface SerializedColumnDef extends Omit<ColDef, 'cellStyle' | 'headerStyle'> {
  cellStyle?: SerializedStyle;
  headerStyle?: SerializedStyle;
}

// Serialize a column definition with style functions
export function serializeColumnDef(colDef: ColDef): SerializedColumnDef {
  const serialized = { ...colDef };

  // Handle cellStyle
  if (colDef.cellStyle) {
    if (typeof colDef.cellStyle === 'function') {
      // Try to extract format string from function (this is a workaround)
      // In production, you'd want to store the format configuration separately
      serialized.cellStyle = {
        type: 'function',
        // Store function as string for now - we'll need to improve this
        formatString: (colDef as any)._cellStyleFormat || '',
        baseStyle: (colDef as any)._cellStyleBase || {}
      };
    } else {
      serialized.cellStyle = {
        type: 'static',
        value: colDef.cellStyle as React.CSSProperties
      };
    }
  }

  // Handle headerStyle
  if (colDef.headerStyle) {
    if (typeof colDef.headerStyle === 'function') {
      serialized.headerStyle = {
        type: 'function',
        value: {} // Header style functions typically return empty for floating filters
      };
    } else {
      serialized.headerStyle = {
        type: 'static',
        value: colDef.headerStyle as React.CSSProperties
      };
    }
  }

  return serialized as SerializedColumnDef;
}

// Deserialize a column definition and recreate style functions
export function deserializeColumnDef(serialized: SerializedColumnDef): ColDef {
  const colDef = { ...serialized };

  // Handle cellStyle
  if (serialized.cellStyle && typeof serialized.cellStyle === 'object' && 'type' in serialized.cellStyle) {
    const styleConfig = serialized.cellStyle as SerializedStyle;

    if (styleConfig.type === 'static' && styleConfig.value) {
      colDef.cellStyle = styleConfig.value as any;
    } else if (styleConfig.type === 'function' && styleConfig.formatString) {
      // Recreate the cell style function
      // This is a simplified version - you'd need the actual createCellStyleFunction
      colDef.cellStyle = (() => {
        // For now, just return the base style
        // In production, you'd recreate the conditional logic
        return styleConfig.baseStyle || {};
      }) as any;
    }
  }

  // Handle headerStyle
  if (serialized.headerStyle && typeof serialized.headerStyle === 'object' && 'type' in serialized.headerStyle) {
    const styleConfig = serialized.headerStyle as SerializedStyle;

    if (styleConfig.type === 'static' && styleConfig.value) {
      colDef.headerStyle = styleConfig.value as any;
    } else if (styleConfig.type === 'function') {
      // Recreate header style function that avoids floating filter
      colDef.headerStyle = ((params: { floatingFilter?: boolean }) => {
        if (params?.floatingFilter) {
          return null;
        }
        return styleConfig.value || {};
      }) as any;
    }
  }

  return colDef as ColDef;
}

// Serialize all column definitions
export function serializeColumnDefs(columnDefs: ColDef[]): SerializedColumnDef[] {
  return columnDefs.map(serializeColumnDef);
}

// Deserialize all column definitions
export function deserializeColumnDefs(serialized: SerializedColumnDef[]): ColDef[] {
  return serialized.map(deserializeColumnDef);
}

import { 
  ValueFormatterParams, 
  ColDef as AgColDef,
  GridApi,
  GridReadyEvent,
  CellValueChangedEvent,
  SortChangedEvent,
  FilterChangedEvent,
  ColumnResizedEvent,
  ColumnMovedEvent,
  ColumnPinnedEvent,
  ColumnVisibleEvent
} from 'ag-grid-community';

// CellStyleParams type definition for AG-Grid v33+
export interface CellStyleParams {
  value: any;
  data: any;
  node: any;
  colDef: any;
  column: any;
  context: any;
  api: GridApi;
}

/**
 * Enhanced column definition with required fields
 */
export interface ColumnDef extends Omit<AgColDef, 'field' | 'headerName' | 'type'> {
  field: string;
  headerName: string;
  type?: string | string[]; // For legacy/custom use
  cellDataType?: 'text' | 'number' | 'date' | 'boolean' | string | boolean; // ag-Grid v33+ optimization
}

/**
 * Metadata attached to formatter functions for serialization
 */
export interface FormatterMetadata {
  __formatString: string;
  __formatterType: 'excel' | 'visual' | 'custom';
  __visualRules?: SerializedFormattingRule[];
  __visualDefaultFallback?: SerializedDefaultFallback;
}

/**
 * Metadata attached to cell style functions
 */
export interface CellStyleMetadata {
  __formatString?: string;
  __baseStyle?: React.CSSProperties;
  __cacheKey?: string;
}

/**
 * Enhanced formatter function type with metadata
 */
export type FormatterFunction = ((params: ValueFormatterParams) => string) & FormatterMetadata;

/**
 * Enhanced cell style function type with metadata
 */
export type CellStyleFunction = ((params: CellStyleParams) => React.CSSProperties | undefined) & CellStyleMetadata;

/**
 * Serialized formatting rule for visual formatter
 */
export interface SerializedFormattingRule {
  id: string;
  condition: {
    type: 'equals' | 'contains' | 'startsWith' | 'endsWith' | 'greaterThan' | 'lessThan' | 'greaterEqual' | 'lessEqual' | 'between' | 'isEmpty' | 'isNotEmpty';
    value: string;
    value2?: string;
  };
  display: {
    type: 'text' | 'original' | 'custom';
    text: string;
  };
  styling: {
    backgroundColor?: string;
    textColor?: string;
    fontSize?: number;
    fontWeight?: 'normal' | 'bold' | '100' | '200' | '300' | '400' | '500' | '600' | '700' | '800' | '900';
    fontStyle?: 'normal' | 'italic';
    textDecoration?: 'none' | 'underline' | 'line-through';
    textAlign?: 'left' | 'center' | 'right';
    border?: {
      width: number;
      style: 'solid' | 'dashed' | 'dotted' | 'double';
      color: string;
      sides: {
        top: boolean;
        right: boolean;
        bottom: boolean;
        left: boolean;
      };
    };
    padding?: {
      top: number;
      right: number;
      bottom: number;
      left: number;
    };
  };
  enabled: boolean;
}

/**
 * Serialized default fallback for visual formatter
 */
export interface SerializedDefaultFallback {
  display: {
    type: 'original' | 'text';
    text: string;
  };
  styling: SerializedFormattingRule['styling'];
}

/**
 * Props for DataTable component
 */
export interface DataTableProps {
  columnDefs: ColumnDef[];
  dataRow: Record<string, unknown>[];
}

/**
 * Context value for DataTable
 */
export interface DataTableContextValue {
  processedColumns: ColumnDef[];
  selectedFont: string;
  handleFontChange: (font: string) => void;
  showColumnDialog: boolean;
  setShowColumnDialog: (show: boolean) => void;
  gridApiRef: React.MutableRefObject<GridApi | null>;
  getColumnDefsWithStyles: () => ColumnDef[];
  setGridApi?: (api: GridApi | null) => void;
}

/**
 * Grid event types
 */
export type GridEvent = 
  | GridReadyEvent
  | CellValueChangedEvent
  | SortChangedEvent
  | FilterChangedEvent
  | ColumnResizedEvent
  | ColumnMovedEvent
  | ColumnPinnedEvent
  | ColumnVisibleEvent;

/**
 * Column property type for bulk updates
 */
export interface ColumnProperty {
  key: string;
  value: unknown;
  type?: 'style' | 'format' | 'general';
}

/**
 * Template properties for bulk actions
 */
export interface TemplateProperty {
  property: string;
  value: unknown;
  type?: string;
}

import { FormatterFunction, CellStyleFunction } from '../types';

/**
 * Type guard to check if a function is a formatter with metadata
 */
export function isFormatterFunction(fn: unknown): fn is FormatterFunction {
  return typeof fn === 'function' && '__formatString' in fn;
}

/**
 * Type guard to check if a function is a cell style function with metadata
 */
export function isCellStyleFunction(fn: unknown): fn is CellStyleFunction {
  return typeof fn === 'function' && ('__formatString' in fn || '__baseStyle' in fn);
}

/**
 * Safely extract formatter metadata from a function
 */
export function getFormatterMetadata(fn: unknown): {
  formatString?: string;
  formatterType?: 'excel' | 'visual' | 'custom';
  visualRules?: any[];
  visualDefaultFallback?: any;
} | null {
  if (!isFormatterFunction(fn)) {
    return null;
  }

  return {
    formatString: (fn as any).__formatString,
    formatterType: (fn as any).__formatterType,
    visualRules: (fn as any).__visualRules,
    visualDefaultFallback: (fn as any).__visualDefaultFallback,
  };
}

/**
 * Safely extract cell style metadata from a function
 */
export function getCellStyleMetadata(fn: unknown): {
  formatString?: string;
  baseStyle?: React.CSSProperties;
  cacheKey?: string;
} | null {
  if (!isCellStyleFunction(fn)) {
    return null;
  }

  return {
    formatString: (fn as any).__formatString,
    baseStyle: (fn as any).__baseStyle,
    cacheKey: (fn as any).__cacheKey,
  };
}

import { clsx, type ClassValue } from 'clsx';
import { twMerge } from 'tailwind-merge';

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

/**
 * Debounce function that delays invoking func until after wait milliseconds
 * have elapsed since the last time the debounced function was invoked.
 */
export function debounce<T extends (...args: any[]) => any>(
  func: T,
  wait: number
): (...args: Parameters<T>) => void {
  let timeoutId: NodeJS.Timeout | undefined;

  return function debounced(...args: Parameters<T>) {
    if (timeoutId) {
      clearTimeout(timeoutId);
    }

    timeoutId = setTimeout(() => {
      func(...args);
    }, wait);
  };
}
